  1→/**
  2→ * Token budget tracking per phase and per thesis.
  3→ *
  4→ * Limits (from PLAN.md):
  5→ * - Max 100K output tokens per phase
  6→ * - Max 1.2M total tokens per thesis
  7→ * - Hard-stop at budget (no partial generation)
  8→ *
  9→ * Uses Supabase `ai_conversations` table for persistent tracking.
 10→ * Falls back to in-memory cache if DB unavailable.
 11→ */
 12→
 13→import { createAdminSupabaseClient } from "@/lib/supabase/admin";
 14→
 15→const MAX_OUTPUT_TOKENS_PER_PHASE = 100_000;
 16→const MAX_TOTAL_TOKENS_PER_THESIS = 1_200_000;
 17→
 18→export interface TokenBudgetResult {
 19→  allowed: boolean;
 20→  phaseTokensUsed: number;
 21→  totalTokensUsed: number;
 22→  phaseRemaining: number;
 23→  totalRemaining: number;
 24→  reason?: string;
 25→}
 26→
 27→/**
 28→ * Check if a project has budget remaining for the given phase.
 29→ */
 30→export async function checkTokenBudget(
 31→  projectId: string,
 32→  phaseNumber: number
 33→): Promise<TokenBudgetResult> {
 34→  const supabase = createAdminSupabaseClient();
 35→
 36→  // Fetch all AI conversations for this project
 37→  const { data: conversations } = await supabase
 38→    .from("ai_conversations")
 39→    .select("phase_number, total_tokens")
 40→    .eq("project_id", projectId);
 41→
 42→  const records = conversations ?? [];
 43→
 44→  // Calculate totals
 45→  let totalTokensUsed = 0;
 46→  let phaseTokensUsed = 0;
 47→
 48→  for (const conv of records) {
 49→    const tokens = (conv.total_tokens as number) ?? 0;
 50→    totalTokensUsed += tokens;
 51→    if ((conv.phase_number as number) === phaseNumber) {
 52→      phaseTokensUsed += tokens;
 53→    }
 54→  }
 55→
 56→  const phaseRemaining = MAX_OUTPUT_TOKENS_PER_PHASE - phaseTokensUsed;
 57→  const totalRemaining = MAX_TOTAL_TOKENS_PER_THESIS - totalTokensUsed;
 58→
 59→  if (phaseTokensUsed >= MAX_OUTPUT_TOKENS_PER_PHASE) {
 60→    return {
 61→      allowed: false,
 62→      phaseTokensUsed,
 63→      totalTokensUsed,
 64→      phaseRemaining: 0,
 65→      totalRemaining: Math.max(0, totalRemaining),
 66→      reason: `Phase ${phaseNumber} token budget exhausted (${phaseTokensUsed.toLocaleString()} / ${MAX_OUTPUT_TOKENS_PER_PHASE.toLocaleString()})`,
 67→    };
 68→  }
 69→
 70→  if (totalTokensUsed >= MAX_TOTAL_TOKENS_PER_THESIS) {
 71→    return {
 72→      allowed: false,
 73→      phaseTokensUsed,
 74→      totalTokensUsed,
 75→      phaseRemaining: Math.max(0, phaseRemaining),
 76→      totalRemaining: 0,
 77→      reason: `Thesis token budget exhausted (${totalTokensUsed.toLocaleString()} / ${MAX_TOTAL_TOKENS_PER_THESIS.toLocaleString()})`,
 78→    };
 79→  }
 80→
 81→  return {
 82→    allowed: true,
 83→    phaseTokensUsed,
 84→    totalTokensUsed,
 85→    phaseRemaining: Math.max(0, phaseRemaining),
 86→    totalRemaining: Math.max(0, totalRemaining),
 87→  };
 88→}
