 12→import { REFINE_SYSTEM_PROMPT } from "@/lib/ai/prompts";
 13→import { latexToTiptap } from "@/lib/latex/latex-to-tiptap";
 14→import { resolveSectionCitations } from "@/lib/citations/auto-resolve";
 15→import type { Section } from "@/lib/types/database";
 16→
 17→export async function POST(
 18→  request: NextRequest,
 19→  { params }: { params: Promise<{ id: string; phase: string }> }
 20→) {
 21→  try {
 22→    const authResult = await getAuthenticatedUser();
 23→    if (!authResult) return unauthorised();
 24→
 25→    const { id, phase } = await params;
 26→    const phaseNumber = parseInt(phase, 10);
 27→
 28→    if (!isValidPhase(phaseNumber)) {
 29→      return badRequest("Invalid phase number");
 30→    }
 31→
 32→    const body = await request.json();
 33→    const instructions = (body as { instructions?: string }).instructions;
 34→    if (!instructions || typeof instructions !== "string" || instructions.trim().length === 0) {
 35→      return badRequest("Instructions are required");
 36→    }
 37→
 38→    const supabase = createAdminSupabaseClient();
 39→
 40→    // Verify project ownership
 41→    const { data: project, error: projectError } = await supabase
 42→      .from("projects")
 43→      .select("id")
 44→      .eq("id", id)
 45→      .eq("user_id", authResult.user.id)
 46→      .single();
 47→
 48→    if (projectError || !project) {
 49→      return notFound("Project not found");
 50→    }
 51→
 52→    // Fetch current section content
 53→    const { data: section, error: sectionError } = await supabase
 54→      .from("sections")
 55→      .select("*")
 56→      .eq("project_id", id)
 57→      .eq("phase_number", phaseNumber)
 58→      .single();
 59→
 60→    if (sectionError || !section) {
 61→      return notFound("Section not found — generate content first");
 62→    }
 63→
 64→    const typedSection = section as Section;
 65→    const currentLatex =
 66→      typedSection.ai_generated_latex || typedSection.latex_content;
 67→
 68→    if (!currentLatex) {
 69→      return badRequest("Section has no content to refine — generate first");
 70→    }
 71→
 72→    // Don't change status to "generating" — the client consumes the stream
 73→    // and refreshes when done. Keeping current status avoids a broken UI state
 74→    // if the page is refreshed mid-stream.
 75→    let streamCompleted = false;
 76→
 77→    const encoder = new TextEncoder();
 78→    const stream = new ReadableStream({
 79→      async start(controller) {
 80→        try {
 81→          const client = getAnthropicClient();
 82→          let fullResponse = "";
 83→
 84→          const userMessage = `Here is the current chapter content:\n\n${currentLatex}\n\n---STUDENT INSTRUCTIONS---\n${instructions.trim()}`;
 85→
 86→          const messageStream = client.messages.stream({
 87→            model: "claude-sonnet-4-5-20250929",
 88→            max_tokens: 10000,
 89→            system: [
 90→              {
 91→                type: "text" as const,
 92→                text: REFINE_SYSTEM_PROMPT,
 93→                cache_control: { type: "ephemeral" as const },
 94→              },
 95→            ],
 96→            messages: [{ role: "user", content: userMessage }],
 97→          });
 98→
 99→          messageStream.on("text", (text) => {
100→            fullResponse += text;
101→            controller.enqueue(
102→              encoder.encode(
103→                `data: ${JSON.stringify({ type: "delta", text })}\n\n`
104→              )
105→            );
106→          });
107→
108→          await messageStream.finalMessage();
109→
110→          // Convert LaTeX → Tiptap JSON
111→          const tiptapResult = latexToTiptap(fullResponse);
112→          const citationKeys = tiptapResult.citationKeys;
113→
114→          // Word count
115→          const plainText = fullResponse
116→            .replace(/\\[a-zA-Z]+\{[^}]*\}/g, " ")
117→            .replace(/\\[a-zA-Z]+/g, " ")
118→            .replace(/[{}\\]/g, " ");
119→          const wordCount = plainText.split(/\s+/).filter(Boolean).length;
120→
121→          // Update section (3-tier fallback like generate)
122→          const { error: updateError } = await supabase
123→            .from("sections")
124→            .update({
125→              latex_content: fullResponse,
126→              rich_content_json: tiptapResult.json,
127→              ai_generated_latex: fullResponse,
128→              word_count: wordCount,
129→              citation_keys: citationKeys,
130→              status: "review",
131→              updated_at: new Date().toISOString(),
132→            })
133→            .eq("project_id", id)
134→            .eq("phase_number", phaseNumber);
135→
136→          if (updateError) {
137→            await supabase
138→              .from("sections")
139→              .update({
140→                latex_content: fullResponse,
141→                word_count: wordCount,
142→                citation_keys: citationKeys,
143→                status: "review",
144→                updated_at: new Date().toISOString(),
145→              })
146→              .eq("project_id", id)
147→              .eq("phase_number", phaseNumber);
148→          }
149→
150→          streamCompleted = true;
151→
152→          // Resolve new citations (non-blocking with 10s timeout)
153→          let citationSummary = null;
154→          try {
155→            citationSummary = await Promise.race([
156→              resolveSectionCitations(id, fullResponse),
157→              new Promise<null>((resolve) =>
158→                setTimeout(() => resolve(null), 10_000)
159→              ),
160→            ]);
161→          } catch {
162→            // Non-critical
163→          }
164→
165→          controller.enqueue(
166→            encoder.encode(
167→              `data: ${JSON.stringify({
168→                type: "complete",
169→                wordCount,
170→                citationKeys,
171→                citationSummary,
172→              })}\n\n`
173→            )
174→          );
175→          controller.enqueue(encoder.encode("data: [DONE]\n\n"));
176→          controller.close();
177→        } catch (err) {
178→          streamCompleted = true;
179→
180→          const errorMessage =
181→            err instanceof Error ? err.message : "Refinement failed";
182→          controller.enqueue(
183→            encoder.encode(
184→              `data: ${JSON.stringify({ type: "error", message: errorMessage })}\n\n`
185→            )
186→          );
187→          controller.close();
188→        }
189→      },
190→      cancel: async () => {
191→        // No status reset needed — we never set "generating"
192→      },
193→    });
194→
195→    return new Response(stream, {
196→      headers: {
197→        "Content-Type": "text/event-stream",
198→        "Cache-Control": "no-cache",
199→        Connection: "keep-alive",
200→      },
201→    });
202→  } catch (err) {
203→    console.error(
204→      "Unexpected error in POST /api/projects/[id]/sections/[phase]/refine:",
205→      err
206→    );
207→    return internalError();
208→  }
209→}
