 1→import { escapeLatex } from "./escape";
 2→import type { Abbreviation } from "@/lib/types/database";
 3→
 4→/**
 5→ * Generate LaTeX for the abbreviations list.
 6→ * Uses the \begin{abbreviations} environment from the thesis CLS files.
 7→ * Abbreviations are sorted alphabetically by short_form.
 8→ */
 9→export function generateAbbreviationsLatex(abbreviations: Abbreviation[]): string {
10→  if (abbreviations.length === 0) return "";
11→
12→  const sorted = [...abbreviations].sort((a, b) =>
13→    a.short_form.localeCompare(b.short_form)
14→  );
15→
16→  const rows = sorted
17→    .map((abbr) => `  ${escapeLatex(abbr.short_form)} & ${escapeLatex(abbr.long_form)} \\\\`)
18→    .join("\n");
19→
20→  return `\\begin{abbreviations}\n${rows}\n\\end{abbreviations}\n`;
21→}
