  1→// ── Bidirectional citation integrity audit ──────────────────────────────────
  2→//
  3→// Pure function — no I/O, trivially testable.
  4→
  5→import type { Section, Citation } from "@/lib/types/database";
  6→
  7→export interface AuditResult {
  8→  /** Cite keys used in sections but not in the citations DB */
  9→  missingCitations: { citeKey: string; usedInPhases: number[] }[];
 10→  /** Citations in DB not referenced by any section */
 11→  orphanedCitations: { citationId: string; citeKey: string; tier: string }[];
 12→  /** Tier D citations that are actively used in sections (blocks Final QC) */
 13→  tierDBlocking: { citationId: string; citeKey: string; usedInPhases: number[] }[];
 14→  totalCitations: number;
 15→  totalCiteCommands: number;
 16→  /** 0–100 score: (commands with Tier A/B/C match) / total commands × 100 */
 17→  integrityScore: number;
 18→}
 19→
 20→/**
 21→ * Run a bidirectional integrity audit on citations.
 22→ *
 23→ * Forward: cite keys in section.citation_keys[] not in citation DB → missingCitations
 24→ * Reverse: citations in DB not in any section → orphanedCitations
 25→ * Tier D used in sections → tierDBlocking
 26→ * Score: (commands with Tier A/B/C match) / total commands × 100
 27→ */
 28→export function auditCitations(
 29→  sections: Section[],
 30→  citations: Citation[]
 31→): AuditResult {
 32→  // 1. Collect all cite keys from sections → map citeKey → Set<phaseNumber>
 33→  const usedKeys = new Map<string, Set<number>>();
 34→  let totalCiteCommands = 0;
 35→
 36→  for (const section of sections) {
 37→    if (!section.citation_keys || section.citation_keys.length === 0) continue;
 38→    for (const key of section.citation_keys) {
 39→      if (!usedKeys.has(key)) {
 40→        usedKeys.set(key, new Set());
 41→      }
 42→      usedKeys.get(key)!.add(section.phase_number);
 43→      totalCiteCommands++;
 44→    }
 45→  }
 46→
 47→  // 2. Build citation lookup: citeKey → Citation
 48→  const citationMap = new Map<string, Citation>();
 49→  for (const citation of citations) {
 50→    citationMap.set(citation.cite_key, citation);
 51→  }
 52→
 53→  // 3. Forward: cite keys in sections not in citation DB
 54→  const missingCitations: AuditResult["missingCitations"] = [];
 55→  for (const [key, phases] of usedKeys) {
 56→    if (!citationMap.has(key)) {
 57→      missingCitations.push({
 58→        citeKey: key,
 59→        usedInPhases: Array.from(phases).sort((a, b) => a - b),
 60→      });
 61→    }
 62→  }
 63→
 64→  // 4. Reverse: citations in DB not in any section
 65→  const orphanedCitations: AuditResult["orphanedCitations"] = [];
 66→  for (const citation of citations) {
 67→    if (!usedKeys.has(citation.cite_key)) {
 68→      orphanedCitations.push({
 69→        citationId: citation.id,
 70→        citeKey: citation.cite_key,
 71→        tier: citation.provenance_tier,
 72→      });
 73→    }
 74→  }
 75→
 76→  // 5. Tier D used in sections → blocking
 77→  const tierDBlocking: AuditResult["tierDBlocking"] = [];
 78→  for (const [key, phases] of usedKeys) {
 79→    const citation = citationMap.get(key);
 80→    if (citation && citation.provenance_tier === "D") {
 81→      tierDBlocking.push({
 82→        citationId: citation.id,
 83→        citeKey: key,
 84→        usedInPhases: Array.from(phases).sort((a, b) => a - b),
 85→      });
 86→    }
 87→  }
 88→
 89→  // 6. Integrity score
 90→  let matchedCommands = 0;
 91→  for (const [key] of usedKeys) {
 92→    const citation = citationMap.get(key);
 93→    if (citation && citation.provenance_tier !== "D") {
 94→      // Count each usage of this key
 95→      matchedCommands += usedKeys.get(key)!.size;
 96→    }
 97→  }
 98→  // Recalculate totalCiteCommands as sum of unique key-phase pairs
 99→  let totalUniqueUsages = 0;
100→  for (const [, phases] of usedKeys) {
101→    totalUniqueUsages += phases.size;
102→  }
103→
104→  const integrityScore =
105→    totalUniqueUsages > 0
106→      ? Math.round((matchedCommands / totalUniqueUsages) * 100)
107→      : 100; // No citations = perfect score
108→
109→  return {
110→    missingCitations,
111→    orphanedCitations,
112→    tierDBlocking,
113→    totalCitations: citations.length,
114→    totalCiteCommands,
115→    integrityScore,
116→  };
117→}
