23→try {
24→  if (process.env.NEXT_PUBLIC_SENTRY_DSN) {
25→    const { withSentryConfig } = require("@sentry/nextjs");
26→    exportedConfig = withSentryConfig(nextConfig, {
27→      org: process.env.SENTRY_ORG,
28→      project: process.env.SENTRY_PROJECT,
29→      authToken: process.env.SENTRY_AUTH_TOKEN,
30→      silent: !process.env.CI,
31→      disableLogger: true,
32→      widenClientFileUpload: true,
33→      sourcemaps: { deleteSourcemapsAfterUpload: true },
34→      tunnelRoute: "/monitoring",
35→    });
36→  }
37→} catch {
38→  // @sentry/nextjs not available — skip Sentry integration
39→}
40→
41→export default exportedConfig;
42→

 1→/**
 2→ * In-memory rate limiter for AI generation requests.
 3→ * Limits: 10 requests per hour per user.
 4→ */
 5→
 6→interface RateLimitEntry {
 7→  timestamps: number[];
 8→}
 9→
10→const WINDOW_MS = 60 * 60 * 1000; // 1 hour
11→const MAX_REQUESTS = 10;
12→
13→const store = new Map<string, RateLimitEntry>();
14→
15→function cleanExpired(entry: RateLimitEntry, now: number): number[] {
16→  return entry.timestamps.filter((ts) => now - ts < WINDOW_MS);
17→}
18→
19→export interface RateLimitResult {
20→  allowed: boolean;
21→  remaining: number;
22→  retryAfterSeconds: number;
23→}
24→
25→export function checkRateLimit(userId: string): RateLimitResult {
26→  const now = Date.now();
27→  const entry = store.get(userId) ?? { timestamps: [] };
28→
29→  entry.timestamps = cleanExpired(entry, now);
30→
31→  if (entry.timestamps.length >= MAX_REQUESTS) {
32→    const oldestInWindow = entry.timestamps[0];
33→    const retryAfterMs = WINDOW_MS - (now - oldestInWindow);
34→    return {
35→      allowed: false,
36→      remaining: 0,
37→      retryAfterSeconds: Math.ceil(retryAfterMs / 1000),
38→    };
39→  }
40→
41→  entry.timestamps.push(now);
42→  store.set(userId, entry);
43→
44→  return {
45→    allowed: true,
46→    remaining: MAX_REQUESTS - entry.timestamps.length,
47→    retryAfterSeconds: 0,
48→  };
49→}
50→
51→/** Reset rate limit for a user (useful for testing). */
52→export function resetRateLimit(userId: string): void {
53→  store.delete(userId);
54→}
55→
56→/** Clear all rate limit entries (useful for testing). */
57→export function clearAllRateLimits(): void {
58→  store.clear();
59→}
