 1→import { inngest } from "../client";
 2→import { createAdminSupabaseClient } from "@/lib/supabase/admin";
 3→import { getPhase } from "@/lib/phases/constants";
 4→
 5→/**
 6→ * Thesis phase workflow: when a phase is approved, auto-create the next section
 7→ * and wait for the next approval event.
 8→ */
 9→export const thesisPhaseWorkflow = inngest.createFunction(
10→  { id: "thesis-phase-workflow", name: "Thesis Phase Workflow" },
11→  { event: "thesis/phase.approved" },
12→  async ({ event, step }) => {
13→    const { projectId, phaseNumber } = event.data;
14→    const nextPhase = phaseNumber + 1;
15→    const nextPhaseDef = getPhase(nextPhase);
16→
17→    if (!nextPhaseDef) {
18→      return { status: "completed", message: "All phases done" };
19→    }
20→
21→    // Create the next section as draft (only if it doesn't already exist)
22→    await step.run("create-next-section", async () => {
23→      const supabase = createAdminSupabaseClient();
24→      const { data: existing } = await supabase
25→        .from("sections")
26→        .select("id")
27→        .eq("project_id", projectId)
28→        .eq("phase_number", nextPhase)
29→        .maybeSingle();
30→
31→      if (!existing) {
32→        await supabase.from("sections").insert({
33→          project_id: projectId,
34→          phase_number: nextPhase,
35→          phase_name: nextPhaseDef.name,
36→          latex_content: "",
37→          word_count: 0,
38→          citation_keys: [],
39→          status: "draft",
40→        });
41→      }
42→    });
43→
44→    // Wait for the next phase approval (timeout: 90 days)
45→    const nextApproval = await step.waitForEvent("wait-for-next-approval", {
46→      event: "thesis/phase.approved",
47→      match: "data.projectId",
48→      timeout: "90d",
49→    });
50→
51→    if (!nextApproval) {
52→      return { status: "timeout", phase: nextPhase };
53→    }
54→
55→    return { status: "advanced", fromPhase: phaseNumber, toPhase: nextPhase };
56→  }
57→);
