 1→import {
 2→  S3Client,
 3→  PutObjectCommand,
 4→  GetObjectCommand,
 5→} from "@aws-sdk/client-s3";
 6→import { getSignedUrl } from "@aws-sdk/s3-request-presigner";
 7→
 8→const ALLOWED_UPLOAD_TYPES = [
 9→  "application/pdf",
10→  "text/csv",
11→  "application/vnd.openxmlformats-officedocument.spreadsheetml.sheet",
12→  "image/png",
13→  "image/jpeg",
14→];
15→
16→const MAX_FILE_SIZE = 50 * 1024 * 1024; // 50MB
17→
18→const s3Client = new S3Client({
19→  region: "auto",
20→  endpoint: `https://${process.env.R2_ACCOUNT_ID}.r2.cloudflarestorage.com`,
21→  credentials: {
22→    accessKeyId: process.env.R2_ACCESS_KEY_ID!,
23→    secretAccessKey: process.env.R2_SECRET_ACCESS_KEY!,
24→  },
25→});
26→
27→export async function generateUploadUrl(
28→  key: string,
29→  contentType: string
30→): Promise<{ url: string; key: string }> {
31→  if (!ALLOWED_UPLOAD_TYPES.includes(contentType)) {
32→    throw new Error(`File type not allowed: ${contentType}`);
33→  }
34→
35→  // Reject path traversal
36→  if (key.includes("..") || key.includes("//")) {
37→    throw new Error("Invalid file key");
38→  }
39→
40→  const command = new PutObjectCommand({
41→    Bucket: process.env.R2_BUCKET_NAME!,
42→    Key: key,
43→    ContentType: contentType,
44→  });
45→
46→  const url = await getSignedUrl(s3Client, command, { expiresIn: 300 }); // 5 min
47→  return { url, key };
48→}
49→
50→export async function generateDownloadUrl(key: string): Promise<string> {
51→  const command = new GetObjectCommand({
52→    Bucket: process.env.R2_BUCKET_NAME!,
53→    Key: key,
54→  });
55→
56→  return getSignedUrl(s3Client, command, { expiresIn: 900 }); // 15 min
57→}
58→
59→export { ALLOWED_UPLOAD_TYPES, MAX_FILE_SIZE };
