 1→import { NextRequest, NextResponse } from "next/server";
 2→import { getAuthenticatedUser } from "@/lib/api/auth";
 3→import { unauthorised, validationError, notFound, internalError } from "@/lib/api/errors";
 4→import { checkoutSchema, PLAN_PRICES } from "@/lib/validation/payment-schemas";
 5→import { createRazorpayOrder, RAZORPAY_KEY_ID } from "@/lib/payments/razorpay";
 6→import { createStripeSession } from "@/lib/payments/stripe";
 7→import { createAdminSupabaseClient } from "@/lib/supabase/admin";
 8→
 9→export async function POST(request: NextRequest) {
10→  try {
11→    const authResult = await getAuthenticatedUser();
12→    if (!authResult) return unauthorised();
13→
14→    const body = await request.json();
15→    const parsed = checkoutSchema.safeParse(body);
16→    if (!parsed.success) {
17→      return validationError("Invalid checkout data", {
18→        issues: parsed.error.flatten().fieldErrors,
19→      });
20→    }
21→
22→    const { plan_type, currency, project_id } = parsed.data;
23→
24→    // Verify project ownership if project_id provided
25→    if (project_id) {
26→      const adminDb = createAdminSupabaseClient();
27→      const { data: project } = await adminDb
28→        .from("projects")
29→        .select("id")
30→        .eq("id", project_id)
31→        .eq("user_id", authResult.user.id)
32→        .single();
33→
34→      if (!project) return notFound("Project not found");
35→    }
36→
37→    const pricing = PLAN_PRICES[plan_type];
38→    if (!pricing) {
39→      return validationError("Invalid plan type");
40→    }
41→
42→    const amount = currency === "INR" ? pricing.INR : pricing.USD;
43→    const origin = request.headers.get("origin") ?? process.env.NEXT_PUBLIC_APP_URL ?? "http://localhost:3000";
44→
45→    const metadata = {
46→      user_id: authResult.user.id,
47→      plan_type,
48→      plan_label: pricing.label,
49→      ...(project_id ? { project_id } : {}),
50→    };
51→
52→    if (currency === "INR") {
53→      // Razorpay for INR payments
54→      const order = await createRazorpayOrder(amount, "INR", metadata);
55→
56→      return NextResponse.json({
57→        data: {
58→          provider: "razorpay",
59→          order_id: order.id,
60→          key_id: RAZORPAY_KEY_ID,
61→          amount: order.amount,
62→          currency: order.currency,
63→          notes: metadata,
64→        },
65→      });
66→    } else {
67→      // Stripe for USD payments
68→      const session = await createStripeSession(
69→        amount,
70→        "USD",
71→        metadata,
72→        `${origin}/dashboard?payment=success`,
73→        `${origin}/dashboard?payment=cancelled`
74→      );
75→
76→      return NextResponse.json({
77→        data: {
78→          provider: "stripe",
79→          redirect_url: session.url,
80→          session_id: session.sessionId,
81→        },
82→      });
83→    }
84→  } catch (err) {
85→    console.error("Checkout error:", err);
86→    return internalError();
87→  }
88→}
