 8→// ── GET /api/citations/search?q=...&source=...&limit=... ────────────────────
 9→
10→export async function GET(request: NextRequest) {
11→  try {
12→    const authResult = await getAuthenticatedUser();
13→    if (!authResult) return unauthorised();
14→
15→    const { searchParams } = new URL(request.url);
16→    const parsed = citationSearchSchema.safeParse({
17→      q: searchParams.get("q"),
18→      source: searchParams.get("source") ?? undefined,
19→      limit: searchParams.get("limit") ?? undefined,
20→    });
21→
22→    if (!parsed.success) {
23→      return validationError("Invalid search parameters", {
24→        issues: parsed.error.issues,
25→      });
26→    }
27→
28→    const { q, source, limit } = parsed.data;
29→
30→    if (source === "crossref") {
31→      const result = await searchCrossRef(q, limit);
32→      return NextResponse.json({
33→        data: {
34→          source: "crossref",
35→          items: result.items.map((item) => ({
36→            doi: item.doi,
37→            title: item.title,
38→            authors: item.authors,
39→            journal: item.journal,
40→            year: item.year,
41→            volume: item.volume,
42→            issue: item.issue,
43→            pages: item.pages,
44→          })),
45→          totalResults: result.totalResults,
46→        },
47→      });
48→    }
49→
50→    // Default: PubMed
51→    const result = await searchPubMed(q, limit);
52→    return NextResponse.json({
53→      data: {
54→        source: "pubmed",
55→        items: result.items.map((item) => ({
56→          pmid: item.pmid,
57→          doi: item.doi,
58→          title: item.title,
59→          authors: item.authors,
60→          journal: item.journal,
61→          year: item.year,
62→          volume: item.volume,
63→          issue: item.issue,
64→          pages: item.pages,
65→        })),
66→        totalResults: result.totalResults,
67→      },
68→    });
69→  } catch (err) {
70→    console.error("Unexpected error in GET citations/search:", err);
71→    return internalError();
72→  }
73→}
