 1→import { NextRequest, NextResponse } from "next/server";
 2→import { getAuthenticatedUser } from "@/lib/api/auth";
 3→import {
 4→  unauthorised,
 5→  validationError,
 6→  badRequest,
 7→  notFound,
 8→  internalError,
 9→} from "@/lib/api/errors";
10→import {
11→  generateUploadUrl,
12→  ALLOWED_UPLOAD_TYPES,
13→} from "@/lib/r2/client";
14→import { createAdminSupabaseClient } from "@/lib/supabase/admin";
15→
16→export async function POST(request: NextRequest) {
17→  try {
18→    const authResult = await getAuthenticatedUser();
19→    if (!authResult) return unauthorised();
20→
21→    const body = await request.json();
22→    const { fileName, contentType, projectId } = body as {
23→      fileName?: string;
24→      contentType?: string;
25→      projectId?: string;
26→    };
27→
28→    if (!fileName || !contentType || !projectId) {
29→      return validationError(
30→        "fileName, contentType, and projectId are required"
31→      );
32→    }
33→
34→    // Validate content type
35→    if (!ALLOWED_UPLOAD_TYPES.includes(contentType)) {
36→      return badRequest(
37→        `File type not allowed: ${contentType}. Allowed types: ${ALLOWED_UPLOAD_TYPES.join(", ")}`
38→      );
39→    }
40→
41→    // Validate file name - reject path traversal characters
42→    if (
43→      fileName.includes("..") ||
44→      fileName.includes("//") ||
45→      fileName.includes("\\")
46→    ) {
47→      return badRequest("Invalid file name: path traversal characters are not permitted");
48→    }
49→
50→    // Verify project ownership
51→    const adminDb = createAdminSupabaseClient();
52→    const { data: project } = await adminDb
53→      .from("projects")
54→      .select("id")
55→      .eq("id", projectId)
56→      .eq("user_id", authResult.user.id)
57→      .single();
58→
59→    if (!project) return notFound("Project not found");
60→
61→    // Sanitise the file name (remove any remaining unsafe characters)
62→    const sanitisedFileName = fileName
63→      .replace(/[^\w.\-]/g, "_")
64→      .replace(/_{2,}/g, "_");
65→
66→    const key = `projects/${projectId}/${crypto.randomUUID()}/${sanitisedFileName}`;
67→
68→    const result = await generateUploadUrl(key, contentType);
69→
70→    return NextResponse.json({ data: result });
71→  } catch (err) {
72→    console.error("Unexpected error in POST /api/upload/signed-url:", err);
73→    return internalError();
74→  }
75→}
