 1→import { NextResponse } from "next/server";
 2→import { ERROR_CODES, type ApiErrorResponse } from "@/lib/types/api";
 3→
 4→function errorResponse(
 5→  code: string,
 6→  message: string,
 7→  status: number,
 8→  details?: Record<string, unknown>
 9→): NextResponse<ApiErrorResponse> {
10→  return NextResponse.json({ error: { code, message, details } }, { status });
11→}
12→
13→export function unauthorised(message = "Authentication required") {
14→  return errorResponse(ERROR_CODES.UNAUTHORISED, message, 401);
15→}
