 5→export async function getAuthenticatedUser(): Promise<{
 6→  user: User;
 7→  clerkUserId: string;
 8→} | null> {
 9→  const { userId } = await auth();
10→  if (!userId) return null;
11→
12→  const supabase = createAdminSupabaseClient();
13→  const { data: user } = await supabase
14→    .from("users")
15→    .select("*")
16→    .eq("clerk_user_id", userId)
17→    .single();
18→
19→  if (user) return { user: user as User, clerkUserId: userId };
20→
21→  // JIT provisioning: create user in Supabase if they exist in Clerk
22→  // but not yet synced (e.g. webhook not configured in local dev)
23→  const clerkUser = await currentUser();
24→  if (!clerkUser) return null;
25→
26→  const email = clerkUser.emailAddresses?.[0]?.emailAddress;
27→  if (!email) return null;
28→
29→  const name =
30→    [clerkUser.firstName, clerkUser.lastName].filter(Boolean).join(" ") ||
31→    email;
32→
33→  const { data: newUser, error: insertError } = await supabase
34→    .from("users")
35→    .insert({
36→      clerk_user_id: userId,
37→      email,
38→      name,
39→      role: "student",
40→    })
41→    .select("*")
42→    .single();
43→
44→  if (insertError || !newUser) {
45→    console.error("JIT user provisioning failed:", insertError);
46→    return null;
47→  }
48→
49→  return { user: newUser as User, clerkUserId: userId };
