 4→
 5→/**
 6→ * Inngest function: run a statistical analysis via R Plumber.
 7→ *
 8→ * Triggered by "analysis/run.requested" event from the analysis API route.
 9→ * Handles execution, status updates, and semaphore release.
10→ */
11→export const analysisRunnerFn = inngest.createFunction(
12→  {
13→    id: "analysis-runner",
14→    retries: 1,
15→  },
16→  { event: "analysis/run.requested" },
17→  async ({ event, step }) => {
18→    const { analysisId, jobId } = event.data;
19→
20→    try {
21→      await step.run("run-analysis", async () => {
22→        await executeAnalysis(analysisId);
23→      });
24→
25→      return { status: "completed", analysisId };
26→    } catch (err) {
27→      console.error(`Analysis ${analysisId} failed:`, err);
28→      return {
29→        status: "failed",
30→        analysisId,
31→        error: err instanceof Error ? err.message : "Unknown error",
32→      };
33→    } finally {
34→      // Always release the semaphore
35→      release(jobId);
36→    }
37→  }
38→);
