256→/**
257→ * Inject draftwatermark LaTeX package for local compile mode.
258→ * Docker mode handles this via --watermark-mode flag instead.
259→ */
260→function injectWatermarkPackage(
261→  texContent: string,
262→  options: { watermark?: boolean; draftFooter?: boolean }
263→): string {
264→  if (!options.watermark && !options.draftFooter) return texContent;
265→
266→  const beginDocIdx = texContent.indexOf("\\begin{document}");
267→  if (beginDocIdx === -1) return texContent;
268→
269→  let pkg: string;
270→  if (options.watermark) {
271→    pkg =
272→      "\\usepackage[text={SANDBOX --- Apollo},color=gray!20,scale=1.5,angle=45]{draftwatermark}\n";
273→  } else {
274→    pkg =
275→      "\\usepackage[text={Generated with Apollo},color=gray!30,scale=0.3,pos=b]{draftwatermark}\n";
276→  }
277→
278→  return texContent.slice(0, beginDocIdx) + pkg + texContent.slice(beginDocIdx);
279→}
