278→/**
279→ * Inject draftwatermark LaTeX package for local compile mode.
280→ * Docker mode handles this via --watermark-mode flag instead.
281→ */
282→function injectWatermarkPackage(
283→  texContent: string,
284→  options: { watermark?: boolean; draftFooter?: boolean }
285→): string {
286→  if (!options.watermark && !options.draftFooter) return texContent;
287→
288→  const beginDocIdx = texContent.indexOf("\\begin{document}");
289→  if (beginDocIdx === -1) return texContent;
290→
291→  let pkg: string;
292→  if (options.watermark) {
293→    pkg =
294→      "\\usepackage[text={SANDBOX --- Apollo},color=gray!20,scale=1.5,angle=45]{draftwatermark}\n";
295→  } else {
296→    pkg =
297→      "\\usepackage[text={Generated with Apollo},color=gray!30,scale=0.3,pos=b]{draftwatermark}\n";
298→  }
299→
300→  return texContent.slice(0, beginDocIdx) + pkg + texContent.slice(beginDocIdx);
301→}
