  1→const R_PLUMBER_URL =
  2→  process.env.R_PLUMBER_URL ?? "http://localhost:8787";
  3→
  4→export interface RPlumberResult<T> {
  5→  data: T;
  6→  warnings: string[];
  7→}
  8→
  9→export class RPlumberError extends Error {
 10→  constructor(
 11→    message: string,
 12→    public readonly statusCode?: number,
 13→    public readonly rError?: string
 14→  ) {
 15→    super(message);
 16→    this.name = "RPlumberError";
 17→  }
 18→}
 19→
 20→/**
 21→ * Call an R Plumber API endpoint with timeout enforcement.
 22→ */
 23→export async function callRPlumber<T>(
 24→  endpoint: string,
 25→  body: Record<string, unknown>,
 26→  timeoutMs: number
 27→): Promise<RPlumberResult<T>> {
 28→  const url = `${R_PLUMBER_URL}${endpoint}`;
 29→  const controller = new AbortController();
 30→  const timer = setTimeout(() => controller.abort(), timeoutMs);
 31→
 32→  try {
 33→    const headers: Record<string, string> = {
 34→      "Content-Type": "application/json",
 35→    };
 36→    if (process.env.R_PLUMBER_SECRET) {
 37→      headers["Authorization"] = `Bearer ${process.env.R_PLUMBER_SECRET}`;
 38→    }
 39→
 40→    const response = await fetch(url, {
 41→      method: "POST",
 42→      headers,
 43→      body: JSON.stringify(body),
 44→      signal: controller.signal,
 45→    });
 46→
 47→    if (!response.ok) {
 48→      const text = await response.text().catch(() => "");
 49→      throw new RPlumberError(
 50→        `R Plumber returned ${response.status}: ${text}`,
 51→        response.status,
 52→        text
 53→      );
 54→    }
 55→
 56→    const result = (await response.json()) as Record<string, unknown>;
 57→
 58→    // R Plumber returns error field when R code throws
 59→    if (result.error) {
 60→      throw new RPlumberError(
 61→        `R analysis error: ${String(result.error)}`,
 62→        undefined,
 63→        String(result.error)
 64→      );
 65→    }
 66→
 67→    return {
 68→      data: result as T,
 69→      warnings: Array.isArray(result.warnings)
 70→        ? (result.warnings as string[])
 71→        : [],
 72→    };
 73→  } catch (err) {
 74→    if (err instanceof RPlumberError) throw err;
 75→
 76→    if (err instanceof DOMException && err.name === "AbortError") {
 77→      throw new RPlumberError(
 78→        `R analysis timed out after ${timeoutMs}ms`,
 79→        undefined,
 80→        "timeout"
 81→      );
 82→    }
 83→
 84→    throw new RPlumberError(
 85→      `Failed to connect to R Plumber: ${err instanceof Error ? err.message : String(err)}`
 86→    );
 87→  } finally {
 88→    clearTimeout(timer);
 89→  }
 90→}
