 92→/**
 93→ * Health check — verify the R Plumber service is available.
 94→ */
 95→export async function checkRPlumberHealth(): Promise<boolean> {
 96→  try {
 97→    const headers: Record<string, string> = {};
 98→    if (process.env.R_PLUMBER_SECRET) {
 99→      headers["Authorization"] = `Bearer ${process.env.R_PLUMBER_SECRET}`;
100→    }
101→
102→    const response = await fetch(`${R_PLUMBER_URL}/health`, {
103→      headers,
104→      signal: AbortSignal.timeout(5000),
105→    });
106→    return response.ok;
107→  } catch {
108→    return false;
109→  }
110→}
