  1→import * as Sentry from "@sentry/nextjs";
  2→import { mkdir, writeFile } from "fs/promises";
  3→import path from "path";
  4→import os from "os";
  5→import { callRPlumber, RPlumberError } from "./client";
  6→import { createAdminSupabaseClient } from "@/lib/supabase/admin";
  7→import { uploadToR2 } from "@/lib/r2/client";
  8→import {
  9→  ANALYSIS_TIMEOUTS,
 10→  REQUIRED_PARAMS,
 11→  type AnalysisType,
 12→} from "@/lib/validation/analysis-schemas";
 13→import type { Analysis } from "@/lib/types/database";
 14→
 15→/** Base directory for local figure file storage (fallback) */
 16→const FIGURES_BASE_DIR = path.join(os.tmpdir(), "apollo-figures");
 17→
 18→/** R Plumber response shape (consistent across all endpoints) */
 19→export interface RAnalysisResponse {
 20→  summary: Record<string, unknown>;
 21→  table_latex: string;
 22→  figures: { filename: string; base64: string }[];
 23→  warnings: string[];
 24→  r_script: string;
