 30→  figures_urls: string[];
 31→  r_script: string;
 32→  warnings: string[];
 33→}
 34→
 35→/** Map analysis type to R Plumber endpoint */
 36→const ENDPOINT_MAP: Record<AnalysisType, string> = {
 37→  descriptive: "/descriptive",
 38→  "chi-square": "/chi-square",
 39→  "t-test": "/t-test",
 40→  correlation: "/correlation",
 41→  survival: "/survival",
 42→  roc: "/roc",
 43→  logistic: "/logistic",
 44→  kruskal: "/kruskal",
 45→  "meta-analysis": "/meta-analysis",
 46→};
 47→
 48→/**
 49→ * Run a statistical analysis by dispatching to the correct R Plumber endpoint.
 50→ * Updates the analysis record in the database with results.
 51→ */
 52→export async function runAnalysis(
 53→  analysis: Analysis,
 54→  datasetRows: Record<string, unknown>[]
 55→): Promise<AnalysisResult> {
 56→  const analysisType = analysis.analysis_type as AnalysisType;
 57→  const endpoint = ENDPOINT_MAP[analysisType];
 58→  const timeout = ANALYSIS_TIMEOUTS[analysisType] ?? 30_000;
 59→  const params = analysis.parameters_json;
 60→
 61→  if (!endpoint) {
 62→    throw new Error(`Unknown analysis type: ${analysisType}`);
 63→  }
 64→
 65→  // Pre-flight: validate data is not empty
 66→  if (datasetRows.length === 0) {
 67→    throw new Error(
 68→      `Cannot run ${analysisType} analysis: dataset has no rows. ` +
 69→      "Please re-upload the dataset file."
 70→    );
 71→  }
 72→
 73→  // Pre-flight: validate required parameters are present
 74→  const requiredParams = REQUIRED_PARAMS[analysisType] ?? [];
 75→  const missingParams = requiredParams.filter(
 76→    (key) => !params[key] || (typeof params[key] === "string" && params[key].trim() === "")
 77→  );
 78→  if (missingParams.length > 0) {
 79→    throw new Error(
 80→      `Cannot run ${analysisType} analysis: missing required parameters: ${missingParams.join(", ")}. ` +
 81→      "Please specify the required column mappings."
 82→    );
 83→  }
 84→
 85→  // Pre-flight: validate that referenced columns exist in the dataset
 86→  const dataColumns = Object.keys(datasetRows[0] ?? {});
 87→  const columnParams = ["outcome", "predictor", "group", "time", "event"] as const;
 88→  for (const key of columnParams) {
 89→    const colName = params[key] as string | undefined;
 90→    if (colName && colName.trim() !== "" && !colName.includes(",")) {
 91→      // Single column reference — check it exists (skip comma-separated predictors)
 92→      if (!dataColumns.includes(colName)) {
 93→        throw new Error(
 94→          `Column "${colName}" (${key}) not found in dataset. ` +
 95→          `Available columns: ${dataColumns.join(", ")}`
 96→        );
 97→      }
 98→    }
 99→  }
100→
101→  // Build the request body — only include defined parameters
102→  const requestBody: Record<string, unknown> = {
103→    data: datasetRows,
104→    confidence_level: params.confidence_level ?? 0.95,
105→  };
106→  // Only add non-empty string parameters to avoid sending undefined/null to R
107→  for (const key of columnParams) {
108→    const val = params[key] as string | undefined;
109→    if (val && val.trim() !== "") {
110→      requestBody[key] = val;
111→    }
112→  }
113→  // Pass figure preferences if present
114→  const figPrefs = params.figure_preferences as Record<string, unknown> | undefined;
115→  if (figPrefs) {
116→    if (figPrefs.chart_type && figPrefs.chart_type !== "auto") {
117→      requestBody.chart_type = figPrefs.chart_type;
118→    }
119→    if (figPrefs.colour_scheme && figPrefs.colour_scheme !== "default") {
120→      requestBody.colour_scheme = figPrefs.colour_scheme;
121→    }
122→  }
123→
124→  const { data: rResult } = await Sentry.startSpan(
125→    {
126→      name: `r-plumber.${analysisType}`,
127→      op: "r.analysis",
128→      attributes: {
129→        "analysis.type": analysisType,
130→        "analysis.endpoint": endpoint,
131→        "analysis.timeout_ms": timeout,
132→        "analysis.id": analysis.id,
133→        "project.id": analysis.project_id,
134→      },
135→    },
136→    () => callRPlumber<RAnalysisResponse>(endpoint, requestBody, timeout)
137→  );
138→
139→  // Process figures — decode base64 and write to disk + insert DB record
140→  const figureUrls: string[] = [];
141→  const supabase = createAdminSupabaseClient();
142→
143→  for (const fig of rResult.figures ?? []) {
144→    if (!fig || !fig.base64) continue;
145→
146→    const figureUrl = `figures/${analysis.project_id}/${analysis.id}/${fig.filename}`;
147→    figureUrls.push(figureUrl);
148→
149→    const buffer = Buffer.from(fig.base64, "base64");
150→
151→    // Upload to R2 for persistent storage
152→    try {
153→      const r2Key = `projects/${analysis.project_id}/figures/${analysis.id}/${fig.filename}`;
154→      const contentType = fig.filename.endsWith(".pdf")
155→        ? "application/pdf"
156→        : "image/png";
157→      await uploadToR2(r2Key, buffer, contentType);
158→    } catch (err) {
159→      console.warn(`Failed to upload figure to R2 ${figureUrl}:`, err);
160→    }
161→
162→    // Also write to local disk as fallback for dev/immediate compile
163→    try {
164→      const figDir = path.join(
165→        FIGURES_BASE_DIR,
166→        analysis.project_id,
167→        analysis.id
168→      );
169→      await mkdir(figDir, { recursive: true });
170→      await writeFile(path.join(figDir, fig.filename), buffer);
171→    } catch (err) {
172→      console.warn(`Failed to write figure file ${figureUrl}:`, err);
173→    }
174→
175→    // Insert figure record
176→    await supabase.from("figures").insert({
177→      project_id: analysis.project_id,
178→      figure_type: analysisType,
179→      source_tool: "ggplot2",
180→      source_code: rResult.r_script,
181→      file_url: figureUrl,
182→      caption: `${analysisType} analysis --- ${fig.filename}`,
183→      label: `fig:${analysisType}-${fig.filename.replace(/\.[^.]+$/, "").replace(/[^a-z0-9-]/g, "-")}`,
184→      width_pct: 100,
185→      dpi: 300,
186→      format: "pdf",
187→    });
188→  }
189→
190→  return {
191→    results_json: {
192→      summary: rResult.summary,
193→      table_latex: rResult.table_latex,
194→    },
195→    figures_urls: figureUrls,
196→    r_script: rResult.r_script ?? "",
197→    warnings: rResult.warnings ?? [],
198→  };
199→}
