201→/**
202→ * Execute analysis end-to-end: fetch data, run R, update DB status.
203→ */
204→export async function executeAnalysis(analysisId: string): Promise<void> {
205→  const supabase = createAdminSupabaseClient();
206→
207→  // Update status to running
208→  await supabase
209→    .from("analyses")
210→    .update({ status: "running" })
211→    .eq("id", analysisId);
212→
213→  try {
214→    // Fetch analysis + dataset
215→    const { data: analysis, error: fetchErr } = await supabase
216→      .from("analyses")
217→      .select("*")
218→      .eq("id", analysisId)
219→      .single();
220→
221→    if (fetchErr || !analysis) {
222→      throw new Error(`Analysis ${analysisId} not found`);
223→    }
224→
225→    const typedAnalysis = analysis as Analysis;
226→
227→    if (!typedAnalysis.dataset_id) {
228→      throw new Error("Analysis has no associated dataset");
229→    }
230→
231→    // Fetch dataset with stored rows
232→    const { data: dataset } = await supabase
233→      .from("datasets")
234→      .select("*")
235→      .eq("id", typedAnalysis.dataset_id)
236→      .single();
237→
238→    if (!dataset) {
239→      throw new Error(`Dataset ${typedAnalysis.dataset_id} not found`);
240→    }
241→
242→    // Use stored rows_json — fail if missing (dataset must be re-uploaded)
243→    const datasetRows = dataset.rows_json as Record<string, unknown>[] | null;
244→    if (!datasetRows || datasetRows.length === 0) {
245→      throw new Error(
246→        `Dataset ${typedAnalysis.dataset_id} has no stored data (rows_json is empty). ` +
247→        "The dataset file may need to be re-uploaded."
248→      );
249→    }
250→
251→    const result = await runAnalysis(typedAnalysis, datasetRows);
252→
253→    // Update analysis with results
254→    await supabase
255→      .from("analyses")
256→      .update({
257→        status: "completed",
258→        results_json: result.results_json,
259→        figures_urls: result.figures_urls,
260→        r_script: result.r_script,
261→      })
262→      .eq("id", analysisId);
263→  } catch (err) {
264→    const errorMessage =
265→      err instanceof RPlumberError
266→        ? err.message
267→        : err instanceof Error
268→          ? err.message
269→          : "Unknown error";
270→
271→    await supabase
272→      .from("analyses")
273→      .update({
274→        status: "failed",
275→        results_json: { error: errorMessage },
276→      })
277→      .eq("id", analysisId);
278→
279→    throw err;
280→  }
281→}
