331→  supabase: ReturnType<typeof createAdminSupabaseClient>,
332→  compilationId: string,
333→  data: Partial<Compilation>
334→) {
335→  await supabase
336→    .from("compilations")
337→    .update(data)
338→    .eq("id", compilationId);
339→}
