 27→/**
 28→ * Compile LaTeX source into a PDF.
 29→ */
 30→export async function compileTex(
 31→  texContent: string,
 32→  options: {
 33→    projectId: string;
 34→    watermark?: boolean;
 35→    draftFooter?: boolean;
 36→    clsFiles?: string[];
 37→    bstFile?: string;
 38→    bibContent?: string;
 39→    chapterFiles?: Record<string, string>;
 40→    /** Paths to figure files (relative path → absolute source path) */
 41→    figureFiles?: Record<string, string>;
 42→  }
 43→): Promise<CompileResult> {
 44→  const mode = getCompileMode();
 45→
 46→  if (mode === "mock") {
 47→    return mockCompile(texContent);
 48→  }
 49→
 50→  if (mode === "docker") {
 51→    return dockerCompile(texContent, options);
 52→  }
 53→
 54→  return localCompile(texContent, options);
 55→}
 56→
 57→async function mockCompile(texContent: string): Promise<CompileResult> {
 58→  // For testing: validate basic TeX structure
 59→  const hasDocumentClass = /\\documentclass/.test(texContent);
 60→  const hasBeginDocument = /\\begin\{document\}/.test(texContent);
 61→  const hasEndDocument = /\\end\{document\}/.test(texContent);
 62→
 63→  const errors: string[] = [];
 64→  if (!hasDocumentClass) errors.push("Missing \\documentclass");
 65→  if (!hasBeginDocument) errors.push("Missing \\begin{document}");
 66→  if (!hasEndDocument) errors.push("Missing \\end{document}");
 67→
 68→  const success = errors.length === 0;
 69→
 70→  // In mock mode, write a minimal placeholder PDF so the compile route sees a pdfPath
 71→  let pdfPath: string | null = null;
 72→  if (success) {
 73→    const persistDir = path.join(os.tmpdir(), "apollo-pdfs");
 74→    await mkdir(persistDir, { recursive: true });
 75→    pdfPath = path.join(persistDir, "mock.pdf");
 76→    // Minimal valid PDF (1-page blank)
 77→    const minimalPdf = `%PDF-1.0\n1 0 obj<</Pages 2 0 R>>endobj\n2 0 obj<</Kids[3 0 R]/Count 1>>endobj\n3 0 obj<</MediaBox[0 0 612 792]>>endobj\ntrailer<</Root 1 0 R>>\n`;
 78→    await writeFile(pdfPath, minimalPdf);
 79→  }
 80→
 81→  return {
 82→    success,
 83→    pdfPath,
 84→    log: { errors, warnings: [], errorCount: errors.length, warningCount: 0 },
 85→    rawLog: success ? "Mock compilation successful" : errors.join("\n"),
 86→    compileTimeMs: 1,
 87→  };
 88→}
 89→
 90→async function dockerCompile(
 91→  texContent: string,
 92→  options: {
 93→    projectId: string;
 94→    watermark?: boolean;
 95→    draftFooter?: boolean;
 96→    clsFiles?: string[];
 97→    bstFile?: string;
 98→    bibContent?: string;
 99→    chapterFiles?: Record<string, string>;
100→    figureFiles?: Record<string, string>;
101→  }
102→): Promise<CompileResult> {
103→  const start = Date.now();
104→  const workDir = path.join(os.tmpdir(), `apollo-compile-${options.projectId}`);
105→
106→  try {
107→    await mkdir(workDir, { recursive: true });
108→
109→    // Write tex content
110→    await writeFile(path.join(workDir, "main.tex"), texContent);
111→
112→    // Copy CLS and BST files from templates
113→    const templatesDir = path.resolve(process.cwd(), "../../templates");
114→    const clsFiles = options.clsFiles ?? ["sskm-thesis.cls", "ssuhs-thesis.cls"];
115→
116→    for (const cls of clsFiles) {
117→      try {
118→        const content = await readFile(path.join(templatesDir, cls));
119→        await writeFile(path.join(workDir, cls), content);
120→      } catch {
121→        // CLS file may not exist locally in all environments
122→      }
123→    }
124→
125→    const bstFile = options.bstFile ?? "vancouver.bst";
126→    try {
127→      const bstContent = await readFile(path.join(templatesDir, bstFile));
128→      await writeFile(path.join(workDir, bstFile), bstContent);
129→    } catch {
130→      // BST may not exist locally
131→    }
132→
133→    // Copy logo directory
134→    try {
135→      const logoDir = path.join(templatesDir, "logo");
136→      await mkdir(path.join(workDir, "logo"), { recursive: true });
137→      const { stdout: logoFiles } = await execFileAsync("ls", [logoDir]);
138→      for (const file of logoFiles.trim().split("\n").filter(Boolean)) {
139→        const content = await readFile(path.join(logoDir, file));
140→        await writeFile(path.join(workDir, "logo", file), content);
141→      }
142→    } catch {
143→      // Logo dir may not exist
144→    }
145→
146→    // Write references.bib (populated or empty)
147→    await writeFile(path.join(workDir, "references.bib"), options.bibContent ?? "");
148→
149→    // Write chapter files
150→    if (options.chapterFiles) {
151→      await mkdir(path.join(workDir, "chapters"), { recursive: true });
152→      for (const [filename, content] of Object.entries(options.chapterFiles)) {
153→        await writeFile(path.join(workDir, filename), content);
154→      }
155→    }
156→
157→    // Copy figure files (relative path → absolute source) into workDir
158→    if (options.figureFiles) {
159→      for (const [relPath, srcPath] of Object.entries(options.figureFiles)) {
160→        try {
161→          const destPath = path.join(workDir, relPath);
162→          await mkdir(path.dirname(destPath), { recursive: true });
163→          await copyFile(srcPath, destPath);
164→        } catch {
165→          // Figure file may have been deleted — non-fatal
166→        }
167→      }
168→    }
169→
170→    // Create output directory
171→    const outputDir = path.join(workDir, "output");
172→    await mkdir(outputDir, { recursive: true });
173→
174→    // Run Docker compile with full security hardening
175→    const containerName = process.env.LATEX_CONTAINER_NAME ?? "apollo-latex";
176→
177→    // Resolve seccomp profile path (graceful fallback to Docker default)
178→    const seccompPath = path.resolve(process.cwd(), "../../docker/seccomp-latex.json");
179→    let seccompExists = false;
180→    try {
181→      await access(seccompPath);
182→      seccompExists = true;
