 54→    if (typedProject.current_phase !== phaseNumber) {
 55→      return conflict(
 56→        `Cannot approve Phase ${phaseNumber} — project is currently at Phase ${typedProject.current_phase}`
 57→      );
 58→    }
 59→
 60→    // Fetch section
 61→    const { data: section, error: sectionError } = await supabase
 62→      .from("sections")
 63→      .select("*")
 64→      .eq("project_id", id)
 65→      .eq("phase_number", phaseNumber)
 66→      .single();
 67→
 68→    if (sectionError || !section) {
 69→      return notFound("Section not found — generate content first");
 70→    }
 71→
 72→    const typedSection = section as Section;
 73→
 74→    // Section must have approvable content
 75→    if (typedSection.status === "generating") {
 76→      return conflict("Section is still generating — wait for completion before approving");
 77→    }
 78→
 79→    if (typedSection.status === "approved") {
 80→      return conflict("Section is already approved");
 81→    }
 82→
 83→    if (typedSection.status === "draft") {
 84→      if (!typedSection.latex_content?.trim()) {
 85→        return badRequest("Cannot approve a draft section with no content — generate content first");
 86→      }
 87→      // Auto-promote draft with content to review, then approve below
 88→      await supabase
 89→        .from("sections")
 90→        .update({ status: "review", updated_at: new Date().toISOString() })
 91→        .eq("id", typedSection.id);
 92→    }
 93→
 94→    // At this point status is "review" (either originally or just promoted)
 95→
 96→    // AI-powered review (non-blocking — warnings only, never blocks approval)
 97→    let aiReviewIssues: { severity: string; category: string; message: string }[] = [];
 98→    if (typedProject.synopsis_text && typedSection.latex_content) {
 99→      try {
100→        aiReviewIssues = await aiReviewSection(
101→          typedSection.latex_content,
102→          phaseNumber,
103→          id,
104→          typedProject.synopsis_text,
105→          (typedProject.metadata_json ?? {}) as Record<string, unknown>,
106→        );
107→      } catch {
108→        // Non-blocking — AI review failure doesn't prevent approval
109→      }
110→    }
111→
112→    // Phase 6: Figure/table QC gates (DECISIONS.md 5.3)
113→    if (phaseNumber === 6) {
114→      const MIN_FIGURES = 5;
115→      const MIN_TABLES = 7;
116→
117→      const [{ count: figureCount }, { data: completedWithTables }] =
118→        await Promise.all([
119→          supabase
120→            .from("figures")
121→            .select("id", { count: "exact", head: true })
122→            .eq("project_id", id),
123→          supabase
124→            .from("analyses")
125→            .select("results_json")
126→            .eq("project_id", id)
127→            .eq("status", "completed"),
128→        ]);
129→
130→      // Count actual tables: analyses with table_latex in results_json
131→      const analysisTableCount = (completedWithTables ?? []).filter(
132→        (a) => (a.results_json as Record<string, unknown>)?.table_latex
133→      ).length;
134→
135→      if ((figureCount ?? 0) < MIN_FIGURES) {
136→        return badRequest(
137→          `Results requires at least ${MIN_FIGURES} figures (currently ${figureCount ?? 0}). ` +
138→            "Run additional analyses or upload figures."
139→        );
140→      }
141→
142→      // Count tables: use the HIGHER of analysis-produced tables vs LaTeX table
143→      // environments in content. These overlap (AI embeds analysis tables verbatim),
144→      // so summing would double-count.
145→      const tableLatexCount = (
146→        typedSection.latex_content?.match(
147→          /\\begin\{(table|longtable|tabular)\}/g
148→        ) ?? []
149→      ).length;
150→      const totalTables = Math.max(analysisTableCount ?? 0, tableLatexCount);
151→
152→      if (totalTables < MIN_TABLES) {
153→        return badRequest(
154→          `Results requires at least ${MIN_TABLES} tables (currently ${totalTables}). ` +
155→            "Run additional analyses to generate more tables."
156→        );
157→      }
158→
159→      // Analysis plan match: every non-skipped planned analysis must have a completed result
160→      const plan = (typedProject.analysis_plan_json ?? []) as unknown as PlannedAnalysis[];
161→      if (plan.length > 0) {
162→        const { data: completedAnalyses } = await supabase
163→          .from("analyses")
164→          .select("analysis_type")
165→          .eq("project_id", id)
166→          .eq("status", "completed");
167→
168→        const completedSet = new Set(
169→          (completedAnalyses ?? []).map((a) => a.analysis_type)
170→        );
171→        const missingPlanned = plan.filter(
172→          (p) => p.status !== "skipped" && !completedSet.has(p.analysis_type)
173→        );
174→
175→        if (missingPlanned.length > 0) {
176→          return badRequest(
177→            `Analysis plan incomplete: ${missingPlanned.map((p) => p.analysis_type).join(", ")} not yet completed.`
178→          );
179→        }
180→      }
181→    }
182→
183→    // Phase 11: Final QC gate — MUST run BEFORE section approval.
184→    // If QC fails, we return early without changing section status, so the
185→    // student can fix issues and re-attempt.
186→    if (phaseNumber === 11) {
187→      const { finalQC } = await import("@/lib/qc/final-qc");
188→      const [
189→        { data: allSections },
190→        { data: allCitations },
191→        { data: latestCompilation },
192→        { count: figs },
193→        { data: qcAnalyses },
194→      ] = await Promise.all([
195→        supabase
196→          .from("sections")
197→          .select("*")
198→          .eq("project_id", id)
199→          .order("phase_number", { ascending: true }),
200→        supabase.from("citations").select("*").eq("project_id", id),
201→        supabase
202→          .from("compilations")
203→          .select("log_text")
204→          .eq("project_id", id)
205→          .eq("status", "completed")
206→          .order("created_at", { ascending: false })
207→          .limit(1)
208→          .maybeSingle(),
209→        supabase
210→          .from("figures")
211→          .select("id", { count: "exact", head: true })
212→          .eq("project_id", id),
213→        supabase
214→          .from("analyses")
215→          .select("results_json")
216→          .eq("project_id", id)
217→          .eq("status", "completed"),
218→      ]);
219→
220→      const qcTableCount = (qcAnalyses ?? []).filter(
221→        (a) => (a.results_json as Record<string, unknown>)?.table_latex
222→      ).length;
223→
224→      const qcReport = finalQC(
225→        (allSections ?? []) as Section[],
226→        (allCitations ?? []) as import("@/lib/types/database").Citation[],
227→        (latestCompilation?.log_text as string) ?? null,
228→        figs ?? 0,
229→        qcTableCount,
230→      );
231→
232→      if (!qcReport.overallPass) {
233→        return badRequest(
234→          `Final QC failed: ${qcReport.blockingCount} blocking issue(s). ` +
235→          "Run Final QC from the dashboard for details."
236→        );
237→      }
238→    }
239→
240→    // Check if project can advance
241→    const transitionCheck = canAdvancePhase(typedProject);
242→
243→    if (!transitionCheck.allowed) {
244→      if (transitionCheck.code === "LICENCE_REQUIRED") {
245→        return licenceRequired("Attach a licence to advance beyond Phase 1");
246→      }
247→      return conflict(transitionCheck.reason ?? "Cannot advance phase");
248→    }
249→
250→    // Approve section and advance phase
251→    const newPhase = phaseNumber + 1;
252→    const newPhasesCompleted = [
253→      ...typedProject.phases_completed,
254→      phaseNumber,
255→    ];
256→
257→    const [sectionResult, projectResult] = await Promise.all([
258→      supabase
259→        .from("sections")
260→        .update({ status: "approved", updated_at: new Date().toISOString() })
261→        .eq("id", typedSection.id)
262→        .select("*")
263→        .single(),
264→      supabase
265→        .from("projects")
266→        .update({
267→          current_phase: newPhase,
268→          phases_completed: newPhasesCompleted,
269→          updated_at: new Date().toISOString(),
270→        })
271→        .eq("id", id)
272→        .select("*")
273→        .single(),
274→    ]);
275→
276→    if (sectionResult.error || projectResult.error) {
277→      console.error("Failed to approve section:", sectionResult.error, projectResult.error);
278→      return internalError("Failed to approve section");
279→    }
280→
281→    // Track monthly phase advancement for subscription plans
282→    if (typedProject.license_id) {
283→      try {
284→        const { data: licence } = await supabase
285→          .from("thesis_licenses")
286→          .select("plan_type, monthly_phases_advanced")
287→          .eq("id", typedProject.license_id)
288→          .single();
289→
290→        if (licence) {
291→          try {
292→            const config = getPlanConfig(licence.plan_type as string);
293→            if (config.maxPhasesPerMonth !== null) {
294→              await supabase
295→                .from("thesis_licenses")
296→                .update({
297→                  monthly_phases_advanced:
298→                    (licence.monthly_phases_advanced as number) + 1,
299→                })
300→                .eq("id", typedProject.license_id);
301→            }
302→          } catch {
303→            // Unknown plan type — skip tracking
304→          }
305→        }
306→      } catch (trackErr) {
307→        console.warn("Monthly phase tracking failed (non-blocking):", trackErr);
308→      }
309→    }
310→
311→    // If Phase 0 was just approved, auto-create Phase 1 section with front matter
312→    if (phaseNumber === 0) {
313→      const phase1Def = getPhase(1);
314→      const updatedProject = projectResult.data as Project;
315→      const frontMatterLatex = generateFrontMatterLatex(updatedProject);
316→
317→      await supabase.from("sections").upsert(
318→        {
319→          project_id: id,
320→          phase_number: 1,
321→          phase_name: phase1Def?.name ?? "front_matter",
322→          latex_content: frontMatterLatex,
323→          word_count: frontMatterLatex.split(/\s+/).length,
324→          citation_keys: [],
325→          status: "review",
326→        },
327→        { onConflict: "project_id,phase_number" }
328→      );
329→    }
330→
331→    // Phase 8 approved → auto-create Phase 9 with citation audit results
332→    if (phaseNumber === 8) {
333→      const phase9Def = getPhase(9);
334→      try {
335→        // Fetch all sections and citations for audit
336→        const [{ data: allSections }, { data: allCitations }] = await Promise.all([
337→          supabase
338→            .from("sections")
339→            .select("*")
340→            .eq("project_id", id)
341→            .eq("status", "approved"),
342→          supabase
343→            .from("citations")
344→            .select("*")
345→            .eq("project_id", id),
346→        ]);
347→
348→        const auditResult = auditCitations(
349→          (allSections ?? []) as Section[],
350→          (allCitations ?? []) as unknown as import("@/lib/types/database").Citation[]
351→        );
352→
353→        const citations = allCitations ?? [];
354→        const auditLatex = [
355→          "\\section{Citation Audit Summary}",
356→          "",
357→          `Integrity Score: ${auditResult.integrityScore}\\%`,
358→          "",
359→          `Total citations: ${citations.length}`,
360→          `Tier A (verified): ${citations.filter((c) => c.provenance_tier === "A").length}`,
361→          `Tier B (confirmed): ${citations.filter((c) => c.provenance_tier === "B").length}`,
362→          `Tier C (attested): ${citations.filter((c) => c.provenance_tier === "C").length}`,
363→          `Tier D (unverified): ${citations.filter((c) => c.provenance_tier === "D").length}`,
364→          "",
365→          auditResult.missingCitations.length > 0
366→            ? `\\subsection{Missing Citations}\n${auditResult.missingCitations.map((m) => `\\texttt{${m.citeKey}}`).join(", ")}`
367→            : "No missing citations.",
368→          "",
369→          auditResult.orphanedCitations.length > 0
370→            ? `\\subsection{Orphaned Citations}\n${auditResult.orphanedCitations.map((o) => `\\texttt{${o.citeKey}}`).join(", ")}`
371→            : "No orphaned citations.",
372→          "",
373→          auditResult.tierDBlocking.length > 0
374→            ? `\\subsection{Tier D Citations (Blocking Final QC)}\n${auditResult.tierDBlocking.map((d) => `\\texttt{${d.citeKey}}`).join(", ")}`
375→            : "No Tier D citations blocking Final QC.",
376→        ].join("\n");
377→
378→        await supabase.from("sections").upsert(
379→          {
380→            project_id: id,
381→            phase_number: 9,
382→            phase_name: phase9Def?.name ?? "references",
383→            latex_content: auditLatex,
384→            word_count: auditLatex.split(/\s+/).length,
385→            citation_keys: [],
386→            status: "draft",
387→          },
388→          { onConflict: "project_id,phase_number" }
389→        );
390→      } catch (auditError) {
391→        console.warn("Citation audit auto-trigger failed (non-blocking):", auditError);
392→      }
393→    }
394→
395→    // Phase 10 approved → auto-create Phase 11 with QC placeholder
396→    if (phaseNumber === 10) {
397→      const phase11Def = getPhase(11);
398→      await supabase.from("sections").upsert(
399→        {
400→          project_id: id,
401→          phase_number: 11,
402→          phase_name: phase11Def?.name ?? "final_qc",
403→          latex_content: "Run Final QC to generate the quality report.",
404→          word_count: 0,
405→          citation_keys: [],
406→          status: "review",
407→        },
408→        { onConflict: "project_id,phase_number" }
409→      );
410→    }
411→
412→    // Phase 11 approved + QC passed (checked above) → mark project completed
413→    if (phaseNumber === 11) {
414→      await supabase
415→        .from("projects")
416→        .update({
417→          status: "completed",
418→          updated_at: new Date().toISOString(),
419→        })
420→        .eq("id", id);
421→    }
422→
423→    // Emit Inngest event for workflow orchestration (skip if not configured)
424→    if (process.env.INNGEST_EVENT_KEY) {
425→      try {
426→        await inngest.send({
427→          name: "thesis/phase.approved",
428→          data: {
429→            projectId: id,
430→            phaseNumber,
431→            userId: authResult.user.id,
432→          },
433→        });
434→      } catch (inngestError) {
435→        // Non-blocking — workflow will retry on next approval
436→        console.warn("Failed to emit Inngest event:", inngestError);
437→      }
438→    }
439→
440→    return NextResponse.json({
441→      data: {
442→        section: sectionResult.data,
443→        project: projectResult.data,
444→        advanced_to_phase: newPhase,
445→        ai_review: aiReviewIssues.length > 0 ? aiReviewIssues : undefined,
446→      },
447→    });
448→  } catch (err) {
449→    console.error("Unexpected error in POST /api/projects/[id]/sections/[phase]/approve:", err);
450→    return internalError();
451→  }
452→}
