 28→// Sections stuck in "generating" for longer than this are considered stale
 29→const STALE_GENERATING_MS = 5 * 60 * 1000; // 5 minutes
 30→
 31→export async function POST(
 32→  request: NextRequest,
 33→  { params }: { params: Promise<{ id: string; phase: string }> }
 34→) {
 35→  try {
 36→    const authResult = await getAuthenticatedUser();
 37→    if (!authResult) return unauthorised();
 38→
 39→    const { id, phase } = await params;
 40→    const phaseNumber = parseInt(phase, 10);
 41→
 42→    if (!isValidPhase(phaseNumber)) {
 43→      return badRequest("Invalid phase number");
 44→    }
 45→
 46→    // Rate limit check
 47→    const rateCheck = await checkRateLimit(authResult.user.id);
 48→    if (!rateCheck.allowed) {
 49→      return rateLimited(rateCheck.retryAfterSeconds);
 50→    }
 51→
 52→    // Licence phase gate
 53→    const gateResult = await checkLicenceForPhase(id, authResult.user.id, phaseNumber, "generate");
 54→    if (gateResult instanceof NextResponse) return gateResult;
 55→
 56→    const supabase = createAdminSupabaseClient();
 57→    const typedProject = gateResult.project;
 58→
 59→    // Phase 0: Synopsis parsing (special handling)
 60→    if (phaseNumber === 0) {
 61→      return handlePhase0Generate(request, typedProject, supabase);
 62→    }
 63→
 64→    // Phases 1-8: General thesis section generation
 65→    const systemPrompt = getPhaseSystemPrompt(phaseNumber);
 66→    if (!systemPrompt) {
 67→      return badRequest(`AI generation for Phase ${phaseNumber} is not yet supported`);
 68→    }
 69→
 70→    return handleSectionGenerate(phaseNumber, typedProject, supabase, gateResult);
 71→  } catch (err) {
 72→    console.error("Unexpected error in POST generate:", err);
 73→    return internalError();
 74→  }
 75→}
