 77→/**
 78→ * Check if a "generating" section is stale (stuck for > 5 minutes).
 79→ * If stale, reset to "draft" so re-generation can proceed.
 80→ */
 81→async function checkAndResetStaleGeneration(
 82→  supabase: ReturnType<typeof createAdminSupabaseClient>,
 83→  projectId: string,
 84→  phaseNumber: number
 85→): Promise<boolean> {
 86→  const { data: existingSection } = await supabase
 87→    .from("sections")
 88→    .select("status, updated_at")
 89→    .eq("project_id", projectId)
 90→    .eq("phase_number", phaseNumber)
 91→    .single();
 92→
 93→  if (existingSection?.status !== "generating") {
 94→    return false; // Not generating, no conflict
 95→  }
 96→
 97→  // Check if the generation is stale
 98→  const updatedAt = new Date(existingSection.updated_at).getTime();
 99→  const isStale = Date.now() - updatedAt > STALE_GENERATING_MS;
100→
101→  if (isStale) {
102→    console.warn(
103→      `Resetting stale "generating" section: project=${projectId} phase=${phaseNumber} (stuck since ${existingSection.updated_at})`
104→    );
105→    await supabase
106→      .from("sections")
107→      .update({ status: "draft", updated_at: new Date().toISOString() })
108→      .eq("project_id", projectId)
109→      .eq("phase_number", phaseNumber);
110→    return false; // Reset done, no conflict
111→  }
112→
113→  return true; // Still actively generating
114→}
