136→/**
137→ * Generic handler for phases 1-8: generates thesis section content via SSE streaming.
138→ */
139→async function handleSectionGenerate(
140→  phaseNumber: number,
141→  project: Project,
142→  supabase: ReturnType<typeof createAdminSupabaseClient>,
143→  gateResult: LicenceGateResult,
144→) {
145→  if (!project.synopsis_text) {
146→    return badRequest("Project has no synopsis text — upload a synopsis first");
147→  }
148→
149→  // Check section isn't already generating (with stale detection)
150→  const isActivelyGenerating = await checkAndResetStaleGeneration(
151→    supabase,
152→    project.id,
153→    phaseNumber
154→  );
155→
156→  if (isActivelyGenerating) {
157→    return conflict("Generation already in progress for this section");
158→  }
159→
160→  // Token budget check — hard-stop if budget exhausted
161→  const budgetCheck = await checkTokenBudget(project.id, phaseNumber);
162→  if (!budgetCheck.allowed) {
163→    return badRequest(budgetCheck.reason ?? "Token budget exhausted");
164→  }
165→
166→  // Gather context from previously completed sections
167→  const { data: approvedSections } = await supabase
168→    .from("sections")
169→    .select("phase_number, phase_name, latex_content")
170→    .eq("project_id", project.id)
171→    .eq("status", "approved")
172→    .order("phase_number", { ascending: true });
173→
174→  const previousSections = (approvedSections ?? [])
175→    .filter((s) => s.phase_number < phaseNumber && s.latex_content)
176→    .map((s) => ({
177→      phaseName: s.phase_name as string,
178→      content: s.latex_content as string,
179→    }));
180→
181→  // Get prompts
182→  const systemPrompt = getPhaseSystemPrompt(phaseNumber)!;
183→
184→  // Phase 10 (Appendices): fetch dataset columns for proforma context
185→  let extraContext: { datasetColumns?: Record<string, unknown>[] } | undefined;
186→  if (phaseNumber === 10) {
187→    const { data: dataset } = await supabase
188→      .from("datasets")
189→      .select("columns_json")
190→      .eq("project_id", project.id)
191→      .order("created_at", { ascending: false })
192→      .limit(1)
193→      .maybeSingle();
194→    if (dataset?.columns_json) {
195→      extraContext = { datasetColumns: dataset.columns_json as Record<string, unknown>[] };
196→    }
197→  }
198→
199→  let userMessage = getPhaseUserMessage(
200→    phaseNumber,
201→    project.synopsis_text,
202→    (project.metadata_json ?? {}) as Record<string, unknown>,
203→    previousSections,
204→    extraContext
205→  );
206→
207→  // Phase 6 (Results): gate on 6a completion then gather analysis context
208→  if (phaseNumber === 6) {
209→    // Gate: Phase 6a must be complete (analysis plan approved)
210→    if (project.analysis_plan_status !== "approved") {
211→      return badRequest(
212→        "Complete Phase 6a first: upload a dataset, generate an analysis plan, and approve it."
213→      );
214→    }
215→
216→    // Gate: All planned analyses must be completed
217→    const plan = (project.analysis_plan_json ?? []) as unknown as PlannedAnalysis[];
218→    const { data: planCheckAnalyses } = await supabase
219→      .from("analyses")
220→      .select("analysis_type, status")
221→      .eq("project_id", project.id)
222→      .eq("status", "completed");
223→
224→    const completedTypes = new Set(
225→      (planCheckAnalyses ?? []).map((a) => a.analysis_type)
226→    );
227→    const unrunPlanned = plan.filter(
228→      (p) => p.status !== "skipped" && !completedTypes.has(p.analysis_type)
229→    );
230→
231→    if (unrunPlanned.length > 0) {
232→      return badRequest(
233→        `${unrunPlanned.length} planned analyses not yet completed: ${unrunPlanned.map((p) => p.analysis_type).join(", ")}`
234→      );
235→    }
236→    const { data: completedAnalyses } = await supabase
237→      .from("analyses")
238→      .select("analysis_type, results_json, r_script")
239→      .eq("project_id", project.id)
240→      .eq("status", "completed")
241→      .order("created_at", { ascending: true });
242→
243→    const { data: projectFigures } = await supabase
244→      .from("figures")
245→      .select("figure_type, label, caption, file_url")
246→      .eq("project_id", project.id)
247→      .order("created_at", { ascending: true });
248→
249→    let analysisContext = "\n\n--- COMPLETED ANALYSES ---\n";
250→
251→    for (const analysis of completedAnalyses ?? []) {
252→      const results = analysis.results_json as Record<string, unknown>;
253→      analysisContext += `\nAnalysis: ${analysis.analysis_type}\n`;
254→      if (results.summary) {
255→        analysisContext += `Summary: ${JSON.stringify(results.summary, null, 2)}\n`;
256→      }
257→      if (results.table_latex) {
258→        analysisContext += `LaTeX Table (include VERBATIM):\n${results.table_latex}\n`;
259→      }
260→    }
261→
262→    if ((projectFigures ?? []).length > 0) {
263→      analysisContext += "\n--- FIGURES ---\n";
264→      for (const fig of projectFigures ?? []) {
265→        analysisContext += `Figure: \\label{${fig.label}} — ${fig.caption} (\\includegraphics{${fig.file_url}})\n`;
266→      }
267→    }
268→
269→    userMessage += analysisContext;
270→  }
271→
272→  // Pre-seed real PubMed references for citation-heavy phases
273→  const CITATION_HEAVY_PHASES = new Set([2, 4, 5, 7]);
274→  if (CITATION_HEAVY_PHASES.has(phaseNumber)) {
275→    try {
276→      const metadata = (project.metadata_json ?? {}) as Record<string, unknown>;
277→      const keywords = (metadata.keywords as string[]) ?? [];
278→      const studyType = (metadata.study_type as string) ?? null;
279→      const department = (metadata.department as string) ?? null;
280→      const maxRefs = phaseNumber === 4 ? 30 : 20; // ROL needs more references
281→
282→      const preSeeded = await preSeedReferences(
283→        project.title ?? "",
284→        keywords,
285→        studyType,
286→        department,
287→        maxRefs
288→      );
289→
290→      if (preSeeded.length > 0) {
291→        userMessage += formatReferencesForPrompt(preSeeded);
292→      }
293→    } catch (err) {
294→      console.warn("Pre-seed references failed (non-blocking):", err);
295→    }
296→  }
297→
298→  // Create/update section as "generating"
299→  const phaseDef = getPhase(phaseNumber);
300→  await supabase.from("sections").upsert(
301→    {
302→      project_id: project.id,
303→      phase_number: phaseNumber,
304→      phase_name: phaseDef?.name ?? `phase_${phaseNumber}`,
305→      latex_content: "",
306→      streaming_content: "",
307→      word_count: 0,
308→      citation_keys: [],
309→      status: "generating",
310→    },
311→    { onConflict: "project_id,phase_number" }
312→  );
313→
314→  // Model routing: Opus for Introduction/Discussion (Professional plan), Sonnet for others
315→  const useOpus = [2, 7].includes(phaseNumber) && gateResult.planConfig?.modelTier === "opus";
316→  const model = useOpus
317→    ? "claude-opus-4-5-20250514"
318→    : "claude-sonnet-4-5-20250929";
319→  // ROL needs most tokens; citation-heavy phases need extra for BibTeX trailer
320→  const maxTokens = phaseNumber === 4 ? 16000   // ROL: ~5K body + ~5K BibTeX (30+ refs)
321→    : phaseNumber === 7 ? 12000                  // Discussion: ~5K body + ~3K BibTeX (20 refs)
322→    : [2, 5].includes(phaseNumber) ? 10000       // Intro/M&M: ~3K body + ~2.5K BibTeX
323→    : 6000;                                      // Aims, Conclusion, others
324→
325→  // Enqueue Inngest background job for AI generation
326→  await inngest.send({
327→    name: "thesis/section.generate",
328→    data: {
329→      projectId: project.id,
330→      phaseNumber,
331→      systemPrompt,
332→      userMessage,
333→      model,
334→      maxTokens,
335→    },
336→  });
337→
338→  // Return immediately --- generation continues in background
339→  return NextResponse.json({
340→    data: { status: "generating", message: "Generation started" },
341→  });
342→}
