344→/**
345→ * Phase 0: Synopsis parsing (returns structured JSON, not LaTeX).
346→ */
347→async function handlePhase0Generate(
348→  _request: NextRequest,
349→  project: Project,
350→  supabase: ReturnType<typeof createAdminSupabaseClient>
351→) {
352→  if (!project.synopsis_text) {
353→    return badRequest("Project has no synopsis text — upload a synopsis first");
354→  }
355→
356→  // Check section isn't already generating (with stale detection)
357→  const isActivelyGenerating = await checkAndResetStaleGeneration(
358→    supabase,
359→    project.id,
360→    0
361→  );
362→
363→  if (isActivelyGenerating) {
364→    return conflict("Generation already in progress for this section");
365→  }
366→
367→  // Create/update section as "generating"
368→  const phaseDef = getPhase(0);
369→  await supabase.from("sections").upsert(
370→    {
371→      project_id: project.id,
372→      phase_number: 0,
373→      phase_name: phaseDef?.name ?? "orientation",
374→      latex_content: "",
375→      word_count: 0,
376→      citation_keys: [],
377→      status: "generating",
378→    },
379→    { onConflict: "project_id,phase_number" }
380→  );
381→
382→  let streamCompleted = false;
383→
384→  // Stream response via SSE
385→  const encoder = new TextEncoder();
386→  const stream = new ReadableStream({
387→    async start(controller) {
388→      try {
389→        const client = getAnthropicClient();
390→        let fullResponse = "";
391→
392→        const messageStream = client.messages.stream({
393→          model: "claude-sonnet-4-5-20250929",
394→          max_tokens: 2000,
395→          system: SYNOPSIS_PARSE_SYSTEM_PROMPT,
396→          messages: [
397→            {
398→              role: "user",
399→              content: `Parse the following medical thesis synopsis and extract structured metadata as JSON:\n\n${project.synopsis_text}`,
400→            },
401→          ],
402→        });
403→
404→        messageStream.on("text", (text) => {
405→          fullResponse += text;
406→          controller.enqueue(
407→            encoder.encode(`data: ${JSON.stringify({ type: "delta", text })}\n\n`)
408→          );
409→        });
410→
411→        await messageStream.finalMessage();
412→
413→        // Parse the complete response
414→        const parsed = parseSynopsisResponse(fullResponse);
415→
416→        if (parsed) {
417→          // Update project metadata with extracted data
418→          const metadataUpdate: Record<string, unknown> = {
419→            ...project.metadata_json,
420→          };
421→          if (parsed.department) metadataUpdate.department = parsed.department;
422→
423→          const projectUpdate: Record<string, unknown> = {
424→            metadata_json: metadataUpdate,
425→            updated_at: new Date().toISOString(),
426→          };
427→          if (parsed.title && !project.title) {
428→            projectUpdate.title = parsed.title;
429→          }
430→          if (parsed.study_type) {
431→            projectUpdate.study_type = parsed.study_type;
432→          }
433→
434→          await supabase
435→            .from("projects")
436→            .update(projectUpdate)
437→            .eq("id", project.id);
438→
439→          // Update section with parsed content and set to review
440→          await supabase
441→            .from("sections")
442→            .update({
443→              latex_content: fullResponse,
444→              status: "review",
445→              updated_at: new Date().toISOString(),
446→            })
447→            .eq("project_id", project.id)
448→            .eq("phase_number", 0);
449→
450→          controller.enqueue(
451→            encoder.encode(
452→              `data: ${JSON.stringify({ type: "complete", parsed })}\n\n`
453→            )
454→          );
455→        } else {
456→          // Parsing failed — still save raw content
457→          await supabase
458→            .from("sections")
459→            .update({
460→              latex_content: fullResponse,
461→              status: "review",
462→              updated_at: new Date().toISOString(),
463→            })
464→            .eq("project_id", project.id)
465→            .eq("phase_number", 0);
466→
467→          controller.enqueue(
468→            encoder.encode(
469→              `data: ${JSON.stringify({ type: "complete", parsed: null, raw: fullResponse })}\n\n`
470→            )
471→          );
472→        }
473→
474→        streamCompleted = true;
475→
476→        controller.enqueue(encoder.encode("data: [DONE]\n\n"));
477→        controller.close();
478→      } catch (err) {
479→        // Reset section status on error
480→        await supabase
481→          .from("sections")
482→          .update({ status: "draft", updated_at: new Date().toISOString() })
483→          .eq("project_id", project.id)
484→          .eq("phase_number", 0);
485→
486→        streamCompleted = true;
487→
488→        const errorMessage =
489→          err instanceof Error ? err.message : "AI generation failed";
490→        controller.enqueue(
491→          encoder.encode(
492→            `data: ${JSON.stringify({ type: "error", message: errorMessage })}\n\n`
493→          )
494→        );
495→        controller.close();
496→      }
497→    },
498→    cancel: async () => {
499→      if (!streamCompleted) {
500→        await makeStreamCancelHandler(supabase, project.id, 0)();
501→      }
502→    },
503→  });
504→
505→  return new Response(stream, {
506→    headers: {
507→      "Content-Type": "text/event-stream",
508→      "Cache-Control": "no-cache",
509→      Connection: "keep-alive",
510→    },
511→  });
512→}
