  1→/**
  2→ * LaTeX validation for chapter content before compilation.
  3→ *
  4→ * Two tiers:
  5→ * 1. Pre-flight (local, deterministic, fast) — blocks on errors
  6→ * 2. AI validation via Haiku (deeper checks) — non-blocking warnings
  7→ */
  8→
  9→import { getAnthropicClient } from "@/lib/ai/client";
 10→
 11→// ── Types ───────────────────────────────────────────────────────────────────
 12→
 13→export interface ValidationIssue {
 14→  chapter: string;
 15→  severity: "error" | "warning";
 16→  message: string;
 17→  line?: number;
 18→}
 19→
 20→// ── Pre-flight checks (local, deterministic) ────────────────────────────────
 21→
 22→/**
 23→ * Run fast, deterministic checks on a chapter's LaTeX content.
 24→ * Returns issues found — "error" severity blocks compilation.
 25→ */
 26→export function preflightChapter(
 27→  chapterName: string,
 28→  latex: string
 29→): ValidationIssue[] {
 30→  if (!latex.trim()) return [];
 31→
 32→  const issues: ValidationIssue[] = [];
 33→  const lines = latex.split("\n");
 34→  let inTabularEnv = 0; // depth counter for tabular/longtable/array
 35→
 36→  for (let i = 0; i < lines.length; i++) {
 37→    const line = lines[i];
 38→    const lineNum = i + 1;
 39→
 40→    // Track tabular environment depth (& is valid inside these)
 41→    const tabOpens = (line.match(/\\begin\{(tabular|longtable|tabularx|array)\}/g) || []).length;
 42→    const tabCloses = (line.match(/\\end\{(tabular|longtable|tabularx|array)\}/g) || []).length;
 43→    inTabularEnv += tabOpens - tabCloses;
 44→    inTabularEnv = Math.max(0, inTabularEnv);
 45→
 46→    // Unescaped # not preceded by \ (the exact bug we hit)
 47→    // Match # that is NOT preceded by \ and NOT inside a comment (line starting with %)
 48→    if (!line.trimStart().startsWith("%")) {
 49→      const hashMatches = line.matchAll(/(?<!\\)#/g);
 50→      for (const match of hashMatches) {
 51→        // Skip if inside a \# sequence (already escaped)
 52→        const pos = match.index!;
 53→        if (pos > 0 && line[pos - 1] === "\\") continue;
 54→        issues.push({
 55→          chapter: chapterName,
 56→          severity: "error",
 57→          message: `Unescaped '#' character — use '\\#' in LaTeX`,
 58→          line: lineNum,
 59→        });
 60→      }
 61→    }
 62→
 63→    // Bare & outside tabular environments (causes "Misplaced alignment tab character &")
 64→    if (!line.trimStart().startsWith("%") && !inTabularEnv) {
 65→      const ampMatches = line.matchAll(/(?<!\\)&/g);
 66→      for (const _match of ampMatches) {
 67→        issues.push({
 68→          chapter: chapterName,
 69→          severity: "warning",
 70→          message: `Bare '&' character — should be '\\&' outside tabular environments`,
 71→          line: lineNum,
 72→        });
 73→      }
 74→    }
 75→
 76→    // Markdown heading artifacts: lines starting with # (markdown heading)
 77→    if (/^#{1,6}\s+/.test(line.trimStart())) {
 78→      issues.push({
 79→        chapter: chapterName,
 80→        severity: "error",
 81→        message: `Markdown heading detected ('${line.trim().slice(0, 40)}') — not valid LaTeX`,
 82→        line: lineNum,
 83→      });
 84→    }
 85→
 86→    // Markdown horizontal rules: lines that are just --- or *** or ___
 87→    if (/^\s*[-*_]{3,}\s*$/.test(line) && !line.includes("BIBTEX")) {
 88→      issues.push({
 89→        chapter: chapterName,
 90→        severity: "warning",
 91→        message: `Possible markdown separator '${line.trim()}'`,
 92→        line: lineNum,
 93→      });
 94→    }
 95→
 96→    // Bare URLs (should be \url{} or \href{})
 97→    if (/(?<!\\url\{)(?<!\\href\{)https?:\/\/\S+/.test(line) && !line.trimStart().startsWith("%")) {
 98→      issues.push({
 99→        chapter: chapterName,
100→        severity: "warning",
101→        message: `Bare URL detected — wrap in \\url{} or \\href{}`,
102→        line: lineNum,
103→      });
104→    }
105→  }
106→
107→  // Unbalanced braces
108→  let braceDepth = 0;
109→  let inComment = false;
110→  for (let i = 0; i < latex.length; i++) {
111→    const ch = latex[i];
112→    if (ch === "%" && (i === 0 || latex[i - 1] !== "\\")) {
113→      inComment = true;
114→      continue;
115→    }
116→    if (ch === "\n") {
117→      inComment = false;
118→      continue;
119→    }
120→    if (inComment) continue;
121→
122→    if (ch === "{" && (i === 0 || latex[i - 1] !== "\\")) {
123→      braceDepth++;
124→    } else if (ch === "}" && (i === 0 || latex[i - 1] !== "\\")) {
125→      braceDepth--;
126→    }
127→  }
128→
129→  if (braceDepth !== 0) {
130→    issues.push({
131→      chapter: chapterName,
132→      severity: "error",
133→      message: `Unbalanced braces: ${braceDepth > 0 ? `${braceDepth} unclosed '{'` : `${Math.abs(braceDepth)} extra '}'`}`,
134→    });
135→  }
136→
137→  // Unbalanced \begin{}/\end{} environments
138→  const beginMatches = [...latex.matchAll(/\\begin\{(\w+)\}/g)];
139→  const endMatches = [...latex.matchAll(/\\end\{(\w+)\}/g)];
140→  const envStack: string[] = [];
141→  const allEnvTokens: { type: "begin" | "end"; name: string; pos: number }[] = [];
142→
143→  for (const m of beginMatches) {
144→    allEnvTokens.push({ type: "begin", name: m[1], pos: m.index! });
145→  }
146→  for (const m of endMatches) {
147→    allEnvTokens.push({ type: "end", name: m[1], pos: m.index! });
148→  }
149→  allEnvTokens.sort((a, b) => a.pos - b.pos);
150→
151→  for (const token of allEnvTokens) {
152→    if (token.type === "begin") {
153→      envStack.push(token.name);
154→    } else {
155→      if (envStack.length === 0 || envStack[envStack.length - 1] !== token.name) {
156→        issues.push({
157→          chapter: chapterName,
158→          severity: "error",
159→          message: `Unmatched \\end{${token.name}} — no corresponding \\begin{${token.name}}`,
160→        });
161→      } else {
162→        envStack.pop();
163→      }
164→    }
165→  }
166→
167→  for (const env of envStack) {
168→    issues.push({
169→      chapter: chapterName,
170→      severity: "error",
171→      message: `Unclosed environment: \\begin{${env}} has no \\end{${env}}`,
172→    });
173→  }
174→
175→  return issues;
176→}
177→
178→// ── AI validation via Haiku (non-blocking) ──────────────────────────────────
179→
180→/**
181→ * Send chapter LaTeX to Claude Haiku for deeper validation.
182→ * Returns warnings (never blocks). On failure/timeout, returns empty array.
183→ */
184→export async function aiValidateChapters(
185→  chapters: Record<string, string>
186→): Promise<ValidationIssue[]> {
187→  const nonEmpty = Object.entries(chapters).filter(([, content]) => content.trim());
188→  if (nonEmpty.length === 0) return [];
189→
190→  const chapterSnippets = nonEmpty
191→    .map(([name, content]) => {
192→      // Truncate to ~2000 chars per chapter to keep prompt reasonable
193→      const truncated = content.length > 2000 ? content.slice(0, 2000) + "\n... [truncated]" : content;
194→      return `=== ${name} ===\n${truncated}`;
195→    })
196→    .join("\n\n");
197→
198→  try {
199→    const client = getAnthropicClient();
200→    const controller = new AbortController();
201→    const timeout = setTimeout(() => controller.abort(), 15_000);
202→
203→    const response = await client.messages.create(
204→      {
205→        model: "claude-haiku-4-5-20251001",
206→        max_tokens: 1024,
207→        system:
208→          "You are a LaTeX syntax validator for medical theses. Check for formatting errors, structural issues, and LaTeX-invalid constructs. Return a JSON array of issues. Each issue: {\"chapter\": \"filename\", \"severity\": \"warning\", \"message\": \"description\"}. If no issues found, return []. Return ONLY the JSON array, no other text.",
209→        messages: [
210→          {
211→            role: "user",
212→            content: `Validate the following LaTeX chapter content for syntax issues:\n\n${chapterSnippets}`,
213→          },
214→        ],
215→      },
216→      { signal: controller.signal }
217→    );
218→
219→    clearTimeout(timeout);
220→
221→    const text =
222→      response.content[0].type === "text" ? response.content[0].text : "";
223→
224→    // Parse JSON response
225→    const jsonMatch = text.match(/\[[\s\S]*\]/);
226→    if (!jsonMatch) return [];
227→
228→    const parsed: unknown = JSON.parse(jsonMatch[0]);
229→    if (!Array.isArray(parsed)) return [];
230→
231→    return parsed
232→      .filter(
233→        (item: unknown): item is { chapter: string; severity: string; message: string } =>
234→          typeof item === "object" &&
235→          item !== null &&
236→          "chapter" in item &&
237→          "message" in item
238→      )
239→      .map((item) => ({
240→        chapter: String(item.chapter),
241→        severity: "warning" as const,
242→        message: String(item.message),
243→      }));
244→  } catch (err) {
245→    console.warn("AI validation failed (non-blocking):", err instanceof Error ? err.message : err);
246→    return [];
247→  }
248→}
