  1→/**
  2→ * Pre-seed real references from PubMed for AI context.
  3→ *
  4→ * Builds PubMed search queries from thesis metadata, fetches articles,
  5→ * converts to BibTeX, and formats for inclusion in AI prompts.
  6→ */
  7→
  8→import { searchPubMed, pubmedArticleToBibtex } from "./pubmed";
  9→import type { PubMedArticle } from "./pubmed";
 10→
 11→const TIME_BUDGET_MS = 8_000;
 12→
 13→// ── Query builders ─────────────────────────────────────────────────────────
 14→
 15→/**
 16→ * Build 2-3 PubMed queries of decreasing specificity from thesis metadata.
 17→ */
 18→export function buildSearchQueries(
 19→  title: string,
 20→  keywords: string[],
 21→  studyType: string | null,
 22→  department: string | null
 23→): string[] {
 24→  const queries: string[] = [];
 25→
 26→  // Most specific: title keywords + study type
 27→  const titleWords = title
 28→    .replace(/[^a-zA-Z0-9\s]/g, "")
 29→    .split(/\s+/)
 30→    .filter((w) => w.length > 3)
 31→    .slice(0, 5)
 32→    .join(" ");
 33→
 34→  if (titleWords && studyType) {
 35→    queries.push(`${titleWords} ${studyType}`);
 36→  } else if (titleWords) {
 37→    queries.push(titleWords);
 38→  }
 39→
 40→  // Medium: keywords combined
 41→  if (keywords.length > 0) {
 42→    queries.push(keywords.slice(0, 4).join(" "));
 43→  }
 44→
 45→  // Broad: department + first 2 keywords
 46→  if (department && keywords.length >= 2) {
 47→    queries.push(`${department} ${keywords.slice(0, 2).join(" ")}`);
 48→  }
 49→
 50→  return queries;
 51→}
 52→
 53→// ── Main function ──────────────────────────────────────────────────────────
 54→
 55→export interface PreSeededReference {
 56→  pmid: string;
 57→  citeKey: string;
 58→  bibtex: string;
 59→  article: PubMedArticle;
 60→}
 61→
 62→/**
 63→ * Search PubMed with multiple queries, deduplicate, and convert to BibTeX.
 64→ * Respects an 8-second time budget.
 65→ */
 66→export async function preSeedReferences(
 67→  title: string,
 68→  keywords: string[],
 69→  studyType: string | null,
 70→  department: string | null,
 71→  maxRefs = 20
 72→): Promise<PreSeededReference[]> {
 73→  const queries = buildSearchQueries(title, keywords, studyType, department);
 74→  if (queries.length === 0) return [];
 75→
 76→  const deadline = Date.now() + TIME_BUDGET_MS;
 77→  const seenPmids = new Set<string>();
 78→  const articles: PubMedArticle[] = [];
 79→
 80→  // Run queries in parallel
 81→  const perQueryLimit = Math.ceil(maxRefs / queries.length) + 5;
 82→  const results = await Promise.allSettled(
 83→    queries.map((q) => searchPubMed(q, perQueryLimit))
 84→  );
 85→
 86→  for (const result of results) {
 87→    if (result.status !== "fulfilled") continue;
 88→    for (const article of result.value.items) {
 89→      if (!seenPmids.has(article.pmid)) {
 90→        seenPmids.add(article.pmid);
 91→        articles.push(article);
 92→      }
 93→    }
 94→  }
 95→
 96→  // Limit to maxRefs
 97→  const selected = articles.slice(0, maxRefs);
 98→
 99→  // Convert to BibTeX with cite keys derived from first author + year
100→  const refs: PreSeededReference[] = [];
101→  for (const article of selected) {
102→    if (Date.now() > deadline) break;
103→
104→    const citeKey = generateCiteKey(article, refs);
105→    try {
106→      const bibtex = await pubmedArticleToBibtex(article, citeKey);
107→      refs.push({ pmid: article.pmid, citeKey, bibtex, article });
108→    } catch {
109→      // Skip failed conversions
110→    }
111→  }
112→
113→  return refs;
114→}
115→
116→/**
117→ * Generate a unique cite key from article metadata (e.g., "kumar2019").
118→ */
119→function generateCiteKey(
120→  article: PubMedArticle,
121→  existing: PreSeededReference[]
122→): string {
123→  const firstAuthor = article.authors[0] ?? "unknown";
124→  const surname = firstAuthor
125→    .split(/[\s,]+/)[0]
126→    ?.toLowerCase()
127→    .replace(/[^a-z]/g, "") ?? "unknown";
128→  const year = article.year ?? 2020;
129→  const base = `${surname}${year}`;
130→
131→  const usedKeys = new Set(existing.map((r) => r.citeKey));
132→  if (!usedKeys.has(base)) return base;
133→
134→  // Append a/b/c suffix for duplicates
135→  for (const suffix of "REDACTED") {
136→    const candidate = `${base}${suffix}`;
137→    if (!usedKeys.has(candidate)) return candidate;
138→  }
139→
140→  return `${base}_${article.pmid}`;
141→}
142→
143→// ── Prompt formatting ──────────────────────────────────────────────────────
144→
145→/**
146→ * Format pre-seeded references for inclusion in an AI prompt.
147→ */
148→export function formatReferencesForPrompt(refs: PreSeededReference[]): string {
149→  if (refs.length === 0) return "";
150→
151→  const lines = refs.map(
152→    (r) =>
153→      `\\cite{${r.citeKey}} — ${r.article.authors.slice(0, 3).join(", ")}${
154→        r.article.authors.length > 3 ? " et al." : ""
155→      } (${r.article.year ?? "n.d."}). ${r.article.title}`
156→  );
157→
158→  const bibtexBlock = refs.map((r) => r.bibtex).join("\n\n");
159→
160→  return `
161→
162→--- AVAILABLE REFERENCES ---
163→Use these verified references where appropriate. You may also generate additional references, but prefer these.
164→
165→${lines.join("\n")}
166→
167→--- PRE-SEEDED BIBTEX ---
168→Include these entries in your ---BIBTEX--- section if you use the corresponding \\cite{key}.
169→
170→${bibtexBlock}
171→`;
172→}
