  1→import { escapeLatexArg } from "./escape";
  2→import type { Project, ProjectMetadata } from "@/lib/types/database";
  3→
  4→/**
  5→ * Generate front matter LaTeX from project metadata.
  6→ * This populates the metadata commands that the CLS files use
  7→ * to generate title page, certificates, and declaration.
  8→ */
  9→export function generateFrontMatterLatex(project: Project): string {
 10→  const meta = project.metadata_json ?? {};
 11→  const lines: string[] = [];
 12→
 13→  lines.push("%% Front matter metadata -- auto-generated from project settings");
 14→  lines.push(`%% Project: ${project.id}`);
 15→  lines.push(`%% Generated: ${new Date().toISOString()}`);
 16→  lines.push("");
 17→
 18→  // Title
 19→  if (project.title) {
 20→    lines.push(`\\thesistitle{${escapeLatexArg(project.title)}}`);
 21→  }
 22→
 23→  // Candidate
 24→  if (meta.candidate_name) {
 25→    lines.push(`\\candidatename{${escapeLatexArg(meta.candidate_name)}}`);
 26→  }
 27→  lines.push("\\candidatedesignation{Post Graduate Trainee}");
 28→
 29→  // Registration
 30→  if (meta.registration_no) {
 31→    lines.push(`\\registrationno{${escapeLatexArg(meta.registration_no)}}`);
 32→  }
 33→
 34→  // Session
 35→  if (meta.session) {
 36→    lines.push(`\\academicsession{${escapeLatexArg(meta.session)}}`);
 37→    lines.push(`\\studyperiod{${escapeLatexArg(meta.session)}}`);
 38→  }
 39→
 40→  // Guide
 41→  if (meta.guide_name) {
 42→    lines.push(`\\supervisorname{${escapeLatexArg(meta.guide_name)}}`);
 43→    lines.push("\\supervisordesignation{Professor}");
 44→  }
 45→
 46→  // HOD
 47→  if (meta.hod_name) {
 48→    lines.push(`\\hodname{${escapeLatexArg(meta.hod_name)}}`);
 49→    lines.push("\\hoddesignation{Professor and Head of Department}");
 50→  }
 51→
 52→  // Department
 53→  if (meta.department) {
 54→    lines.push(`\\departmentname{${escapeLatexArg(meta.department)}}`);
 55→  }
 56→
 57→  // Degree
 58→  if (meta.degree) {
 59→    lines.push(`\\degreename{${escapeLatexArg(meta.degree)}}`);
 60→  }
 61→  if (meta.speciality) {
 62→    lines.push(`\\degreebranch{${escapeLatexArg(meta.speciality)}}`);
 63→  }
 64→
 65→  // Year
 66→  if (meta.year) {
 67→    lines.push(`\\submissionyear{${escapeLatexArg(meta.year)}}`);
 68→  }
 69→
 70→  return lines.join("\n");
 71→}
 72→
 73→/**
 74→ * Generate acknowledgements LaTeX text from metadata.
 75→ * Follows the standard order: Guide → HOD → Head of Institution → Colleagues → Family
 76→ */
 77→export function generateAcknowledgements(meta: ProjectMetadata): string {
 78→  const parts: string[] = [];
 79→
 80→  if (meta.guide_name) {
 81→    parts.push(
 82→      `I express my sincere gratitude to my guide, ${escapeLatexArg(meta.guide_name)}, for their invaluable guidance, constant encouragement, and meticulous supervision throughout this study.`
 83→    );
 84→  }
 85→
 86→  if (meta.hod_name) {
 87→    parts.push(
 88→      `I am deeply indebted to ${escapeLatexArg(meta.hod_name)}, Head of the Department${meta.department ? `, ${escapeLatexArg(meta.department)}` : ""}, for providing the necessary facilities and support to carry out this work.`
 89→    );
 90→  }
 91→
 92→  parts.push(
 93→    "I am thankful to all the faculty members and colleagues of the department for their help and cooperation during the course of this study."
 94→  );
 95→
 96→  parts.push(
 97→    "I sincerely thank all the patients who participated in this study for their cooperation and trust."
 98→  );
 99→
100→  parts.push(
101→    "Last but not least, I am eternally grateful to my family for their unwavering support, patience, and encouragement."
102→  );
103→
104→  return parts.join("\n\n");
105→}
