  1→import { NextRequest } from "next/server";
  2→import { getAuthenticatedUser } from "@/lib/api/auth";
  3→import {
  4→  unauthorised,
  5→  notFound,
  6→  badRequest,
  7→  internalError,
  8→  rateLimited,
  9→} from "@/lib/api/errors";
 10→import { checkRateLimit } from "@/lib/ai/rate-limit";
 11→import { createAdminSupabaseClient } from "@/lib/supabase/admin";
 12→import { isValidPhase } from "@/lib/phases/transitions";
 13→import { getAnthropicClient } from "@/lib/ai/client";
 14→import { REFINE_SYSTEM_PROMPT } from "@/lib/ai/prompts";
 15→import { extractCiteKeys } from "@/lib/citations/extract-keys";
 16→import { resolveSectionCitations } from "@/lib/citations/auto-resolve";
 17→import { checkTokenBudget, recordTokenUsage } from "@/lib/ai/token-budget";
 18→import { checkLicenceForPhase } from "@/lib/api/licence-phase-gate";
 19→import type { Section } from "@/lib/types/database";
 20→import { NextResponse } from "next/server";
 21→
 22→export async function POST(
 23→  request: NextRequest,
 24→  { params }: { params: Promise<{ id: string; phase: string }> }
 25→) {
 26→  try {
 27→    const authResult = await getAuthenticatedUser();
 28→    if (!authResult) return unauthorised();
 29→
 30→    // Rate limit check
 31→    const rateCheck = await checkRateLimit(authResult.user.id);
 32→    if (!rateCheck.allowed) {
 33→      return rateLimited(rateCheck.retryAfterSeconds);
 34→    }
 35→
 36→    const { id, phase } = await params;
 37→    const phaseNumber = parseInt(phase, 10);
 38→
 39→    if (!isValidPhase(phaseNumber)) {
 40→      return badRequest("Invalid phase number");
 41→    }
 42→
 43→    const body = await request.json();
 44→    const instructions = (body as { instructions?: string }).instructions;
 45→    if (!instructions || typeof instructions !== "string" || instructions.trim().length === 0) {
 46→      return badRequest("Instructions are required");
 47→    }
 48→
 49→    // Licence phase gate
 50→    const gateResult = await checkLicenceForPhase(id, authResult.user.id, phaseNumber, "refine");
 51→    if (gateResult instanceof NextResponse) return gateResult;
 52→
 53→    const supabase = createAdminSupabaseClient();
 54→
 55→    // Fetch current section content
 56→    const { data: section, error: sectionError } = await supabase
 57→      .from("sections")
 58→      .select("*")
 59→      .eq("project_id", id)
 60→      .eq("phase_number", phaseNumber)
 61→      .single();
 62→
 63→    if (sectionError || !section) {
 64→      return notFound("Section not found — generate content first");
 65→    }
 66→
 67→    const typedSection = section as Section;
 68→    const currentLatex =
 69→      typedSection.ai_generated_latex || typedSection.latex_content;
 70→
 71→    if (!currentLatex) {
 72→      return badRequest("Section has no content to refine --- generate first");
 73→    }
 74→
 75→    // Token budget check
 76→    const budgetCheck = await checkTokenBudget(id, phaseNumber);
 77→    if (!budgetCheck.allowed) {
 78→      return badRequest(budgetCheck.reason ?? "Token budget exhausted");
 79→    }
 80→
 81→    // Don't change status to "generating" --- the client consumes the stream
 82→    // and refreshes when done. Keeping current status avoids a broken UI state
 83→    // if the page is refreshed mid-stream.
 84→    let streamCompleted = false;
 85→
 86→    const encoder = new TextEncoder();
 87→    const stream = new ReadableStream({
 88→      async start(controller) {
 89→        try {
 90→          const client = getAnthropicClient();
 91→          let fullResponse = "";
 92→
 93→          const userMessage = `Here is the current chapter content:\n\n${currentLatex}\n\n---STUDENT INSTRUCTIONS---\n${instructions.trim()}`;
 94→
 95→          const messageStream = client.messages.stream({
 96→            model: "claude-sonnet-4-5-20250929",
 97→            max_tokens: 10000,
 98→            system: [
 99→              {
100→                type: "text" as const,
101→                text: REFINE_SYSTEM_PROMPT,
102→                cache_control: { type: "ephemeral" as const },
103→              },
104→            ],
105→            messages: [{ role: "user", content: userMessage }],
106→          });
107→
108→          messageStream.on("text", (text) => {
109→            fullResponse += text;
110→            controller.enqueue(
111→              encoder.encode(
112→                `data: ${JSON.stringify({ type: "delta", text })}\n\n`
113→              )
114→            );
115→          });
116→
117→          const finalMsg = await messageStream.finalMessage();
118→
119→          // Record token usage
120→          const inputTokens = finalMsg.usage?.input_tokens ?? 0;
121→          const outputTokens = finalMsg.usage?.output_tokens ?? 0;
122→          void recordTokenUsage(id, phaseNumber, inputTokens, outputTokens, "claude-sonnet-4-5-20250929").catch(console.error);
123→
124→          // Extract citation keys directly from LaTeX (no round-trip)
125→          const citationKeys = extractCiteKeys(fullResponse);
126→
127→          // Word count
128→          const plainText = fullResponse
129→            .replace(/\\[a-zA-Z]+\{[^}]*\}/g, " ")
130→            .replace(/\\[a-zA-Z]+/g, " ")
131→            .replace(/[{}\\]/g, " ");
132→          const wordCount = plainText.split(/\s+/).filter(Boolean).length;
133→
134→          // If section was previously approved, roll back phases_completed
135→          if (typedSection.status === "approved") {
136→            const { data: proj } = await supabase
137→              .from("projects")
138→              .select("phases_completed, current_phase")
139→              .eq("id", id)
140→              .single();
141→
142→            if (proj) {
143→              const updatedPhases = ((proj.phases_completed ?? []) as number[]).filter(
144→                (p) => p !== phaseNumber
145→              );
146→              await supabase
147→                .from("projects")
148→                .update({
149→                  phases_completed: updatedPhases,
150→                  current_phase: phaseNumber,
151→                  updated_at: new Date().toISOString(),
152→                })
153→                .eq("id", id);
154→            }
155→          }
156→
157→          // Update section (LaTeX is canonical --- no rich_content_json)
158→          const { error: updateError } = await supabase
159→            .from("sections")
160→            .update({
161→              latex_content: fullResponse,
162→              rich_content_json: null,
163→              ai_generated_latex: fullResponse,
164→              word_count: wordCount,
165→              citation_keys: citationKeys,
166→              status: "review",
167→              updated_at: new Date().toISOString(),
168→            })
169→            .eq("project_id", id)
170→            .eq("phase_number", phaseNumber);
171→
172→          if (updateError) {
173→            await supabase
174→              .from("sections")
175→              .update({
176→                latex_content: fullResponse,
177→                rich_content_json: null,
178→                word_count: wordCount,
179→                citation_keys: citationKeys,
180→                status: "review",
181→                updated_at: new Date().toISOString(),
182→              })
183→              .eq("project_id", id)
184→              .eq("phase_number", phaseNumber);
185→          }
186→
187→          streamCompleted = true;
188→
189→          // Resolve new citations (non-blocking with 10s timeout)
190→          let citationSummary = null;
191→          try {
192→            citationSummary = await Promise.race([
193→              resolveSectionCitations(id, fullResponse),
194→              new Promise<null>((resolve) =>
195→                setTimeout(() => resolve(null), 10_000)
196→              ),
197→            ]);
198→          } catch {
199→            // Non-critical
200→          }
201→
202→          controller.enqueue(
203→            encoder.encode(
204→              `data: ${JSON.stringify({
205→                type: "complete",
206→                wordCount,
207→                citationKeys,
208→                citationSummary,
209→              })}\n\n`
210→            )
211→          );
212→          controller.enqueue(encoder.encode("data: [DONE]\n\n"));
213→          controller.close();
214→        } catch (err) {
215→          streamCompleted = true;
216→
217→          const errorMessage =
218→            err instanceof Error ? err.message : "Refinement failed";
219→          controller.enqueue(
220→            encoder.encode(
221→              `data: ${JSON.stringify({ type: "error", message: errorMessage })}\n\n`
222→            )
223→          );
224→          controller.close();
225→        }
226→      },
227→      cancel: async () => {
228→        // No status reset needed — we never set "generating"
229→      },
230→    });
231→
232→    return new Response(stream, {
233→      headers: {
234→        "Content-Type": "text/event-stream",
235→        "Cache-Control": "no-cache",
236→        Connection: "keep-alive",
237→      },
238→    });
239→  } catch (err) {
240→    console.error(
241→      "Unexpected error in POST /api/projects/[id]/sections/[phase]/refine:",
242→      err
243→    );
244→    return internalError();
245→  }
246→}
