  1→import { NextRequest, NextResponse } from "next/server";
  2→import { getAuthenticatedUser } from "@/lib/api/auth";
  3→import { unauthorised, notFound, internalError } from "@/lib/api/errors";
  4→import { createAdminSupabaseClient } from "@/lib/supabase/admin";
  5→
  6→export async function POST(
  7→  _request: NextRequest,
  8→  { params }: { params: Promise<{ id: string }> }
  9→) {
 10→  try {
 11→    const authResult = await getAuthenticatedUser();
 12→    if (!authResult) return unauthorised();
 13→
 14→    const { id } = await params;
 15→    const supabase = createAdminSupabaseClient();
 16→
 17→    // Fetch project
 18→    const { data: project } = await supabase
 19→      .from("projects")
 20→      .select("id, status, license_id, current_phase, user_id")
 21→      .eq("id", id)
 22→      .eq("user_id", authResult.user.id)
 23→      .single();
 24→
 25→    if (!project) return notFound("Project not found");
 26→
 27→    // Must be licensed
 28→    if (project.status !== "licensed") {
 29→      return NextResponse.json(
 30→        {
 31→          error: {
 32→            code: "INVALID_STATE",
 33→            message: "Only licensed projects can be reset.",
 34→          },
 35→        },
 36→        { status: 400 }
 37→      );
 38→    }
 39→
 40→    // Must be before Phase 4
 41→    if ((project.current_phase as number) >= 4) {
 42→      return NextResponse.json(
 43→        {
 44→          error: {
 45→            code: "PHASE_TOO_ADVANCED",
 46→            message:
 47→              "Projects can only be reset before Phase 4 (Review of Literature). Your project is too far along.",
 48→          },
 49→        },
 50→        { status: 400 }
 51→      );
 52→    }
 53→
 54→    // Check reset count on licence
 55→    if (!project.license_id) {
 56→      return NextResponse.json(
 57→        {
 58→          error: {
 59→            code: "NO_LICENCE",
 60→            message: "No licence attached to this project.",
 61→          },
 62→        },
 63→        { status: 400 }
 64→      );
 65→    }
 66→
 67→    const { data: licence } = await supabase
 68→      .from("thesis_licenses")
 69→      .select("id, reset_count")
 70→      .eq("id", project.license_id)
 71→      .single();
 72→
 73→    if (!licence) {
 74→      return NextResponse.json(
 75→        {
 76→          error: {
 77→            code: "LICENCE_NOT_FOUND",
 78→            message: "Licence not found.",
 79→          },
 80→        },
 81→        { status: 400 }
 82→      );
 83→    }
 84→
 85→    if ((licence.reset_count as number) >= 1) {
 86→      return NextResponse.json(
 87→        {
 88→          error: {
 89→            code: "RESET_EXHAUSTED",
 90→            message: "You have already used your free reset for this licence.",
 91→          },
 92→        },
 93→        { status: 400 }
 94→      );
 95→    }
 96→
 97→    // Delete all project data
 98→    const deletions = await Promise.allSettled([
 99→      supabase.from("sections").delete().eq("project_id", id),
100→      supabase.from("citations").delete().eq("project_id", id),
101→      supabase.from("figures").delete().eq("project_id", id),
102→      supabase.from("analyses").delete().eq("project_id", id),
103→      supabase.from("compilations").delete().eq("project_id", id),
104→      supabase.from("ai_conversations").delete().eq("project_id", id),
105→      supabase.from("datasets").delete().eq("project_id", id),
106→      supabase.from("abbreviations").delete().eq("project_id", id),
107→      supabase.from("compliance_checks").delete().eq("project_id", id),
108→    ]);
109→
110→    const failures = deletions.filter((d) => d.status === "rejected");
111→    if (failures.length > 0) {
112→      console.error("Some reset deletions failed:", failures);
113→    }
114→
115→    // Reset project to phase 0
116→    await supabase
117→      .from("projects")
118→      .update({
119→        current_phase: 0,
120→        phases_completed: [],
121→        synopsis_text: null,
122→        study_type: null,
123→        metadata_json: {},
124→        updated_at: new Date().toISOString(),
125→      })
126→      .eq("id", id);
127→
128→    // Increment reset count on licence
129→    await supabase
130→      .from("thesis_licenses")
131→      .update({ reset_count: (licence.reset_count as number) + 1 })
132→      .eq("id", licence.id);
133→
134→    return NextResponse.json({
135→      data: { message: "Project reset to Phase 0 successfully." },
136→    });
137→  } catch (err) {
138→    console.error("Unexpected error in POST /api/projects/[id]/reset:", err);
139→    return internalError();
140→  }
141→}
