 1→import type { Project } from "@/lib/types/database";
 2→import { getPhase, MAX_PHASE } from "./constants";
 3→
 4→export interface TransitionCheck {
 5→  allowed: boolean;
 6→  reason?: string;
 7→  code?: "LICENCE_REQUIRED" | "INVALID_TRANSITION";
 8→}
 9→
10→/**
11→ * Check whether a project can advance from its current phase to the next.
12→ *
13→ * Section status validation is handled by the approve route itself (lines 76-93)
14→ * before calling this function, so we only check phase bounds + licence here.
15→ */
16→export function canAdvancePhase(
17→  project: Project,
18→): TransitionCheck {
19→  const { current_phase, status } = project;
20→
21→  // Cannot go beyond the final phase (but final phase itself CAN be approved)
22→  if (current_phase > MAX_PHASE) {
23→    return {
24→      allowed: false,
25→      reason: "Already beyond the final phase",
26→      code: "INVALID_TRANSITION",
27→    };
28→  }
29→
30→  // Final phase — no next phase to check licence for, just allow completion
31→  if (current_phase === MAX_PHASE) {
32→    return { allowed: true };
33→  }
34→
35→  // Check if next phase requires a licence
36→  const nextPhase = getPhase(current_phase + 1);
37→  const devBypass = process.env.NODE_ENV !== "production" && process.env.DEV_LICENCE_BYPASS === "true";
38→  if (
39→    nextPhase?.requiresLicence &&
40→    status !== "licensed" &&
41→    status !== "completed" &&
42→    !devBypass
43→  ) {
44→    return {
45→      allowed: false,
46→      reason: "Active licence required to advance beyond Phase 2",
47→      code: "LICENCE_REQUIRED",
48→    };
49→  }
50→
51→  return { allowed: true };
52→}
53→
54→/**
55→ * Check if a phase number is valid.
56→ */
57→export function isValidPhase(phase: number): boolean {
58→  return Number.isInteger(phase) && phase >= 0 && phase <= MAX_PHASE;
59→}
