  1→/**
  2→ * Inngest background function for AI thesis section generation.
  3→ *
  4→ * Replaces SSE streaming: generation runs as a durable background job.
  5→ * If the student stays on the page, they see live text via Supabase Realtime.
  6→ * If they close the tab, generation continues to completion.
  7→ */
  8→
  9→import { inngest } from "../client";
 10→import { getAnthropicClient } from "@/lib/ai/client";
 11→import { extractCiteKeys } from "@/lib/citations/extract-keys";
 12→import { resolveSectionCitations } from "@/lib/citations/auto-resolve";
 13→import { recordTokenUsage } from "@/lib/ai/token-budget";
 14→import { checkBibtexIntegrity, requestMissingBibtexEntries } from "@/lib/ai/bibtex-completion";
 15→import { splitBibtex } from "@/lib/latex/assemble";
 16→import { createAdminSupabaseClient } from "@/lib/supabase/admin";
 17→
 18→export const aiGenerateFn = inngest.createFunction(
 19→  {
 20→    id: "ai-generate-section",
 21→    retries: 1,
 22→    concurrency: [{ limit: 5 }],
 23→  },
 24→  { event: "thesis/section.generate" },
 25→  async ({ event, step }) => {
 26→    const {
 27→      projectId,
 28→      phaseNumber,
 29→      systemPrompt,
 30→      userMessage,
 31→      model,
 32→      maxTokens,
 33→    } = event.data as {
 34→      projectId: string;
 35→      phaseNumber: number;
 36→      systemPrompt: string;
 37→      userMessage: string;
 38→      model: string;
 39→      maxTokens: number;
 40→    };
 41→
 42→    const supabase = createAdminSupabaseClient();
 43→
 44→    // Step 1: Stream AI response, writing chunks to streaming_content
 45→    const result = await step.run("stream-ai-response", async () => {
 46→      const client = getAnthropicClient();
 47→      let fullResponse = "";
 48→
 49→      const messageStream = client.messages.stream({
 50→        model,
 51→        max_tokens: maxTokens,
 52→        system: [
 53→          {
 54→            type: "text" as const,
 55→            text: systemPrompt,
 56→            cache_control: { type: "ephemeral" as const },
 57→          },
 58→        ],
 59→        messages: [{ role: "user", content: userMessage }],
 60→      });
 61→
 62→      // Batch Realtime updates every ~500 chars to avoid excessive DB writes
 63→      let lastFlushed = 0;
 64→      const FLUSH_INTERVAL = 500;
 65→
 66→      messageStream.on("text", (text) => {
 67→        fullResponse += text;
 68→        if (fullResponse.length - lastFlushed >= FLUSH_INTERVAL) {
 69→          // Fire-and-forget partial update for Realtime subscribers
 70→          void supabase
 71→            .from("sections")
 72→            .update({ streaming_content: fullResponse })
 73→            .eq("project_id", projectId)
 74→            .eq("phase_number", phaseNumber)
 75→            .then(() => {});
 76→          lastFlushed = fullResponse.length;
 77→        }
 78→      });
 79→
 80→      const finalMessage = await messageStream.finalMessage();
 81→
 82→      return {
 83→        fullResponse,
 84→        inputTokens: finalMessage.usage?.input_tokens ?? 0,
 85→        outputTokens: finalMessage.usage?.output_tokens ?? 0,
 86→      };
 87→    });
 88→
 89→    // Step 2: BibTeX integrity check + completion
 90→    const finalContent = await step.run("bibtex-integrity", async () => {
 91→      let response = result.fullResponse;
 92→      const integrity = checkBibtexIntegrity(response);
 93→
 94→      if (!integrity.complete && integrity.missingKeys.length > 0) {
 95→        console.warn(
 96→          `BibTeX trailer incomplete: ${integrity.missingKeys.length} missing entries. Requesting...`
 97→        );
 98→        try {
 99→          const missingBibtex = await requestMissingBibtexEntries(
100→            integrity.missingKeys,
101→            splitBibtex(response).body,
102→            model,
103→          );
104→          if (missingBibtex.trim()) {
105→            const { body, bib } = splitBibtex(response);
106→            response = bib.trim()
107→              ? `${body}\n\n---BIBTEX---\n${bib}\n\n${missingBibtex}`
108→              : `${body}\n\n---BIBTEX---\n${missingBibtex}`;
109→          }
110→        } catch (err) {
111→          console.error("Failed to request missing BibTeX entries:", err);
112→        }
113→      }
114→
115→      return response;
116→    });
117→
118→    // Step 3: Save final content
119→    await step.run("save-content", async () => {
120→      const citationKeys = extractCiteKeys(finalContent);
121→      const plainText = finalContent
122→        .replace(/\\[a-zA-Z]+\{[^}]*\}/g, " ")
123→        .replace(/\\[a-zA-Z]+/g, " ")
124→        .replace(/[{}\\]/g, " ");
125→      const wordCount = plainText.split(/\s+/).filter(Boolean).length;
126→
127→      await supabase
128→        .from("sections")
129→        .update({
130→          latex_content: finalContent,
131→          ai_generated_latex: finalContent,
132→          rich_content_json: null,
133→          streaming_content: "",
134→          word_count: wordCount,
135→          citation_keys: citationKeys,
136→          status: "review",
137→          updated_at: new Date().toISOString(),
138→        })
139→        .eq("project_id", projectId)
140→        .eq("phase_number", phaseNumber);
141→    });
142→
143→    // Step 4: Record token usage
144→    await step.run("record-tokens", async () => {
145→      await recordTokenUsage(
146→        projectId,
147→        phaseNumber,
148→        result.inputTokens,
149→        result.outputTokens,
150→        model,
151→      );
152→    });
153→
154→    // Step 5: Resolve citations (non-blocking)
155→    await step.run("resolve-citations", async () => {
156→      const timeoutMs = phaseNumber === 4 ? 45_000 : 15_000;
157→      try {
158→        await Promise.race([
159→          resolveSectionCitations(projectId, finalContent),
160→          new Promise<null>((r) => setTimeout(() => r(null), timeoutMs)),
161→        ]);
162→      } catch (err) {
163→        console.error("Citation resolution failed:", err);
164→      }
165→    });
166→
167→    return { success: true };
168→  },
169→);
