  1→/**
  2→ * Token budget tracking per phase and per thesis.
  3→ *
  4→ * Limits (from PLAN.md):
  5→ * - Max 100K output tokens per phase
  6→ * - Max 1.2M total tokens per thesis
  7→ * - Hard-stop at budget (no partial generation)
  8→ *
  9→ * Uses Supabase `ai_conversations` table for persistent tracking.
 10→ */
 11→
 12→import { createAdminSupabaseClient } from "@/lib/supabase/admin";
 13→
 14→const MAX_OUTPUT_TOKENS_PER_PHASE = 100_000;
 15→const MAX_TOTAL_TOKENS_PER_THESIS = 1_200_000;
 16→
 17→export interface TokenBudgetResult {
 18→  allowed: boolean;
 19→  phaseTokensUsed: number;
 20→  totalTokensUsed: number;
 21→  phaseRemaining: number;
 22→  totalRemaining: number;
 23→  reason?: string;
 24→}
 25→
 26→/**
 27→ * Check if a project has budget remaining for the given phase.
 28→ * Phase budget checks output_tokens only; thesis budget checks total.
 29→ */
 30→export async function checkTokenBudget(
 31→  projectId: string,
 32→  phaseNumber: number
 33→): Promise<TokenBudgetResult> {
 34→  const supabase = createAdminSupabaseClient();
 35→
 36→  // Fetch all AI conversations for this project
 37→  const { data: conversations } = await supabase
 38→    .from("ai_conversations")
 39→    .select("phase_number, total_tokens, output_tokens")
 40→    .eq("project_id", projectId);
 41→
 42→  const records = conversations ?? [];
 43→
 44→  // Calculate totals
 45→  let totalTokensUsed = 0;
 46→  let phaseOutputTokensUsed = 0;
 47→
 48→  for (const conv of records) {
 49→    const total = (conv.total_tokens as number) ?? 0;
 50→    const output = (conv.output_tokens as number) ?? 0;
 51→    totalTokensUsed += total;
 52→    if ((conv.phase_number as number) === phaseNumber) {
 53→      phaseOutputTokensUsed += output;
 54→    }
 55→  }
 56→
 57→  const phaseRemaining = MAX_OUTPUT_TOKENS_PER_PHASE - phaseOutputTokensUsed;
 58→  const totalRemaining = MAX_TOTAL_TOKENS_PER_THESIS - totalTokensUsed;
 59→
 60→  if (phaseOutputTokensUsed >= MAX_OUTPUT_TOKENS_PER_PHASE) {
 61→    return {
 62→      allowed: false,
 63→      phaseTokensUsed: phaseOutputTokensUsed,
 64→      totalTokensUsed,
 65→      phaseRemaining: 0,
 66→      totalRemaining: Math.max(0, totalRemaining),
 67→      reason: `Phase ${phaseNumber} output token budget exhausted (${phaseOutputTokensUsed.toLocaleString()} / ${MAX_OUTPUT_TOKENS_PER_PHASE.toLocaleString()})`,
 68→    };
 69→  }
 70→
 71→  if (totalTokensUsed >= MAX_TOTAL_TOKENS_PER_THESIS) {
 72→    return {
 73→      allowed: false,
 74→      phaseTokensUsed: phaseOutputTokensUsed,
 75→      totalTokensUsed,
 76→      phaseRemaining: Math.max(0, phaseRemaining),
 77→      totalRemaining: 0,
 78→      reason: `Thesis token budget exhausted (${totalTokensUsed.toLocaleString()} / ${MAX_TOTAL_TOKENS_PER_THESIS.toLocaleString()})`,
 79→    };
 80→  }
 81→
 82→  return {
 83→    allowed: true,
 84→    phaseTokensUsed: phaseOutputTokensUsed,
 85→    totalTokensUsed,
 86→    phaseRemaining: Math.max(0, phaseRemaining),
 87→    totalRemaining: Math.max(0, totalRemaining),
 88→  };
 89→}
 90→
 91→/**
 92→ * Record token usage after a successful generation.
 93→ * Accepts separate input/output token counts.
 94→ * Returns the conversation ID for linking to the section.
 95→ */
 96→export async function recordTokenUsage(
 97→  projectId: string,
 98→  phaseNumber: number,
 99→  inputTokens: number,
100→  outputTokens: number,
101→  modelUsed: string
102→): Promise<string | null> {
103→  const supabase = createAdminSupabaseClient();
104→
105→  const { data } = await supabase
106→    .from("ai_conversations")
107→    .insert({
108→      project_id: projectId,
109→      phase_number: phaseNumber,
110→      messages_json: [],
111→      model_used: modelUsed,
112→      input_tokens: inputTokens,
113→      output_tokens: outputTokens,
114→      total_tokens: inputTokens + outputTokens,
115→    })
116→    .select("id")
117→    .single();
118→
119→  if (data?.id) {
120→    // Link the conversation to the section
121→    await supabase
122→      .from("sections")
123→      .update({ ai_conversation_id: data.id as string })
124→      .eq("project_id", projectId)
125→      .eq("phase_number", phaseNumber);
126→  }
127→
128→  return (data?.id as string) ?? null;
129→}
