54→    event.respondWith(
55→      caches.match(request).then((cached) => {
56→        if (cached) return cached;
57→        return fetch(request).then((response) => {
58→          const clone = response.clone();
59→          caches.open(CACHE_NAME).then((cache) => cache.put(request, clone));
60→          return response;
61→        });
62→      })
63→    );
64→    return;
65→  }
66→
67→  // Pages: network-first with cache fallback
68→  event.respondWith(
