 55→/**
 56→ * Parse an Excel (.xlsx) buffer into structured rows with auto-detected column types.
 57→ */
 58→export function parseExcel(buffer: Buffer): ParseResult {
 59→  const workbook = XLSX.read(buffer, { type: "buffer" });
 60→  const firstSheet = workbook.SheetNames[0];
 61→  if (!firstSheet) throw new Error("Excel file has no sheets");
 62→
 63→  const sheet = workbook.Sheets[firstSheet]!;
 64→  const jsonData = XLSX.utils.sheet_to_json<Record<string, unknown>>(sheet, {
 65→    defval: null,
 66→  });
 67→
 68→  if (jsonData.length === 0) throw new Error("Excel sheet is empty");
 69→
 70→  const headers = Object.keys(jsonData[0]!);
 71→  if (headers.length > MAX_COLUMNS) {
 72→    throw new Error(`Too many columns (${headers.length}). Maximum is ${MAX_COLUMNS}.`);
 73→  }
 74→
 75→  const rows = jsonData.slice(0, MAX_ROWS);
 76→
 77→  // Convert all values to strings for consistent type detection
 78→  const stringRows = rows.map((row) => {
 79→    const out: Record<string, string> = {};
 80→    for (const h of headers) {
 81→      const val = row[h];
 82→      out[h] = val === null || val === undefined ? "" : String(val);
 83→    }
 84→    return out;
 85→  });
 86→
 87→  const columns = detectColumnTypes(headers, stringRows);
 88→
 89→  return {
 90→    headers,
 91→    rows: rows as Record<string, unknown>[],
 92→    rowCount: jsonData.length,
 93→    columns,
 94→  };
 95→}
 96→
 97→/**
 98→ * Auto-detect column types from string data.
 99→ * - numeric: >80% of non-empty values parse as a finite number
100→ * - date: >80% of non-empty values match common date patterns
101→ * - categorical: everything else (treated as factors)
102→ */
103→function detectColumnTypes(
104→  headers: string[],
105→  rows: Record<string, string>[]
106→): ParsedColumn[] {
107→  return headers.map((name) => {
108→    const values = rows.map((r) => r[name] ?? "").filter((v) => v.trim() !== "");
109→    const total = values.length;
110→
111→    if (total === 0) return { name, type: "text" };
112→
113→    // Check numeric
114→    const numericCount = values.filter((v) => {
115→      const n = Number(v);
116→      return !isNaN(n) && isFinite(n);
117→    }).length;
118→
119→    if (numericCount / total > 0.8) return { name, type: "numeric" };
120→
121→    // Check date — ISO, DD/MM/YYYY, MM/DD/YYYY, YYYY-MM-DD
122→    const datePatterns = [
123→      /^\d{4}-\d{2}-\d{2}/, // ISO
124→      /^\d{1,2}\/\d{1,2}\/\d{2,4}$/, // DD/MM/YYYY or MM/DD/YYYY
125→      /^\d{1,2}-\d{1,2}-\d{2,4}$/, // DD-MM-YYYY
126→    ];
127→    const dateCount = values.filter((v) =>
128→      datePatterns.some((p) => p.test(v.trim()))
129→    ).length;
130→
131→    if (dateCount / total > 0.8) return { name, type: "date" };
132→
133→    return { name, type: "categorical" };
134→  });
135→}
