  const ch = latex[i];
  if (ch === "%" && (i === 0 || latex[i - 1] !== "\\")) {
    inComment = true;
    continue;
  }
  if (ch === "\n") {
    inComment = false;
    continue;
  }
  if (inComment) continue;

  if (ch === "{" && (i === 0 || latex[i - 1] !== "\\")) {
    braceDepth++;
  } else if (ch === "}" && (i === 0 || latex[i - 1] !== "\\")) {
    braceDepth--;
  }
}

if (braceDepth !== 0) {
