  1→/**
  2→ * Rate limiter for AI generation requests.
  3→ * Limits: 10 requests per hour per user.
  4→ *
  5→ * Uses Redis sorted set when available, falls back to in-memory.
  6→ */
  7→
  8→import { getRedis } from "@/lib/redis/client";
  9→
 10→const WINDOW_MS = 60 * 60 * 1000; // 1 hour
 11→const WINDOW_S = 3600;
 12→const MAX_REQUESTS = 10;
 13→const REDIS_KEY_PREFIX = "apollo:ratelimit:";
 14→
 15→export interface RateLimitResult {
 16→  allowed: boolean;
 17→  remaining: number;
 18→  retryAfterSeconds: number;
 19→}
 20→
 21→// ── In-memory fallback ──────────────────────────────────────────────────────
 22→
 23→interface RateLimitEntry {
 24→  timestamps: number[];
 25→}
 26→
 27→const store = new Map<string, RateLimitEntry>();
 28→
 29→function memCheckRateLimit(userId: string): RateLimitResult {
 30→  const now = Date.now();
 31→  const entry = store.get(userId) ?? { timestamps: [] };
 32→
 33→  entry.timestamps = entry.timestamps.filter((ts) => now - ts < WINDOW_MS);
 34→
 35→  if (entry.timestamps.length >= MAX_REQUESTS) {
 36→    const oldestInWindow = entry.timestamps[0];
 37→    const retryAfterMs = WINDOW_MS - (now - oldestInWindow);
 38→    return {
 39→      allowed: false,
 40→      remaining: 0,
 41→      retryAfterSeconds: Math.ceil(retryAfterMs / 1000),
 42→    };
 43→  }
 44→
 45→  entry.timestamps.push(now);
 46→  store.set(userId, entry);
 47→
 48→  return {
 49→    allowed: true,
 50→    remaining: MAX_REQUESTS - entry.timestamps.length,
 51→    retryAfterSeconds: 0,
 52→  };
 53→}
 54→
