  1→"use client";
  2→
  3→import { useRef, useMemo, useEffect, useState } from "react";
  4→import { Canvas, useFrame } from "@react-three/fiber";
  5→import {
  6→  Float,
  7→  Line,
  8→  Environment,
  9→  ContactShadows,
 10→  MeshTransmissionMaterial,
 11→  RoundedBox,
 12→} from "@react-three/drei";
 13→import * as THREE from "three";
 14→
 15→// ── Reduced motion detection ────────────────────────────────────────────────
 16→function useReducedMotion() {
 17→  const [reduced, setReduced] = useState(false);
 18→
 19→  useEffect(() => {
 20→    const mq = window.matchMedia("(prefers-reduced-motion: reduce)");
 21→    setReduced(mq.matches);
 22→    const handler = (e: MediaQueryListEvent) => setReduced(e.matches);
 23→    mq.addEventListener("change", handler);
 24→    return () => mq.removeEventListener("change", handler);
 25→  }, []);
 26→
 27→  return reduced;
 28→}
 29→
 30→// ── Canvas2D page texture generator ─────────────────────────────────────────
 31→// Page content per variant — title, subtitle, body paragraphs
 32→const PAGE_CONTENT: { title: string; subtitle: string; body: string[] }[] = [
 33→  {
 34→    title: "Introduction",
 35→    subtitle: "Background and Rationale",
 36→    body: [
 37→      "Chronic kidney disease represents a growing public health concern in the Indian subcontinent, with prevalence estimates ranging from 8.2% to 17.4% across various population-based studies.",
 38→      "The early detection of renal impairment through biomarker screening has demonstrated significant potential for improving patient outcomes and reducing long-term morbidity.",
 39→      "This study investigates the diagnostic accuracy of novel urinary biomarkers in comparison with established serum creatinine-based estimation methods.",
 40→      "Epidemiological data from the Indian CKD Registry indicate that over 17% of the adult population in eastern India harbour stage 2 or higher renal disease, yet fewer than 5% receive timely diagnosis before irreversible nephron loss.",
 41→      "The pathophysiological basis for early biomarker elevation rests on tubular injury preceding glomerular filtration decline. Neutrophil gelatinase-associated lipocalin and kidney injury molecule-1 are upregulated within hours of ischaemic or nephrotoxic insult.",
 42→      "Existing screening protocols relying solely on serum creatinine and estimated GFR have demonstrated limited sensitivity in the subclinical phase, particularly among patients with low muscle mass or vegetarian dietary patterns.",
 43→      "The present study therefore aims to establish a multimarker panel optimised for the Indian clinical context, addressing the diagnostic gap between tubular injury onset and overt glomerular dysfunction.",
 44→    ],
 45→  },
 46→  {
 47→    title: "Materials and Methods",
 48→    subtitle: "Study Design and Protocol",
 49→    body: [
 50→      "A prospective observational study was conducted at the Department of Nephrology, Institute of Post-Graduate Medical Education, Kolkata, over a period of eighteen months from January 2024 to June 2025.",
 51→      "Consecutive patients presenting with clinical features suggestive of early renal impairment were enrolled following informed written consent. The study protocol was approved by the Institutional Ethics Committee.",
 52→      "Serum and urine samples were collected under standardised conditions and analysed using automated biochemistry analysers with appropriate internal quality controls.",
 53→      "Inclusion criteria comprised adults aged 18 to 65 years with at least two risk factors for CKD including diabetes mellitus, hypertension, or family history of renal disease. Exclusion criteria included pre-existing dialysis, renal transplant recipients, and active urinary tract infection.",
 54→      "Statistical analysis was performed using R version 4.4.1. Receiver operating characteristic curves were constructed for each biomarker and the combined panel, with area under the curve compared using the DeLong method.",
 55→      "Sample size was calculated a priori assuming a minimum clinically significant AUC difference of 0.08 between the novel panel and serum creatinine alone, requiring 186 participants at 80% power with alpha set at 0.05.",
 56→    ],
 57→  },
 58→  {
 59→    title: "Review of Literature",
 60→    subtitle: "Current Evidence and Gaps",
 61→    body: [
 62→      "A comprehensive literature search was performed across PubMed, Scopus, and the Cochrane Library databases. Studies published between 2015 and 2025 were included following PRISMA guidelines.",
 63→      "Kumar et al. (2023) reported a sensitivity of 89.4% for cystatin C in detecting early glomerular filtration rate decline, significantly superior to conventional markers.",
 64→      "Despite promising results, heterogeneity in study populations and methodologies necessitates further investigation in the Indian clinical context with standardised protocols.",
 65→      "Sharma and colleagues conducted a multicentre trial across six tertiary hospitals in northern India demonstrating that urinary NGAL levels above 150 ng/mL predicted progression to stage 3 CKD within 24 months with a specificity of 91.2%.",
 66→      "A recent meta-analysis pooling 42 studies and 18,740 participants concluded that combined biomarker panels outperform single-marker strategies, with summary sensitivity of 0.87 and specificity of 0.83 for early CKD detection.",
 67→      "Identified gaps in the existing literature include limited representation of eastern Indian populations, absence of cost-effectiveness analyses for resource-constrained settings, and insufficient longitudinal follow-up beyond 12 months in most published cohorts.",
 68→    ],
 69→  },
 70→];
 71→
 72→function wrapText(ctx: CanvasRenderingContext2D, text: string, maxWidth: number): string[] {
 73→  const words = text.split(" ");
 74→  const lines: string[] = [];
 75→  let currentLine = words[0];
 76→
 77→  for (let i = 1; i < words.length; i++) {
 78→    const testLine = currentLine + " " + words[i];
 79→    if (ctx.measureText(testLine).width <= maxWidth) {
 80→      currentLine = testLine;
 81→    } else {
 82→      lines.push(currentLine);
 83→      currentLine = words[i];
 84→    }
 85→  }
 86→  lines.push(currentLine);
 87→  return lines;
 88→}
 89→
 90→function createPageTexture(variant: 0 | 1 | 2): THREE.CanvasTexture {
 91→  const w = 1024;
 92→  const h = 1440;
 93→  const canvas = document.createElement("canvas");
 94→  canvas.width = w;
 95→  canvas.height = h;
 96→  const ctx = canvas.getContext("2d")!;
 97→
 98→  // Clean white paper background
 99→  ctx.fillStyle = "#FEFEFE";
100→  ctx.fillRect(0, 0, w, h);
101→
102→  // Subtle grain noise
103→  for (let i = 0; i < 800; i++) {
104→    const gx = Math.floor(((i * 7919 + 104729) % 100003) / 100003 * w);
105→    const gy = Math.floor(((i * 6271 + 73856) % 100003) / 100003 * h);
106→    ctx.fillStyle = `rgba(0,0,0,${0.008 + (i % 3) * 0.004})`;
107→    ctx.fillRect(gx, gy, 2, 2);
108→  }
109→
110→  const margin = 88;
111→  const contentW = w - margin * 2;
112→  const content = PAGE_CONTENT[variant];
113→
114→  // Chapter title — large serif
115→  ctx.fillStyle = "#2A2A2A";
116→  ctx.font = "bold 42px Georgia, 'Times New Roman', serif";
117→  ctx.fillText(content.title, margin, 120);
118→
119→  // Subtitle
120→  ctx.fillStyle = "#4A4A4A";
121→  ctx.font = "italic 28px Georgia, 'Times New Roman', serif";
122→  ctx.fillText(content.subtitle, margin, 165);
123→
124→  // Separator line
125→  ctx.fillStyle = "#C0C0C0";
126→  ctx.fillRect(margin, 185, contentW * 0.3, 1.5);
127→
128→  // Body paragraphs — sans-serif
129→  ctx.font = "22px 'Helvetica Neue', Helvetica, Arial, sans-serif";
130→  ctx.fillStyle = "#505050";
131→  const lineHeight = 32;
132→  let y = 230;
133→
134→  for (const paragraph of content.body) {
135→    const wrappedLines = wrapText(ctx, paragraph, contentW);
136→    for (const line of wrappedLines) {
137→      ctx.fillText(line, margin, y);
138→      y += lineHeight;
139→    }
140→    y += 18; // paragraph gap
141→  }
142→
143→  const texture = new THREE.CanvasTexture(canvas);
144→  texture.minFilter = THREE.LinearFilter;
145→  texture.magFilter = THREE.LinearFilter;
146→  texture.colorSpace = THREE.SRGBColorSpace;
147→  return texture;
148→}
149→
150→// ── Thesis Page mesh ────────────────────────────────────────────────────────
151→function ThesisPageMesh({ variant }: { variant: 0 | 1 | 2 }) {
152→  const texture = useMemo(() => createPageTexture(variant), [variant]);
153→
154→  useEffect(() => {
155→    return () => { texture.dispose(); };
156→  }, [texture]);
157→
158→  return (
159→    <group>
160→      {/* Paper body — thicker for visible edges, warm tint for contrast */}
161→      <RoundedBox args={[2.1, 2.97, 0.04]} radius={0.006} smoothness={4} castShadow receiveShadow>
162→        <meshStandardMaterial
163→          color="#F0EDE6"
164→          roughness={0.82}
165→          metalness={0}
166→        />
167→      </RoundedBox>
168→      {/* Front face — bright white texture, sits on front surface */}
169→      <mesh position={[0, 0, 0.021]} castShadow receiveShadow>
170→        <planeGeometry args={[2.1, 2.97]} />
171→        <meshStandardMaterial
172→          map={texture}
173→          color="#FFFFFF"
174→          roughness={0.78}
175→          metalness={0}
176→          emissive="#FFFFFF"
177→          emissiveIntensity={0.06}
178→        />
179→      </mesh>
180→    </group>
181→  );
182→}
183→
184→// ── Thesis pages group — visible fan, 3 edges showing ───────────────────────
185→function ThesisPagesGroup({ reducedMotion }: { reducedMotion: boolean }) {
186→  const Wrapper = reducedMotion ? StaticGroup : FloatWrapper;
187→
188→  return (
189→    <group position={[-0.6, 0.5, 0]}>
190→      {/* Page A — back-left, peeks above and left */}
191→      <Wrapper speed={1.1} floatIntensity={0.15} rotationIntensity={0.03}>
192→        <group
193→          rotation={[0.05, -0.16, -0.08]}
194→          position={[-0.6, 0.4, -1.0]}
195→        >
196→          <ThesisPageMesh variant={0} />
197→        </group>
198→      </Wrapper>
199→
200→      {/* Page B — front-centre, dominant */}
201→      <Wrapper speed={0.9} floatIntensity={0.12} rotationIntensity={0.02}>
202→        <group
203→          rotation={[0.02, -0.03, 0.02]}
204→          position={[0, 0.05, 0]}
205→        >
206→          <ThesisPageMesh variant={1} />
207→        </group>
208→      </Wrapper>
209→
210→      {/* Page C — back-right, peeks right and slightly down */}
211→      <Wrapper speed={0.7} floatIntensity={0.12} rotationIntensity={0.03}>
212→        <group
213→          rotation={[-0.04, 0.18, 0.08]}
214→          position={[0.65, -0.3, -0.65]}
215→        >
216→          <ThesisPageMesh variant={2} />
217→        </group>
218→      </Wrapper>
219→    </group>
220→  );
221→}
222→
223→// ── Float / Static wrapper ──────────────────────────────────────────────────
224→function FloatWrapper({
225→  children,
226→  speed,
227→  floatIntensity,
228→  rotationIntensity,
229→}: {
230→  children: React.ReactNode;
231→  speed: number;
232→  floatIntensity: number;
233→  rotationIntensity: number;
234→}) {
235→  return (
236→    <Float speed={speed} floatIntensity={floatIntensity} rotationIntensity={rotationIntensity}>
237→      {children}
238→    </Float>
239→  );
240→}
241→
242→function StaticGroup({
243→  children,
244→}: {
245→  children: React.ReactNode;
246→  speed?: number;
247→  floatIntensity?: number;
248→  rotationIntensity?: number;
249→}) {
250→  return <group>{children}</group>;
251→}
252→
253→// ── Scatter data — 18 points with varying sizes ─────────────────────────────
254→const SCATTER_COLORS = ["#2D8C5A", "#D4644A", "#3A82B8", "#D4A030"];
255→
256→// Breathe config per colour group — frequency, phase offset, amplitude
257→const GROUP_BREATHE: Record<string, { freq: number; phase: number; amp: number }> = {
258→  [SCATTER_COLORS[0]]: { freq: 0.7, phase: 0, amp: 0.06 },
259→  [SCATTER_COLORS[1]]: { freq: 1.1, phase: 1.2, amp: 0.08 },
260→  [SCATTER_COLORS[2]]: { freq: 0.9, phase: 2.5, amp: 0.05 },
261→  [SCATTER_COLORS[3]]: { freq: 1.4, phase: 0.8, amp: 0.07 },
262→};
263→
264→const SCATTER_POINTS: {
265→  x: number;
266→  y: number;
267→  z: number;
268→  radius: number;
269→  color: string;
270→}[] = [
271→  // Large spheres — spread across Z depth
272→  { x: 0.15, y: 0.22, z: 0.85, radius: 0.12, color: SCATTER_COLORS[0] },
273→  { x: 1.65, y: 1.50, z: 0.20, radius: 0.11, color: SCATTER_COLORS[1] },
274→  { x: 0.90, y: 0.85, z: 1.10, radius: 0.10, color: SCATTER_COLORS[2] },
275→  // Medium-large
276→  { x: 0.50, y: 0.52, z: 0.10, radius: 0.09, color: SCATTER_COLORS[3] },
277→  { x: 1.30, y: 1.20, z: 0.75, radius: 0.09, color: SCATTER_COLORS[0] },
278→  { x: 1.10, y: 0.78, z: 0.40, radius: 0.085, color: SCATTER_COLORS[1] },
279→  // Medium
280→  { x: 0.35, y: 0.55, z: 1.20, radius: 0.075, color: SCATTER_COLORS[2] },
281→  { x: 0.75, y: 0.60, z: 0.05, radius: 0.07, color: SCATTER_COLORS[3] },
282→  { x: 1.45, y: 1.35, z: 0.55, radius: 0.07, color: SCATTER_COLORS[0] },
283→  { x: 1.80, y: 1.58, z: 0.90, radius: 0.075, color: SCATTER_COLORS[1] },
284→  // Small
285→  { x: 0.60, y: 0.75, z: 1.30, radius: 0.055, color: SCATTER_COLORS[2] },
286→  { x: 1.20, y: 0.92, z: 0.15, radius: 0.05, color: SCATTER_COLORS[3] },
287→  { x: 0.25, y: 0.10, z: 0.60, radius: 0.05, color: SCATTER_COLORS[0] },
288→  { x: 1.55, y: 1.10, z: 1.05, radius: 0.055, color: SCATTER_COLORS[1] },
289→  // Tiny
290→  { x: 0.45, y: 0.30, z: 0.95, radius: 0.04, color: SCATTER_COLORS[2] },
291→  { x: 0.85, y: 1.05, z: 1.25, radius: 0.04, color: SCATTER_COLORS[3] },
292→  { x: 1.70, y: 1.42, z: 0.30, radius: 0.035, color: SCATTER_COLORS[0] },
293→  { x: 1.00, y: 0.65, z: 0.70, radius: 0.038, color: SCATTER_COLORS[1] },
294→];
295→
296→// ── Scatter sphere — per-group breathe animation ────────────────────────────
297→function ScatterSphere({
298→  position,
299→  color,
300→  radius,
301→  reducedMotion,
302→}: {
303→  position: [number, number, number];
304→  color: string;
305→  radius: number;
306→  reducedMotion: boolean;
307→}) {
308→  const meshRef = useRef<THREE.Mesh>(null);
309→  const geometry = useMemo(() => new THREE.SphereGeometry(radius, 32, 32), [radius]);
310→  const breathe = GROUP_BREATHE[color] ?? { freq: 0.8, phase: 0, amp: 0.05 };
311→
312→  useFrame(({ clock }) => {
313→    if (reducedMotion || !meshRef.current) return;
314→    const t = clock.getElapsedTime();
315→    const s = 1 + Math.sin(t * breathe.freq + breathe.phase) * breathe.amp;
316→    meshRef.current.scale.setScalar(s);
317→  });
318→
319→  return (
320→    <mesh ref={meshRef} geometry={geometry} position={position} castShadow>
321→      <MeshTransmissionMaterial
322→        transmissionSampler
323→        transmission={1}
324→        thickness={radius * 5}
325→        roughness={0.01}
326→        chromaticAberration={0.04}
327→        anisotropicBlur={0.08}
328→        distortion={0.15}
329→        distortionScale={0.4}
330→        temporalDistortion={0.02}
331→        backside
332→        backsideThickness={radius * 2}
333→        color={color}
334→        toneMapped={false}
335→        opacity={0.65}
336→        transparent
337→      />
338→    </mesh>
339→  );
340→}
341→
342→// ── Axes with tick marks and arrowheads ──────────────────────────────────────
343→const AXIS_COLOR = "#2A2A2A";
344→const AXIS_LENGTH = 2.2;
345→const TICK_SIZE = 0.025;
346→const TICK_INTERVAL = 0.5;
347→const ARROW_HEIGHT = 0.1;
348→const ARROW_RADIUS = 0.025;
349→
350→function AxisArrow({
351→  position,
352→  rotation,
353→}: {
354→  position: [number, number, number];
355→  rotation: [number, number, number];
356→}) {
357→  return (
358→    <mesh position={position} rotation={rotation}>
359→      <coneGeometry args={[ARROW_RADIUS, ARROW_HEIGHT, 8]} />
360→      <meshBasicMaterial color={AXIS_COLOR} />
361→    </mesh>
362→  );
363→}
364→
365→function ScatterAxes({ reducedMotion }: { reducedMotion: boolean }) {
366→  const groupRef = useRef<THREE.Group>(null);
367→
368→  const ticks = useMemo(() => {
369→    const result: { start: [number, number, number]; end: [number, number, number] }[] = [];
370→
371→    for (let t = TICK_INTERVAL; t <= AXIS_LENGTH; t += TICK_INTERVAL) {
372→      result.push({ start: [t, -TICK_SIZE, 0], end: [t, TICK_SIZE, 0] });
373→    }
374→    for (let t = TICK_INTERVAL; t <= AXIS_LENGTH; t += TICK_INTERVAL) {
375→      result.push({ start: [-TICK_SIZE, t, 0], end: [TICK_SIZE, t, 0] });
376→    }
377→    for (let t = TICK_INTERVAL; t <= 0.6; t += TICK_INTERVAL) {
378→      result.push({ start: [0, -TICK_SIZE, t], end: [0, TICK_SIZE, t] });
379→    }
380→
381→    return result;
382→  }, []);
383→
384→  useFrame(({ clock }) => {
385→    if (reducedMotion || !groupRef.current) return;
386→    const t = clock.getElapsedTime();
387→    const s = 1 + Math.sin(t * 0.35) * 0.008;
388→    groupRef.current.scale.setScalar(s);
389→  });
390→
391→  return (
392→    <group ref={groupRef}>
393→      {/* Axis lines */}
394→      <Line points={[[0, 0, 0], [AXIS_LENGTH, 0, 0]]} color={AXIS_COLOR} lineWidth={1} />
395→      <Line points={[[0, 0, 0], [0, AXIS_LENGTH, 0]]} color={AXIS_COLOR} lineWidth={1} />
396→      <Line points={[[0, 0, 0], [0, 0, 0.6]]} color={AXIS_COLOR} lineWidth={1} />
397→
398→      {/* Arrowheads — cones at axis tips */}
399→      <AxisArrow position={[AXIS_LENGTH, 0, 0]} rotation={[0, 0, -Math.PI / 2]} />
400→      <AxisArrow position={[0, AXIS_LENGTH, 0]} rotation={[0, 0, 0]} />
401→      <AxisArrow position={[0, 0, 0.6]} rotation={[Math.PI / 2, 0, 0]} />
402→
403→      {/* Tick marks */}
404→      {ticks.map((tick, i) => (
405→        <Line key={i} points={[tick.start, tick.end]} color={AXIS_COLOR} lineWidth={1} />
406→      ))}
407→    </group>
408→  );
409→}
410→
411→// ── Scatter plot — overlaps right edge of pages ─────────────────────────────
412→function ScatterPlotGroup({ reducedMotion }: { reducedMotion: boolean }) {
413→  const groupRef = useRef<THREE.Group>(null);
414→
415→  useFrame(({ clock }) => {
416→    if (reducedMotion || !groupRef.current) return;
417→    const t = clock.getElapsedTime();
418→    groupRef.current.position.y = -0.3 + Math.sin(t * 0.6) * 0.1;
419→  });
420→
421→  return (
422→    <group
423→      ref={groupRef}
424→      position={[0.0, -0.3, 1.5]}
425→      rotation={[0.1, -45 * (Math.PI / 180), 0.15]}
426→    >
427→      <ScatterAxes reducedMotion={reducedMotion} />
428→      {SCATTER_POINTS.map((pt, i) => (
429→        <ScatterSphere
430→          key={i}
431→          position={[pt.x, pt.y, pt.z]}
432→          color={pt.color}
433→          radius={pt.radius}
434→          reducedMotion={reducedMotion}
435→        />
436→      ))}
437→    </group>
438→  );
439→}
440→
441→// ── Procedural environment (no HDR download) ────────────────────────────────
442→function StudioEnvironment() {
443→  return (
444→    <Environment frames={1} resolution={128} background={false}>
445→      <mesh position={[0, 6, 0]} scale={30}>
446→        <planeGeometry />
447→        <meshBasicMaterial color="#FFFEF8" />
448→      </mesh>
449→      <mesh position={[0, -6, 0]} scale={30} rotation={[Math.PI, 0, 0]}>
450→        <planeGeometry />
451→        <meshBasicMaterial color="#E8E0D0" />
452→      </mesh>
453→      <mesh position={[-8, 0, 0]} scale={20} rotation={[0, Math.PI / 2, 0]}>
454→        <planeGeometry />
455→        <meshBasicMaterial color="#FFF5E8" />
456→      </mesh>
457→      <mesh position={[8, 0, 0]} scale={20} rotation={[0, -Math.PI / 2, 0]}>
458→        <planeGeometry />
459→        <meshBasicMaterial color="#E8ECF0" />
460→      </mesh>
461→      <mesh position={[0, 2, 8]} scale={15} rotation={[0, Math.PI, 0]}>
462→        <planeGeometry />
463→        <meshBasicMaterial color="#FFFFFF" />
464→      </mesh>
465→    </Environment>
466→  );
467→}
468→
469→// ── Main scene ──────────────────────────────────────────────────────────────
470→function Scene() {
471→  const reducedMotion = useReducedMotion();
472→
473→  return (
474→    <>
475→      <StudioEnvironment />
476→
477→      {/* Lighting — key/fill/rim for depth */}
478→      <ambientLight intensity={0.5} />
479→      <directionalLight
480→        position={[4, 8, 3]}
481→        intensity={1.8}
482→        castShadow
483→        shadow-mapSize-width={1024}
484→        shadow-mapSize-height={1024}
485→        shadow-camera-near={0.5}
486→        shadow-camera-far={20}
487→        shadow-camera-left={-6}
488→        shadow-camera-right={6}
489→        shadow-camera-top={6}
490→        shadow-camera-bottom={-6}
491→        shadow-bias={-0.0005}
492→      />
493→      <pointLight position={[-4, 4, 3]} intensity={0.6} color="#FFF5E6" />
494→      <pointLight position={[2, -2, 5]} intensity={0.3} color="#F5F0E8" />
495→      <directionalLight position={[-3, 2, -4]} intensity={0.8} color="#E8ECF0" />
496→
497→      {/* Soft contact shadows beneath objects */}
498→      <ContactShadows
499→        position={[0, -1.5, 0]}
500→        opacity={0.3}
501→        scale={20}
502→        blur={2.5}
503→        smooth
504→        far={5}
505→        resolution={512}
506→        color="#2A2520"
507→      />
508→
509→      <ThesisPagesGroup reducedMotion={reducedMotion} />
510→      <ScatterPlotGroup reducedMotion={reducedMotion} />
511→    </>
512→  );
513→}
514→
515→// ── Exported component ──────────────────────────────────────────────────────
516→export function Hero3DScene() {
517→  return (
518→    <Canvas
519→      shadows
520→      camera={{ position: [0, 0.5, 7], fov: 38, near: 0.1, far: 50 }}
521→      dpr={[1, 1.5]}
522→      gl={{ antialias: true, alpha: true, powerPreference: "default" }}
523→      style={{ background: "transparent" }}
524→    >
525→      <Scene />
526→    </Canvas>
527→  );
528→}
