307→        const content = await readFile(path.join(templatesDir, file));
308→        await writeFile(path.join(workDir, file), content);
309→      } catch {
310→        // May not exist
311→      }
312→    }
313→
314→    // Copy logos
315→    try {
316→      await mkdir(path.join(workDir, "logo"), { recursive: true });
317→      const { stdout } = await execFileAsync("ls", [path.join(templatesDir, "logo")]);
318→      for (const file of stdout.trim().split("\n").filter(Boolean)) {
319→        const content = await readFile(path.join(templatesDir, "logo", file));
320→        await writeFile(path.join(workDir, "logo", file), content);
321→      }
322→    } catch {
323→      // May not exist
324→    }
325→
326→    // Write references.bib (populated or empty)
327→    await writeFile(path.join(workDir, "references.bib"), options.bibContent ?? "");
328→
329→    // Write chapter files
330→    if (options.chapterFiles) {
331→      await mkdir(path.join(workDir, "chapters"), { recursive: true });
332→      for (const [filename, content] of Object.entries(options.chapterFiles)) {
333→        await writeFile(path.join(workDir, filename), content);
334→      }
335→    }
336→
337→    // Copy figure files (relative path → absolute source) into workDir
338→    if (options.figureFiles) {
339→      for (const [relPath, srcPath] of Object.entries(options.figureFiles)) {
340→        try {
341→          const destPath = path.join(workDir, relPath);
342→          await mkdir(path.dirname(destPath), { recursive: true });
343→          await copyFile(srcPath, destPath);
344→        } catch {
345→          // Figure file may have been deleted — non-fatal
346→        }
347→      }
348→    }
349→
350→    // Run pdflatex → bibtex → pdflatex × 2
351→    const pdflatexArgs = ["-interaction=nonstopmode", "-output-directory", workDir, path.join(workDir, "main.tex")];
352→
353→    await execFileAsync("pdflatex", pdflatexArgs, { timeout: 120_000, cwd: workDir }).catch(() => {});
354→    await execFileAsync("bibtex", [path.join(workDir, "main")], { timeout: 30_000, cwd: workDir }).catch(() => {});
355→    await execFileAsync("pdflatex", pdflatexArgs, { timeout: 120_000, cwd: workDir }).catch(() => {});
356→    await execFileAsync("pdflatex", pdflatexArgs, { timeout: 120_000, cwd: workDir }).catch(() => {});
357→
358→    // Persist PDF to a stable directory
359→    let pdfPath: string | null = null;
360→    try {
361→      const pdfData = await readFile(path.join(workDir, "main.pdf"));
362→      const persistDir = path.join(os.tmpdir(), "apollo-pdfs");
363→      await mkdir(persistDir, { recursive: true });
