648→    \noindent I declare that this work has been carried out by me at the \departmentnametext, \institutenametext, \institutelocationtext\ during the period of \studyperiodtext\ under the guidance of \textbf{\supervisornametext}, \supervisordesignationtext.
649→
650→    \vspace{0.25cm}
651→
652→    \noindent I further declare that:
653→    \begin{enumerate}[label=\arabic*.,itemsep=2pt,parsep=2pt]
654→        \item This dissertation or any part thereof has not been previously submitted to any university or institution for the award of any degree, diploma, or other similar title.
655→
656→        \item The work presented in this dissertation is original and the results are based on my own research conducted during the prescribed period and wherever contributions of others are involved, every effort has been made to acknowledge them clearly with due references to the literature and acknowledgement of collaborative research and discussions.
657→
658→        \item I have followed the ethical guidelines prescribed by the Institutional Human Ethics Committee (IHEC) and have obtained necessary ethical clearance for this study.
659→
660→        \item This bonafide work or a part of this work was not submitted by me or any others for any award, degree, or diploma to any other University, Board either in India or abroad.
661→    \end{enumerate}
662→
663→    \vspace{0.5cm}
664→
665→    \noindent
666→    \begin{tabular}{@{}p{0.5\textwidth}p{0.5\textwidth}@{}}
667→        \raggedright
668→        Place: \institutelocationtext
669→
670→        \vspace{0.2cm}
671→
672→        Date: \underline{\hspace{3cm}}
673→        &
674→        \raggedleft
675→        \begin{tabular}[t]{c}
676→            \underline{\hspace{5cm}}\\
677→            \textbf{\candidatenametext}\\
678→            \candidatedesignationtext\\
679→            Registration No.: \registrationnotext\\
680→            \departmentnametext\\
681→            \institutenametext
682→        \end{tabular}
683→    \end{tabular}
684→    \end{singlespace}
685→
686→    \clearpage
687→}
688→
689→%% ============================================================
690→%% ACKNOWLEDGEMENTS ENVIRONMENT
691→%% ============================================================
692→
693→\newenvironment{acknowledgements}{%
694→    \thispagestyle{empty}
695→    \begin{center}
696→        {\Large\bfseries ACKNOWLEDGEMENTS}\\[1cm]
697→    \end{center}
698→}{%
699→    \clearpage
700→}
701→
702→%% ============================================================
703→%% ABSTRACT ENVIRONMENT
704→%% ============================================================
705→
706→\newenvironment{thesis_abstract}{%
707→    \thispagestyle{empty}
708→    \begin{center}
709→        {\Large\bfseries ABSTRACT}\\[0.5cm]
710→    \end{center}
711→
712→    \noindent\textbf{Title:} \thesistitletext\\[0.3cm]
713→    \noindent\textbf{Background:}
714→}{%
715→    \clearpage
716→}
717→
718→%% ============================================================
719→%% LIST OF ABBREVIATIONS ENVIRONMENT
720→%% ============================================================
721→
722→\newenvironment{abbreviations}{%
723→    \thispagestyle{empty}
724→    \begin{center}
725→        {\Large\bfseries LIST OF ABBREVIATIONS}\\[1cm]
726→    \end{center}
727→    \begin{longtable}{@{}l@{\hspace{2cm}}l@{}}
728→}{%
729→    \end{longtable}
730→    \clearpage
731→}
732→
733→%% ============================================================
734→%% FRONT MATTER, MAIN MATTER, BACK MATTER COMMANDS
735→%% ============================================================
736→
737→\newcommand{\frontmatter}{%
738→    \cleardoublepage
739→    \pagenumbering{gobble}  % No page numbers for front matter
740→    \pagestyle{frontmatter}
741→}
742→
743→\newcommand{\mainmatter}{%
744→    \cleardoublepage
745→    \pagenumbering{arabic}
746→    \setcounter{page}{1}
747→    \pagestyle{mainmatter}
748→    % Ensure chapter pages also use mainmatter style
749→    \assignpagestyle{\chapter}{mainmatter}
750→}
751→
752→\newcommand{\backmatter}{%
753→    \cleardoublepage
754→    \pagestyle{bibliography}
755→    \gdef\currentchaptertitle{Bibliography}
756→}
757→
758→\newcommand{\annexurematter}{%
759→    \cleardoublepage
760→    \setcounter{chapter}{0}
761→    \renewcommand{\thechapter}{\Roman{chapter}}
762→    \pagestyle{annexure}
763→    \gdef\currentchaptertitle{Annexures}
764→}
765→
766→%% ============================================================
767→%% ANNEXURE CHAPTER COMMAND
768→%% ============================================================
769→
770→\newcommand{\annexurechapter}[1]{%
771→    \refstepcounter{chapter}
772→    \chapter*{ANNEXURE \thechapter: #1}
773→    \addcontentsline{toc}{chapter}{Annexure \thechapter: #1}
774→    \gdef\currentchaptertitle{Annexure \thechapter: #1}
775→}
776→
777→%% ============================================================
778→%% TABLE AND FIGURE NUMBERING
779→%% ============================================================
780→
781→% Number tables and figures by chapter
782→\numberwithin{table}{chapter}
783→\numberwithin{figure}{chapter}
784→
785→%% ============================================================
786→%% PARAGRAPH FORMATTING
787→%% ============================================================
788→
789→% First line indent (1 tab = 5 spaces ~ 0.5 inch)
790→\setlength{\parindent}{0.5in}
791→
792→% Paragraph spacing
793→\setlength{\parskip}{0pt}
794→
795→%% ============================================================
796→%% BIBLIOGRAPHY SETUP
797→%% ============================================================
798→
799→% Bibliography environment modifications
800→\renewcommand{\bibsection}{%
801→    \chapter*{\bibname}%
802→    \addcontentsline{toc}{chapter}{\bibname}%
803→    \gdef\currentchaptertitle{Bibliography}%
804→}
805→
806→%% ============================================================
807→%% CUSTOM COMMANDS FOR MEDICAL/SCIENTIFIC WRITING
808→%% ============================================================
809→
810→% P-value formatting
811→\newcommand{\pvalue}[1]{p~=~#1}
812→\newcommand{\pless}[1]{p~<~#1}
813→\newcommand{\pgreater}[1]{p~>~#1}
814→
815→% Confidence interval
816→\newcommand{\ci}[2]{95\%~CI~[#1,~#2]}
817→
818→% Mean and SD
819→\newcommand{\meansd}[2]{#1~$\pm$~#2}
820→
821→% Median and IQR
822→\newcommand{\medianiqr}[2]{#1~(IQR:~#2)}
823→
824→% Correlation coefficients
825→\newcommand{\rhoval}[1]{$\rho$~=~#1}
826→\newcommand{\spearmanrho}[1]{$\rho$~=~#1}
827→\newcommand{\pearsonr}[1]{r~=~#1}
828→
829→% Chi-square
830→\newcommand{\chisq}[1]{$\chi^2$~=~#1}
831→
832→% Risk and ratio measures
833→\newcommand{\oddsratio}[1]{OR~=~#1}
834→\newcommand{\relativerisk}[1]{RR~=~#1}
835→\newcommand{\hazardratio}[1]{HR~=~#1}
836→
837→% Sample size
838→\newcommand{\samplesize}[1]{n~=~#1}
839→
840→%% ============================================================
841→%% WATERMARK COMMANDS
842→%% ============================================================
843→
844→% Opacity guide: Higher value = MORE visible
845→%   0.05-0.10 = very subtle | 0.15-0.25 = light but readable
846→%   0.30-0.50 = clearly visible | 0.50+ = very prominent
847→
848→% Main command: \setupwatermark{text}{opacity}
849→\newcommand{\setupwatermark}[2]{%
850→    \SetWatermarkText{#1}%
851→    \edef\@watermarkgray{\fpeval{1-#2}}%
852→    \SetWatermarkColor[gray]{\@watermarkgray}%
853→}
854→
855→% Individual setters
856→\newcommand{\setwatermarktext}[1]{\SetWatermarkText{#1}}
857→\newcommand{\setwatermarkopacity}[1]{%
858→    \edef\@watermarkgray{\fpeval{1-#1}}%
859→    \SetWatermarkColor[gray]{\@watermarkgray}%
860→}
861→\newcommand{\setwatermarkscale}[1]{\SetWatermarkScale{#1}}
862→\newcommand{\setwatermarkangle}[1]{\SetWatermarkAngle{#1}}
863→\newcommand{\removewatermark}{\SetWatermarkText{}}
864→
865→% Presets
866→\newcommand{\draftwatermark}{%
867→    \SetWatermarkText{DRAFT}\SetWatermarkColor[gray]{0.85}%
868→    \SetWatermarkScale{1}\SetWatermarkAngle{45}}
869→\newcommand{\confidentialwatermark}{%
870→    \SetWatermarkText{CONFIDENTIAL}\SetWatermarkColor[gray]{0.80}%
871→    \SetWatermarkScale{0.8}\SetWatermarkAngle{45}}
872→\newcommand{\reviewwatermark}{%
873→    \SetWatermarkText{FOR REVIEW ONLY}\SetWatermarkColor[gray]{0.85}%
874→    \SetWatermarkScale{0.6}\SetWatermarkAngle{45}}
875→\newcommand{\examwatermark}{%
876→    \SetWatermarkText{FOR EXAMINATION}\SetWatermarkColor[gray]{0.88}%
877→    \SetWatermarkScale{0.6}\SetWatermarkAngle{45}}
878→
879→%% ============================================================
