  1→"use client";
  2→
  3→import { useState, useEffect, useRef } from "react";
  4→import { Button } from "@/components/ui/button";
  5→import { FileText, Loader2, Clock } from "lucide-react";
  6→
  7→interface CompileButtonProps {
  8→  projectId: string;
  9→  disabled?: boolean;
 10→  onCompileSuccess?: () => void;
 11→}
 12→
 13→interface CompileResult {
 14→  compilation_id: string;
 15→  status: string;
 16→  warnings: string[];
 17→  compile_time_ms: number;
 18→}
 19→
 20→export function CompileButton({ projectId, disabled, onCompileSuccess }: CompileButtonProps) {
 21→  const [isCompiling, setIsCompiling] = useState(false);
 22→  const [result, setResult] = useState<CompileResult | null>(null);
 23→  const [error, setError] = useState<string | null>(null);
 24→  const [queuePosition, setQueuePosition] = useState<number | null>(null);
 25→  const [estimatedWait, setEstimatedWait] = useState<number | null>(null);
 26→  const pollRef = useRef<ReturnType<typeof setInterval> | null>(null);
 27→
 28→  // Poll for queue position when queued
 29→  useEffect(() => {
 30→    if (queuePosition === null || queuePosition <= 0) {
 31→      if (pollRef.current) {
 32→        clearInterval(pollRef.current);
 33→        pollRef.current = null;
 34→      }
 35→      return;
 36→    }
 37→
 38→    pollRef.current = setInterval(async () => {
 39→      try {
 40→        const response = await fetch(`/api/projects/${projectId}/compile`, {
 41→          method: "POST",
 42→        });
 43→        const body = await response.json();
 44→
 45→        if (response.status === 202 || response.status === 429) {
 46→          const pos = body.data?.queue_position ?? body.error?.queue_position;
 47→          const wait = body.data?.estimated_wait_ms ?? body.error?.estimated_wait_ms;
 48→          setQueuePosition(pos ?? null);
 49→          setEstimatedWait(wait ?? null);
 50→        } else if (response.ok && body.data?.status === "completed") {
 51→          setResult({
 52→            compilation_id: body.data.compilation_id,
 53→            status: "completed",
 54→            warnings: Array.isArray(body.data.warnings) ? body.data.warnings : [],
 55→            compile_time_ms: body.data.compile_time_ms ?? 0,
 56→          });
 57→          setQueuePosition(null);
 58→          setEstimatedWait(null);
 59→          setIsCompiling(false);
 60→          onCompileSuccess?.();
 61→        } else if (body.error) {
 62→          setError(body.error.message);
 63→          setQueuePosition(null);
 64→          setIsCompiling(false);
 65→        }
 66→      } catch {
 67→        // Ignore polling errors
 68→      }
 69→    }, 5000);
 70→
 71→    return () => {
 72→      if (pollRef.current) clearInterval(pollRef.current);
