 79→
 80→type MobileTab = "edit" | "preview";
 81→
 82→type WorkspaceTab = "editor" | "data" | "compliance" | "figures" | "progress";
 83→
 84→interface ProjectWorkspaceProps {
 85→  project: Project;
 86→  sections: Section[];
 87→  citations: Citation[];
 88→  datasets: Dataset[];
 89→  analyses: Analysis[];
 90→  figures: Figure[];
 91→  complianceChecks: ComplianceCheck[];
 92→  compilations: Compilation[];
