676→          className={mobileTab === "preview" ? "hidden" : ""}
677→        >
678→          <ErrorBoundary>
679→            {renderEditor()}
680→          </ErrorBoundary>
681→        </div>
682→        <div
683→          className={mobileTab === "edit" ? "hidden" : ""}
684→        >
685→          <ErrorBoundary>
686→            <PdfViewer key={pdfKey} url={pdfUrl} projectId={project.id} isSandbox={project.status === "sandbox"} />
687→          </ErrorBoundary>
688→        </div>
689→      </div>
690→        </>
691→      )}
692→
693→      {/* Refine dialog — targeted AI editing */}
694→      {refineDialogOpen && (
695→        <div className="fixed inset-0 z-50 flex items-center justify-center bg-black/30 backdrop-blur-sm">
696→          <div className="mx-4 w-full max-w-lg rounded-2xl bg-white p-6 shadow-xl">
697→            <h3 className="font-serif text-lg font-semibold text-[#2F2F2F]">
698→              Refine this section
699→            </h3>
700→            <p className="mt-1 text-sm text-[#6B6B6B]">
701→              Describe what you&apos;d like to change. The AI will modify only the relevant parts.
702→            </p>
703→            <textarea
704→              value={refineInstructions}
705→              onChange={(e) => setRefineInstructions(e.target.value)}
706→              placeholder="e.g., Expand the paragraph about inclusion criteria&#10;Add a comparison table for the three main studies&#10;Add 5 more recent references (2020-2025)"
707→              className="mt-3 w-full rounded-xl border border-black/10 px-3 py-2 text-sm placeholder:text-[#D1D1D1] focus:border-[#8B9D77] focus:outline-none focus:ring-1 focus:ring-[#8B9D77]/30"
708→              rows={4}
709→              disabled={isRefining}
710→            />
711→            <div className="mt-4 flex justify-end gap-2">
712→              <Button
713→                variant="outline"
714→                size="sm"
715→                onClick={() => {
716→                  setRefineDialogOpen(false);
717→                  setRefineInstructions("");
718→                }}
719→                disabled={isRefining}
720→              >
721→                Cancel
722→              </Button>
723→              <Button
724→                size="sm"
725→                disabled={!refineInstructions.trim() || isRefining}
726→                onClick={async () => {
727→                  setIsRefining(true);
728→                  try {
729→                    const res = await fetch(
730→                      `/api/projects/${project.id}/sections/${viewingPhase}/refine`,
731→                      {
732→                        method: "POST",
733→                        headers: { "Content-Type": "application/json" },
734→                        body: JSON.stringify({ instructions: refineInstructions.trim() }),
735→                      }
736→                    );
737→                    if (res.ok && res.body) {
738→                      // Consume the SSE stream to completion before refreshing
739→                      const reader = res.body.getReader();
740→                      try {
741→                        while (true) {
742→                          const { done } = await reader.read();
743→                          if (done) break;
744→                        }
745→                      } finally {
746→                        reader.releaseLock();
747→                      }
748→                      setRefineDialogOpen(false);
749→                      setRefineInstructions("");
750→                      router.refresh();
751→                      void compileAndRefreshPdf();
752→                    }
753→                  } catch {
754→                    // Error handled silently
755→                  } finally {
756→                    setIsRefining(false);
757→                  }
758→                }}
759→              >
760→                {isRefining ? "Refining..." : "Refine"}
761→              </Button>
762→            </div>
763→          </div>
764→        </div>
765→      )}
766→
767→      {/* Review dialog — shown before approval when issues found */}
768→      <ReviewDialog
769→        open={reviewDialogOpen}
770→        issues={reviewIssues}
771→        onGoBack={() => setReviewDialogOpen(false)}
772→        onApproveAnyway={() => {
773→          setReviewDialogOpen(false);
774→          doApprove();
775→        }}
776→      />
777→
778→      </>}
779→
780→      {/* Onboarding tour — shows once for first-time users */}
781→      <TourOverlay />
782→    </div>
783→  );
784→}
