%% Main command: \setupwatermark{text}{opacity}
\newcommand{\setupwatermark}[2]{%
    \SetWatermarkText{#1}%
    \edef\@watermarkgray{\fpeval{1-#2}}%
    \SetWatermarkColor[gray]{\@watermarkgray}%
}

