function countWords(latex: string): number {
  // Only count body — exclude BibTeX trailer
  const sepIdx = latex.indexOf("---BIBTEX---");
  const body = sepIdx >= 0 ? latex.slice(0, sepIdx) : latex;

  const stripped = body
    .replace(/\\[a-zA-Z]+(\{[^}]*\})?/g, " ")
    .replace(/[{}\\%$&_^~#]/g, " ")
    .replace(/\s+/g, " ")
    .trim();
  return stripped ? stripped.split(" ").length : 0;
}

