  1→import { generateTex } from "./generate-tex";
  2→import { normaliseUnicode } from "./escape";
  3→import { generateAbbreviationsLatex } from "./abbreviations";
  4→import { sanitiseLatexOutput } from "@/lib/ai/sanitise-latex";
  5→import type { Project, Section, Citation, Abbreviation } from "@/lib/types/database";
  6→
  7→// ── Phase → chapter file mapping ────────────────────────────────────────────
  8→
  9→const PHASE_CHAPTER_MAP: Record<number, string> = {
 10→  2: "chapters/introduction.tex",
 11→  3: "chapters/aims.tex",
 12→  4: "chapters/literature.tex",
 13→  5: "chapters/methodology.tex",
 14→  6: "chapters/results.tex",
 15→  7: "chapters/discussion.tex",
 16→  8: "chapters/conclusion.tex",
 17→};
 18→
 19→// ── BibTeX splitter ─────────────────────────────────────────────────────────
 20→
 21→const BIBTEX_SEPARATOR = "---BIBTEX---";
 22→
 23→export interface SplitResult {
 24→  body: string;
 25→  bib: string;
 26→}
 27→
 28→/**
 29→ * Split section latex_content at the `---BIBTEX---` marker.
 30→ * Returns the body (before marker) and bib entries (after marker).
 31→ */
 32→export function splitBibtex(latexContent: string): SplitResult {
 33→  const idx = latexContent.indexOf(BIBTEX_SEPARATOR);
 34→  if (idx === -1) {
 35→    return { body: latexContent.trim(), bib: "" };
 36→  }
 37→  return {
 38→    body: latexContent.slice(0, idx).trim(),
 39→    bib: latexContent.slice(idx + BIBTEX_SEPARATOR.length).trim(),
 40→  };
 41→}
 42→
 43→// ── BibTeX deduplication ────────────────────────────────────────────────────
 44→
 45→/**
 46→ * Parse `@type{key,` entries from BibTeX content, deduplicate by cite key
 47→ * (last-write-wins so citations table entries override AI-generated ones).
 48→ */
 49→export function deduplicateBibEntries(bibContent: string): string {
 50→  if (!bibContent.trim()) return "";
 51→
 52→  const entries: { key: string; entry: string }[] = [];
 53→  const lines = bibContent.split("\n");
 54→  let currentEntry = "";
 55→  let currentKey = "";
 56→
 57→  for (const line of lines) {
 58→    const entryStart = line.match(/^@(\w+)\{([^,]+),/);
 59→    if (entryStart) {
 60→      if (currentEntry && currentKey) {
 61→        entries.push({ key: currentKey, entry: currentEntry.trim() });
 62→      }
 63→      currentKey = entryStart[2].trim();
 64→      currentEntry = line + "\n";
 65→    } else if (currentKey) {
 66→      currentEntry += line + "\n";
 67→    }
 68→  }
 69→
 70→  if (currentEntry && currentKey) {
 71→    entries.push({ key: currentKey, entry: currentEntry.trim() });
 72→  }
 73→
 74→  const seen = new Map<string, string>();
 75→  for (const { key, entry } of entries) {
 76→    seen.set(key, entry);
 77→  }
 78→
 79→  return Array.from(seen.values()).join("\n\n");
 80→}
 81→
 82→// ── Invalid citation stripping ───────────────────────────────────────────────
 83→
 84→/**
 85→ * Strip \cite{key} commands where the key is either:
 86→ * (a) Tier D (unverified) --- existing behaviour
 87→ * (b) Not in the citations table at all (orphan)
 88→ *
 89→ * Per PLAN.md §358-362: invalid citations are replaced with:
 90→ *   % UNRESOLVED: key --- provide source or remove
 91→ */
 92→export function stripInvalidCitations(
 93→  chapterBody: string,
 94→  validCiteKeys: Set<string>,
 95→): { stripped: string; replacedKeys: string[] } {
 96→  if (validCiteKeys.size === 0) {
 97→    // No valid keys at all --- strip everything
 98→  }
 99→
100→  const replacedKeys: string[] = [];
101→
102→  // Match \cite{key} and \cite{key1,key2,...} patterns
103→  const stripped = chapterBody.replace(
104→    /\\cite\{([^}]+)\}/g,
105→    (_match, keysStr: string) => {
106→      const keys = keysStr.split(",").map((k) => k.trim());
107→      const kept = keys.filter((k) => validCiteKeys.has(k));
108→      const removed = keys.filter((k) => !validCiteKeys.has(k));
109→
110→      for (const k of removed) {
111→        if (!replacedKeys.includes(k)) replacedKeys.push(k);
112→      }
113→
114→      if (kept.length === 0 && removed.length > 0) {
115→        return removed
116→          .map((k) => `% UNRESOLVED: ${k} --- provide source or remove`)
117→          .join("\n");
118→      }
119→
120→      if (removed.length > 0) {
121→        const comments = removed
122→          .map((k) => `% UNRESOLVED: ${k} --- provide source or remove`)
123→          .join("\n");
124→        return `\\cite{${kept.join(", ")}}\n${comments}`;
125→      }
126→
127→      return _match;
128→    }
129→  );
130→
131→  return { stripped, replacedKeys };
132→}
133→
134→/** @deprecated Use stripInvalidCitations instead */
135→export function stripTierDCitations(
136→  chapterBody: string,
137→  tierDKeys: Set<string>
138→): { stripped: string; replacedKeys: string[] } {
139→  // Build a set of all keys EXCEPT Tier D (inverse)
140→  // For backwards compatibility, we invert the logic
141→  const allKeysInBody = new Set<string>();
142→  const re = /\\cite\{([^}]+)\}/g;
143→  let m: RegExpExecArray | null;
144→  while ((m = re.exec(chapterBody)) !== null) {
145→    for (const k of m[1].split(",")) {
146→      const trimmed = k.trim();
147→      if (trimmed) allKeysInBody.add(trimmed);
148→    }
149→  }
150→  const validKeys = new Set([...allKeysInBody].filter((k) => !tierDKeys.has(k)));
151→  return stripInvalidCitations(chapterBody, validKeys);
152→}
153→
154→// ── Chapter body sanitisation ────────────────────────────────────────────────
155→
156→/**
157→ * Sanitise a chapter body:
158→ * 1. Strip bare markdown separators (--- / *** / ___)
159→ * 2. Repair unbalanced \begin{}/\end{} environments
160→ * 3. Repair unbalanced braces
161→ */
162→function sanitiseChapterLatex(latex: string): string {
163→  // 1. Strip markdown separators (lines that are just --- or *** or ___)
164→  let result = latex.replace(/^\s*[-*_]{3,}\s*$/gm, "");
165→
166→  // 2. Repair environment balance
167→  const beginRe = /\\begin\{(\w+)\}/g;
168→  const endRe = /\\end\{(\w+)\}/g;
169→  const envStack: string[] = [];
170→  const tokens: { type: "begin" | "end"; name: string; pos: number }[] = [];
171→
172→  for (const m of result.matchAll(beginRe)) {
173→    tokens.push({ type: "begin", name: m[1], pos: m.index! });
174→  }
175→  for (const m of result.matchAll(endRe)) {
176→    tokens.push({ type: "end", name: m[1], pos: m.index! });
177→  }
178→  tokens.sort((a, b) => a.pos - b.pos);
179→
180→  // Track unmatched \end{} positions to remove
181→  const unmatchedEndPositions: { pos: number; len: number }[] = [];
182→
183→  for (const token of tokens) {
184→    if (token.type === "begin") {
185→      envStack.push(token.name);
186→    } else {
187→      if (envStack.length > 0 && envStack[envStack.length - 1] === token.name) {
188→        envStack.pop();
189→      } else {
190→        // Unmatched \end{} — mark for removal
191→        const fullMatch = `\\end{${token.name}}`;
192→        unmatchedEndPositions.push({ pos: token.pos, len: fullMatch.length });
193→      }
194→    }
195→  }
196→
197→  // Remove unmatched \end{} from end to start (so positions stay valid)
198→  for (let i = unmatchedEndPositions.length - 1; i >= 0; i--) {
199→    const { pos, len } = unmatchedEndPositions[i];
200→    result = result.slice(0, pos) + result.slice(pos + len);
201→  }
202→
203→  // Close any remaining unclosed environments in LIFO order
204→  if (envStack.length > 0) {
205→    const closings = envStack.reverse().map((env) => `\\end{${env}}`).join("\n");
206→    result = result.trimEnd() + "\n" + closings + "\n";
207→  }
208→
209→  // 3. Repair unbalanced braces
210→  let braceDepth = 0;
211→  let inComment = false;
212→  for (let i = 0; i < result.length; i++) {
213→    const ch = result[i];
214→    if (ch === "%" && (i === 0 || result[i - 1] !== "\\")) {
215→      inComment = true;
216→      continue;
217→    }
218→    if (ch === "\n") {
219→      inComment = false;
220→      continue;
221→    }
222→    if (inComment) continue;
223→    if (ch === "{" && (i === 0 || result[i - 1] !== "\\")) braceDepth++;
224→    else if (ch === "}" && (i === 0 || result[i - 1] !== "\\")) braceDepth--;
225→  }
226→
227→  if (braceDepth > 0) {
228→    // More { than } — append closing braces
229→    result = result.trimEnd() + "}".repeat(braceDepth) + "\n";
230→  } else if (braceDepth < 0) {
231→    // More } than { — prepend opening braces
232→    result = "{".repeat(Math.abs(braceDepth)) + result;
233→  }
234→
235→  return result;
236→}
237→
238→// ── Bare ampersand sanitisation ──────────────────────────────────────────────
239→
240→/**
241→ * Escape bare `&` characters outside of tabular environments.
242→ *
243→ * In LaTeX, `&` is a column separator inside tabular/longtable/array — valid there.
244→ * Everywhere else it causes "Misplaced alignment tab character &".
245→ *
246→ * AI-generated content may contain bare `&` outside tabular environments
247→ * (e.g. journal names like "Endocrinology & Metabolism"). This function
248→ * escapes them while preserving valid column separators inside tabulars.
249→ */
250→function escapeBareAmpersands(latex: string): string {
251→  const lines = latex.split("\n");
252→  let tabularDepth = 0;
253→
254→  return lines
255→    .map((line) => {
256→      const opens = (
257→        line.match(/\\begin\{(tabular|longtable|tabularx|array)\}/g) || []
258→      ).length;
259→      const closes = (
260→        line.match(/\\end\{(tabular|longtable|tabularx|array)\}/g) || []
261→      ).length;
262→
263→      // Inside a tabular env — & is a column separator, leave it
264→      if (tabularDepth > 0 || opens > 0) {
265→        tabularDepth += opens - closes;
266→        tabularDepth = Math.max(0, tabularDepth);
267→        return line;
268→      }
269→
270→      tabularDepth += opens - closes;
271→      tabularDepth = Math.max(0, tabularDepth);
272→
273→      // Skip comment lines
274→      if (line.trimStart().startsWith("%")) return line;
275→
276→      // Escape bare & (not preceded by \)
277→      return line.replace(/(?<!\\)&/g, "\\&");
278→    })
279→    .join("\n");
280→}
281→
282→// ── DOI underscore escaping ──────────────────────────────────────────────────
283→
284→/**
285→ * Escape bare `_` in DOI field values.
286→ * BibTeX passes DOI strings through to the .bbl file unchanged.
287→ * Bare `_` in DOIs like `10.4103/jgid.jgid_132_18` causes
288→ * "Missing $ inserted" errors in pdfLaTeX.
289→ */
290→function escapeDOIUnderscores(bibContent: string): string {
291→  // Match doi = {value} and doi = "value" patterns
292→  return bibContent.replace(
293→    /^(\s*doi\s*=\s*\{)([^}]+)(\})/gm,
294→    (_match, prefix: string, value: string, suffix: string) => {
295→      // Escape bare _ (not already escaped as \_)
296→      const escaped = value.replace(/(?<!\\)_/g, "\\_");
297→      return prefix + escaped + suffix;
298→    }
299→  );
300→}
301→
302→// ── Front matter injection ──────────────────────────────────────────────────
303→
304→/**
305→ * Extract Phase 1 content and inject acknowledgements + abstract into the
306→ * template's \begin{acknowledgements}...\end{acknowledgements} and
307→ * \begin{thesis_abstract}...\end{thesis_abstract} environments.
308→ */
309→function injectFrontMatter(
310→  tex: string,
311→  sections: Section[],
312→  warnings: string[]
313→): string {
314→  const phase1 = sections
315→    .filter((s) => s.phase_number === 1)
316→    .sort((a, b) => {
317→      const priority: Record<string, number> = { approved: 0, review: 1 };
318→      return (priority[a.status] ?? 2) - (priority[b.status] ?? 2);
319→    })[0];
320→
321→  if (!phase1) return tex;
322→
323→  // Get content directly from latex_content (canonical)
324→  let content = "";
325→  if (phase1.latex_content) {
326→    const { body } = splitBibtex(phase1.latex_content);
327→    content = body;
328→  }
329→
330→  if (!content.trim()) return tex;
331→
332→  // Sanitise markdown artefacts — AI may generate markdown instead of LaTeX.
333→  // This converts # headings → \section{}, **bold** → \textbf{}, etc.
334→  content = sanitiseLatexOutput(content);
335→  // Strip markdown separators (---) that sanitiseLatexOutput doesn't handle
336→  content = content.replace(/^\s*[-*_]{3,}\s*$/gm, "");
337→
338→  // Split at ABSTRACT heading — try \section first, then \subsection (from sanitised markdown)
339→  let ackText = content;
340→  let abstractText = "";
341→
342→  const abstractPatterns = [
343→    /\\section\*?\{[^}]*ABSTRACT[^}]*\}/i,
344→    /\\section\*?\{[^}]*Abstract[^}]*\}/,
345→    /\\subsection\*?\{[^}]*ABSTRACT[^}]*\}/i,
346→  ];
347→
348→  for (const pattern of abstractPatterns) {
349→    const match = content.match(pattern);
350→    if (match?.index !== undefined) {
351→      ackText = content.slice(0, match.index).trim();
352→      abstractText = content.slice(match.index + match[0].length).trim();
353→      break;
354→    }
355→  }
356→
357→  // Clean acknowledgements — remove heading, keep body paragraphs
358→  // Handle both LaTeX \section and \subsection (from sanitised markdown ## headings)
359→  ackText = ackText
360→    .replace(/\\section\*?\{[^}]*FRONT\s+MATTER[^}]*\}/i, "")
361→    .replace(/\\section\*?\{[^}]*ACKNOWLEDGEMENTS[^}]*\}/i, "")
362→    .replace(/\\section\*?\{[^}]*Acknowledgements[^}]*\}/i, "")
363→    .replace(/\\subsection\*?\{[^}]*ACKNOWLEDGEMENTS[^}]*\}/i, "")
364→    .trim();
365→
366→  // Inject acknowledgements (sanitise bare & in case AI missed escaping)
367→  if (ackText) {
368→    const safeAck = escapeBareAmpersands(ackText);
369→    tex = tex.replace(
370→      /\\begin\{acknowledgements\}[\s\S]*?\\end\{acknowledgements\}/,
371→      `\\begin{acknowledgements}\n\n${safeAck}\n\n\\end{acknowledgements}`
372→    );
373→  }
374→
375→  // Format abstract: convert \subsection{Label} → \noindent\textbf{Label:}
376→  if (abstractText) {
377→    const formatted = abstractText
378→      .replace(
379→        /\\subsection\*?\{([^}]+)\}/g,
380→        (_m, label: string) =>
381→          `\\vspace{0.3cm}\n\\noindent\\textbf{${label.trim()}:}`
382→      )
383→      // Remove leading \vspace before first label
384→      .replace(/^\s*\\vspace\{0\.3cm\}\s*\n/, "");
385→
386→    const safeAbstract = escapeBareAmpersands(formatted);
387→    tex = tex.replace(
388→      /\\begin\{thesis_abstract\}[\s\S]*?\\end\{thesis_abstract\}/,
389→      `\\begin{thesis_abstract}\n\n${safeAbstract}\n\n\\end{thesis_abstract}`
390→    );
391→  } else {
392→    warnings.push("Phase 1: could not split abstract from acknowledgements");
393→  }
394→
395→  return tex;
396→}
