398→// ── Appendices injection ────────────────────────────────────────────────────
399→
400→/**
401→ * Mapping from AI-generated \section*{} headings to template \annexurechapter{} names.
402→ * The AI prompt instructs Phase 10 to use these exact \section*{} headings.
403→ */
404→const ANNEXURE_SECTION_MAP: Record<string, string> = {
405→  "Patient Information Sheet and Informed Consent Form": "Patient Information Sheet and Informed Consent Form",
406→  "Data Collection Proforma": "Data Collection Proforma",
407→  "Master Chart": "Master Chart",
408→};
409→
410→/**
411→ * Inject Phase 10 appendices content into the template's annexure placeholders.
412→ *
413→ * The template has `\annexurechapter{Name}` followed by `%% [[PLACEHOLDER --- Phase 10]]`.
414→ * Phase 10 AI generates content with `\section*{Name}` headings.
415→ * This function matches them up and replaces placeholders with actual content.
416→ */
417→function injectAppendices(
418→  tex: string,
419→  sections: Section[],
420→  warnings: string[]
421→): string {
422→  const phase10 = sections
423→    .filter((s) => s.phase_number === 10)
424→    .sort((a, b) => {
425→      const priority: Record<string, number> = { approved: 0, review: 1 };
426→      return (priority[a.status] ?? 2) - (priority[b.status] ?? 2);
427→    })[0];
428→
429→  if (!phase10?.latex_content) return tex;
430→
431→  const { body: rawBody } = splitBibtex(phase10.latex_content);
432→  if (!rawBody.trim()) return tex;
433→
434→  // Sanitise markdown artefacts in appendices content
435→  const body = sanitiseLatexOutput(rawBody);
436→
437→  // Split AI content by \section*{} headings
438→  const sectionParts = body.split(/\\section\*?\{([^}]+)\}/);
439→  // sectionParts: [preamble, heading1, content1, heading2, content2, ...]
440→
441→  const contentByHeading = new Map<string, string>();
442→  for (let i = 1; i < sectionParts.length; i += 2) {
443→    const heading = sectionParts[i].trim();
444→    const content = (sectionParts[i + 1] ?? "").trim();
445→    if (content) {
446→      contentByHeading.set(heading, content);
447→    }
448→  }
449→
450→  let injectedCount = 0;
451→
452→  for (const [aiHeading, annexureTitle] of Object.entries(ANNEXURE_SECTION_MAP)) {
453→    const content = contentByHeading.get(aiHeading);
454→    if (!content) continue;
455→
456→    // Replace the placeholder line after the matching \annexurechapter{}
457→    const escapedTitle = annexureTitle.replace(/[.*+?^${}()|[\]\\]/g, "\\$&");
458→    const placeholderRe = new RegExp(
459→      `(\\\\annexurechapter\\{${escapedTitle}\\}\\s*\\n)\\s*%%\\s*\\[\\[PLACEHOLDER\\s*---\\s*Phase 10\\]\\]`,
460→    );
461→
462→    const safeContent = escapeBareAmpersands(content);
463→    const sanitised = sanitiseChapterLatex(safeContent);
464→
465→    if (placeholderRe.test(tex)) {
466→      tex = tex.replace(placeholderRe, `$1\n${sanitised}`);
467→      injectedCount++;
468→    }
469→  }
470→
471→  if (injectedCount === 0) {
472→    warnings.push("Phase 10: appendices content found but no template placeholders matched");
473→  }
474→
475→  return tex;
476→}
477→
478→// ── Abbreviations injection ────────────────────────────────────────────────
479→
480→/**
481→ * Inject abbreviations list into the template's \begin{abbreviations}...\end{abbreviations}.
482→ */
483→function injectAbbreviations(
484→  tex: string,
485→  abbreviations: Abbreviation[]
486→): string {
487→  if (abbreviations.length === 0) return tex;
488→
489→  const abbrLatex = generateAbbreviationsLatex(abbreviations);
490→  if (!abbrLatex) return tex;
491→
492→  return tex.replace(
493→    /\\begin\{abbreviations\}[\s\S]*?\\end\{abbreviations\}/,
494→    abbrLatex.trim()
495→  );
496→}
