498→// ── Main assembly function ──────────────────────────────────────────────────
499→
500→export interface AssembleResult {
501→  tex: string;
502→  bib: string;
503→  chapterFiles: Record<string, string>;
504→  warnings: string[];
505→}
506→
507→/**
508→ * Assemble the thesis .tex, .bib, and chapter files from template,
509→ * project metadata, approved/review sections, and citations.
510→ *
511→ * 1. Populate metadata via `generateTex()`
512→ * 2. For each phase 2–8: find the section, produce chapter body + extract BibTeX
513→ * 3. Chapter body source: `splitBibtex(latex_content).body` (LaTeX is canonical)
514→ * 4. BibTeX source: `ai_generated_latex` (preserves ---BIBTEX--- trailer after user edits),
515→ *    else fallback to `latex_content`
516→ * 5. Each chapter body → `chapterFiles["chapters/xxx.tex"]`
517→ * 6. Missing chapters → empty file (LaTeX `\input{}` of empty file is fine)
518→ * 7. Collect BibTeX from all sections + citations table, deduplicate
519→ */
520→export function assembleThesisContent(
521→  template: string,
522→  project: Project,
523→  sections: Section[],
524→  citations: Citation[],
525→  abbreviations: Abbreviation[] = []
526→): AssembleResult {
527→  const warnings: string[] = [];
528→
529→  // Step 1: Populate metadata
530→  const { tex: metadataTex, warnings: metaWarnings } = generateTex(template, project);
531→  warnings.push(...metaWarnings);
532→
533→  // Step 1b: Inject Phase 1 front matter (acknowledgements + abstract)
534→  let tex = injectFrontMatter(metadataTex, sections, warnings);
535→  const bibParts: string[] = [];
536→  const chapterFiles: Record<string, string> = {};
537→
538→  // Build set of valid cite keys (non-Tier D entries in the citations table)
539→  const validCiteKeys = new Set<string>(
540→    citations
541→      .filter((c) => c.provenance_tier !== "D")
542→      .map((c) => c.cite_key)
543→  );
544→
545→  // Step 2: For each chapter phase, produce chapter file content
546→  for (const [phaseStr, chapterPath] of Object.entries(PHASE_CHAPTER_MAP)) {
547→    const phaseNum = parseInt(phaseStr, 10);
548→
549→    // Find the best section: prefer approved, then review
550→    const section = sections
551→      .filter((s) => s.phase_number === phaseNum)
552→      .sort((a, b) => {
553→        const priority: Record<string, number> = { approved: 0, review: 1 };
554→        return (priority[a.status] ?? 2) - (priority[b.status] ?? 2);
555→      })[0];
556→
557→    if (!section) {
558→      warnings.push(`Phase ${phaseNum}: no approved/review content — chapter will be empty`);
559→      chapterFiles[chapterPath] = "";
560→      continue;
561→    }
562→
563→    // Chapter body: use latex_content directly (canonical — no round-trip)
564→    let chapterBody = "";
565→
566→    if (section.latex_content) {
567→      const { body } = splitBibtex(section.latex_content);
568→      chapterBody = body;
569→    }
570→
571→    if (!chapterBody) {
572→      warnings.push(`Phase ${phaseNum}: section exists but body is empty`);
573→    }
574→
575→    // Strip invalid \cite{key} (Tier D or orphan) --- replace with % UNRESOLVED comment
576→    if (chapterBody) {
577→      const { stripped, replacedKeys } = stripInvalidCitations(chapterBody, validCiteKeys);
578→      chapterBody = stripped;
579→      if (replacedKeys.length > 0) {
580→        warnings.push(
581→          `Phase ${phaseNum}: stripped invalid citations: ${replacedKeys.join(", ")}`
582→        );
583→      }
584→    }
585→
586→    // Sanitise bare & outside tabular environments (AI may output unescaped &)
587→    if (chapterBody) {
588→      chapterBody = escapeBareAmpersands(chapterBody);
589→    }
590→
591→    // Sanitise: strip markdown separators, repair environment/brace balance
592→    if (chapterBody) {
593→      chapterBody = sanitiseChapterLatex(chapterBody);
594→    }
595→
596→    chapterFiles[chapterPath] = chapterBody;
597→
598→    // BibTeX: prefer ai_generated_latex (preserves ---BIBTEX--- even after user edits)
599→    const bibSource = section.ai_generated_latex ?? section.latex_content;
600→    if (bibSource) {
601→      const { bib } = splitBibtex(bibSource);
602→      if (bib) {
603→        bibParts.push(bib);
604→      }
605→    }
606→  }
607→
608→  // Step 3: Collect BibTeX from citations table (exclude Tier D — they have no \cite{})
609→  for (const citation of citations) {
610→    if (citation.bibtex_entry?.trim() && citation.provenance_tier !== "D") {
611→      bibParts.push(citation.bibtex_entry.trim());
612→    }
613→  }
614→
615→  // Step 3b: Inject Phase 10 appendices into template annexure placeholders
616→  tex = injectAppendices(tex, sections, warnings);
617→
618→  // Step 3c: Inject abbreviations into template
619→  tex = injectAbbreviations(tex, abbreviations);
620→
621→  // Step 4: Deduplicate and sanitise BibTeX
622→  const rawBib = bibParts.join("\n\n");
623→  const dedupedBib = deduplicateBibEntries(rawBib);
624→  // Normalise Unicode (smart quotes, em-dashes) in BibTeX field values —
625→  // external APIs (CrossRef, PubMed) and AI often return these characters
626→  // which cause "Invalid UTF-8 byte sequence" during compilation.
627→  const normalisedBib = normaliseUnicode(dedupedBib);
628→  // Escape bare & in BibTeX field values — AI often generates journal names
629→  // like "Endocrinology & Metabolism" instead of "Endocrinology \& Metabolism".
630→  const ampEscapedBib = escapeBareAmpersands(normalisedBib);
631→  // Escape bare _ in doi fields — BibTeX passes DOI values through to .bbl
632→  // as-is, and bare _ causes "Missing $ inserted" in pdflatex.
633→  const bib = escapeDOIUnderscores(ampEscapedBib);
634→
635→  return { tex, bib, chapterFiles, warnings };
636→}
